import{_ as e,c as i,o as r,ae as l}from"./chunks/framework.Cthp9TdA.js";const m=JSON.parse('{"title":"水泡疾病","description":"","frontmatter":{},"headers":[],"relativePath":"Appointments-水泡疾病-6.md","filePath":"Appointments-水泡疾病-6.md"}'),o={name:"Appointments-水泡疾病-6.md"};function t(s,a,h,n,d,u){return r(),i("div",null,a[0]||(a[0]=[l('<h1 id="水泡疾病" tabindex="-1">水泡疾病 <a class="header-anchor" href="#水泡疾病" aria-label="Permalink to &quot;水泡疾病&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="名詞定義" tabindex="-1">名詞定義： <a class="header-anchor" href="#名詞定義" aria-label="Permalink to &quot;名詞定義：&quot;">​</a></h2><h3 id="天皰瘡-pemphigus" tabindex="-1">天皰瘡(pemphigus)： <a class="header-anchor" href="#天皰瘡-pemphigus" aria-label="Permalink to &quot;天皰瘡(pemphigus)：&quot;">​</a></h3><h4 id="一群皮膚、黏膜的自體免疫性水泡性疾病-autoimmune-blistering-disease-有以下特徵" tabindex="-1">一群皮膚、黏膜的自體免疫性水泡性疾病(autoimmune blistering disease)，有以下特徵： <a class="header-anchor" href="#一群皮膚、黏膜的自體免疫性水泡性疾病-autoimmune-blistering-disease-有以下特徵" aria-label="Permalink to &quot;一群皮膚、黏膜的自體免疫性水泡性疾病(autoimmune blistering disease)，有以下特徵：&quot;">​</a></h4><ul><li>組織學特徵：因角質細胞(keratinocytes)間失去黏附性(acantholysis)，形成表皮內水泡(intraepidermal blisters) <ul><li>棘層鬆解(acantholysis)：游離的表皮細胞形狀也從多角形變圓形(acantholytic cell)，使皮膚或黏膜發炎剝離產生水泡</li></ul></li><li>免疫學特徵：有針對角質細胞(keratinocytes)細胞表面的抗體</li></ul><h3 id="橋粒-desmosomes" tabindex="-1">橋粒(Desmosomes)： <a class="header-anchor" href="#橋粒-desmosomes" aria-label="Permalink to &quot;橋粒(Desmosomes)：&quot;">​</a></h3><h4 id="主要功能是維持角質細胞間的連結" tabindex="-1">主要功能是維持角質細胞間的連結 <a class="header-anchor" href="#主要功能是維持角質細胞間的連結" aria-label="Permalink to &quot;主要功能是維持角質細胞間的連結&quot;">​</a></h4><h4 id="desmoglein-1-dsg1-主要分佈於皮膚淺層、黏膜中含量低" tabindex="-1">Desmoglein 1(Dsg1)：主要分佈於皮膚淺層、黏膜中含量低 <a class="header-anchor" href="#desmoglein-1-dsg1-主要分佈於皮膚淺層、黏膜中含量低" aria-label="Permalink to &quot;Desmoglein 1(Dsg1)：主要分佈於皮膚淺層、黏膜中含量低&quot;">​</a></h4><h4 id="desmoglein-3-dsg3-主要分佈於皮膚基底層、口腔黏膜" tabindex="-1">Desmoglein 3(Dsg3)：主要分佈於皮膚基底層、口腔黏膜 <a class="header-anchor" href="#desmoglein-3-dsg3-主要分佈於皮膚基底層、口腔黏膜" aria-label="Permalink to &quot;Desmoglein 3(Dsg3)：主要分佈於皮膚基底層、口腔黏膜&quot;">​</a></h4><h4 id="dermatology-4th-ed-p-584" tabindex="-1">Dermatology 4th ed. p.584 <a class="header-anchor" href="#dermatology-4th-ed-p-584" aria-label="Permalink to &quot;Dermatology 4th ed. p.584&quot;">​</a></h4><h3 id="類天疱瘡抗原-bullous-pemphigoid-antigen-bpag" tabindex="-1">類天疱瘡抗原(Bullous pemphigoid antigen, BPAg)： <a class="header-anchor" href="#類天疱瘡抗原-bullous-pemphigoid-antigen-bpag" aria-label="Permalink to &quot;類天疱瘡抗原(Bullous pemphigoid antigen, BPAg)：&quot;">​</a></h3><h4 id="位於連接基底層-stratum-basale-和basement-membrane的半胞橋小體-hemidesmosome-上" tabindex="-1">位於連接基底層(stratum basale)和Basement membrane的半胞橋小體(hemidesmosome)上 <a class="header-anchor" href="#位於連接基底層-stratum-basale-和basement-membrane的半胞橋小體-hemidesmosome-上" aria-label="Permalink to &quot;位於連接基底層(stratum basale)和Basement membrane的半胞橋小體(hemidesmosome)上&quot;">​</a></h4><h3 id="nikolsky-sign" tabindex="-1">Nikolsky sign： <a class="header-anchor" href="#nikolsky-sign" aria-label="Permalink to &quot;Nikolsky sign：&quot;">​</a></h3><h4 id="在已有的水泡病灶邊緣輕微施壓-原本好的皮膚也會上下層分離-使得水泡病灶延伸擴大-代表細胞間的接合力很差" tabindex="-1">在已有的水泡病灶邊緣輕微施壓，原本好的皮膚也會上下層分離，使得水泡病灶延伸擴大，代表細胞間的接合力很差 <a class="header-anchor" href="#在已有的水泡病灶邊緣輕微施壓-原本好的皮膚也會上下層分離-使得水泡病灶延伸擴大-代表細胞間的接合力很差" aria-label="Permalink to &quot;在已有的水泡病灶邊緣輕微施壓，原本好的皮膚也會上下層分離，使得水泡病灶延伸擴大，代表細胞間的接合力很差&quot;">​</a></h4><h3 id="direct-immunofluorescence-dif-、indirect-immunofluorescence-iif" tabindex="-1">Direct immunofluorescence(DIF)、Indirect immunofluorescence(IIF) <a class="header-anchor" href="#direct-immunofluorescence-dif-、indirect-immunofluorescence-iif" aria-label="Permalink to &quot;Direct immunofluorescence(DIF)、Indirect immunofluorescence(IIF)&quot;">​</a></h3><h2 id="分類" tabindex="-1">分類： <a class="header-anchor" href="#分類" aria-label="Permalink to &quot;分類：&quot;">​</a></h2><h3 id="尋常性天疱瘡-pemphigus-vulgaris-pv" tabindex="-1">尋常性天疱瘡(Pemphigus vulgaris, PV) <a class="header-anchor" href="#尋常性天疱瘡-pemphigus-vulgaris-pv" aria-label="Permalink to &quot;尋常性天疱瘡(Pemphigus vulgaris, PV)&quot;">​</a></h3><h3 id="落葉型天疱瘡-pemphigus-foliaceus-pf" tabindex="-1">落葉型天疱瘡(Pemphigus foliaceus, PF) <a class="header-anchor" href="#落葉型天疱瘡-pemphigus-foliaceus-pf" aria-label="Permalink to &quot;落葉型天疱瘡(Pemphigus foliaceus, PF)&quot;">​</a></h3><h4 id="紅斑型天疱瘡-pemphigus-erythematosus-pe-senear-usher-syndrome" tabindex="-1">紅斑型天疱瘡(Pemphigus erythematosus, PE )=Senear-Usher syndrome <a class="header-anchor" href="#紅斑型天疱瘡-pemphigus-erythematosus-pe-senear-usher-syndrome" aria-label="Permalink to &quot;紅斑型天疱瘡(Pemphigus erythematosus, PE )=Senear-Usher syndrome&quot;">​</a></h4><h3 id="類天疱瘡-bullous-pemphigoid-bp" tabindex="-1">類天疱瘡(Bullous pemphigoid, BP) <a class="header-anchor" href="#類天疱瘡-bullous-pemphigoid-bp" aria-label="Permalink to &quot;類天疱瘡(Bullous pemphigoid, BP)&quot;">​</a></h3><h3 id="dermatitis-herpetiformis" tabindex="-1">Dermatitis herpetiformis <a class="header-anchor" href="#dermatitis-herpetiformis" aria-label="Permalink to &quot;Dermatitis herpetiformis&quot;">​</a></h3><h3 id="iga-pemphigus-linear-iga-dermatosis" tabindex="-1">IgA pemphigus=Linear IgA dermatosis <a class="header-anchor" href="#iga-pemphigus-linear-iga-dermatosis" aria-label="Permalink to &quot;IgA pemphigus=Linear IgA dermatosis&quot;">​</a></h3><h3 id="paraneoplastic-pemphigus" tabindex="-1">Paraneoplastic pemphigus <a class="header-anchor" href="#paraneoplastic-pemphigus" aria-label="Permalink to &quot;Paraneoplastic pemphigus&quot;">​</a></h3><h3 id="drug-induced-pemphigus" tabindex="-1">Drug-induced pemphigus <a class="header-anchor" href="#drug-induced-pemphigus" aria-label="Permalink to &quot;Drug-induced pemphigus&quot;">​</a></h3><h2 id="病生理" tabindex="-1">病生理： <a class="header-anchor" href="#病生理" aria-label="Permalink to &quot;病生理：&quot;">​</a></h2><h3 id="免疫系統產生-igg-自體抗體→攻擊皮膚、黏膜上皮細胞之間的細胞連結結構-橋粒-desmosomes-→角質形成細胞-keratinocytes-間失去黏附性-acantholysis-→形成水泡" tabindex="-1">免疫系統產生 IgG 自體抗體→攻擊皮膚、黏膜上皮細胞之間的細胞連結結構(橋粒，Desmosomes)→角質形成細胞(keratinocytes)間失去黏附性(acantholysis)→形成水泡 <a class="header-anchor" href="#免疫系統產生-igg-自體抗體→攻擊皮膚、黏膜上皮細胞之間的細胞連結結構-橋粒-desmosomes-→角質形成細胞-keratinocytes-間失去黏附性-acantholysis-→形成水泡" aria-label="Permalink to &quot;免疫系統產生 IgG 自體抗體→攻擊皮膚、黏膜上皮細胞之間的細胞連結結構(橋粒，Desmosomes)→角質形成細胞(keratinocytes)間失去黏附性(acantholysis)→形成水泡&quot;">​</a></h3><h2 id="尋常性天疱瘡-pemphigus-vulgaris-pv-1" tabindex="-1">尋常性天疱瘡(Pemphigus vulgaris, PV)： <a class="header-anchor" href="#尋常性天疱瘡-pemphigus-vulgaris-pv-1" aria-label="Permalink to &quot;尋常性天疱瘡(Pemphigus vulgaris, PV)：&quot;">​</a></h2><h3 id="流行病學" tabindex="-1">流行病學： <a class="header-anchor" href="#流行病學" aria-label="Permalink to &quot;流行病學：&quot;">​</a></h3><h4 id="發病高峰-40-60歲" tabindex="-1">發病高峰：40-60歲 <a class="header-anchor" href="#發病高峰-40-60歲" aria-label="Permalink to &quot;發病高峰：40-60歲&quot;">​</a></h4><h3 id="病因" tabindex="-1">病因： <a class="header-anchor" href="#病因" aria-label="Permalink to &quot;病因：&quot;">​</a></h3><h4 id="type-2過敏反應" tabindex="-1">Type 2過敏反應 <a class="header-anchor" href="#type-2過敏反應" aria-label="Permalink to &quot;Type 2過敏反應&quot;">​</a></h4><h4 id="加劇因素-藥物-non-thiol-drugs-、病毒、紫外線輻射、飲食-洋蔥、大蒜、韭菜" tabindex="-1">加劇因素：藥物(non-thiol drugs)、病毒、紫外線輻射、飲食(洋蔥、大蒜、韭菜…) <a class="header-anchor" href="#加劇因素-藥物-non-thiol-drugs-、病毒、紫外線輻射、飲食-洋蔥、大蒜、韭菜" aria-label="Permalink to &quot;加劇因素：藥物(non-thiol drugs)、病毒、紫外線輻射、飲食(洋蔥、大蒜、韭菜…)&quot;">​</a></h4><h4 id="可被誘發" tabindex="-1">可被誘發 <a class="header-anchor" href="#可被誘發" aria-label="Permalink to &quot;可被誘發&quot;">​</a></h4><h3 id="臨床表現" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h3><h4 id="好發部位" tabindex="-1">好發部位： <a class="header-anchor" href="#好發部位" aria-label="Permalink to &quot;好發部位：&quot;">​</a></h4><ul><li>表皮：頭皮、臉部、軀幹、皮膚皺摺處(flexural area)</li><li>黏膜：口咽部最常見</li></ul><h4 id="先從出現口腔黏膜潰瘍-50-數個月後皮膚才會開始出現病灶" tabindex="-1">先從出現口腔黏膜潰瘍(&gt;50%)，數個月後皮膚才會開始出現病灶 <a class="header-anchor" href="#先從出現口腔黏膜潰瘍-50-數個月後皮膚才會開始出現病灶" aria-label="Permalink to &quot;先從出現口腔黏膜潰瘍(&gt;50%)，數個月後皮膚才會開始出現病灶&quot;">​</a></h4><h4 id="皮膚病灶-卵圓形含清澈組織液-鬆且易破的水泡-flaccid-vesicle-bulla-破裂之後呈現表淺的糜爛-erosion-傷口-很痛-會輕微出血與結痂-crust-來診病患多呈現身體大面積糜爛" tabindex="-1">皮膚病灶：卵圓形含清澈組織液，鬆且易破的水泡(flaccid vesicle/ bulla)， 破裂之後呈現表淺的糜爛(erosion)傷口，很痛，會輕微出血與結痂(crust)，來診病患多呈現身體大面積糜爛 <a class="header-anchor" href="#皮膚病灶-卵圓形含清澈組織液-鬆且易破的水泡-flaccid-vesicle-bulla-破裂之後呈現表淺的糜爛-erosion-傷口-很痛-會輕微出血與結痂-crust-來診病患多呈現身體大面積糜爛" aria-label="Permalink to &quot;皮膚病灶：卵圓形含清澈組織液，鬆且易破的水泡(flaccid vesicle/ bulla)， 破裂之後呈現表淺的糜爛(erosion)傷口，很痛，會輕微出血與結痂(crust)，來診病患多呈現身體大面積糜爛&quot;">​</a></h4><h3 id="診斷" tabindex="-1">診斷： <a class="header-anchor" href="#診斷" aria-label="Permalink to &quot;診斷：&quot;">​</a></h3><h4 id="anti-desmoglein-igg抗體-dsg1-±-dsg3" tabindex="-1">Anti-desmoglein IgG抗體：Dsg1 ± Dsg3 <a class="header-anchor" href="#anti-desmoglein-igg抗體-dsg1-±-dsg3" aria-label="Permalink to &quot;Anti-desmoglein IgG抗體：Dsg1 ± Dsg3&quot;">​</a></h4><h4 id="病理變化-表皮內的基底層-stratum-basale-與其上的表皮細胞分離-形成表皮層內裂縫-intraepidermal-cleft-裂縫中有許多游離的acantholytic-cell-此種水泡稱為suprabasal-blister" tabindex="-1">病理變化：表皮內的基底層(Stratum basale)與其上的表皮細胞分離，形成表皮層內裂縫(Intraepidermal cleft) ，裂縫中有許多游離的acantholytic cell，此種水泡稱為suprabasal blister <a class="header-anchor" href="#病理變化-表皮內的基底層-stratum-basale-與其上的表皮細胞分離-形成表皮層內裂縫-intraepidermal-cleft-裂縫中有許多游離的acantholytic-cell-此種水泡稱為suprabasal-blister" aria-label="Permalink to &quot;病理變化：表皮內的基底層(Stratum basale)與其上的表皮細胞分離，形成表皮層內裂縫(Intraepidermal cleft) ，裂縫中有許多游離的acantholytic cell，此種水泡稱為suprabasal blister&quot;">​</a></h4><h4 id="直接免疫螢光染色-direct-immunofluorescence-dif-igg和c3沉積在細胞間隙-intercellular-ic-deposition-呈現網狀的ic-pattern-如右圖可見表皮層自基底層以上剝離-dif呈網狀圖案" tabindex="-1">直接免疫螢光染色(Direct immunofluorescence, DIF )：IgG和C3沉積在細胞間隙(intercellular(IC) deposition)呈現網狀的IC pattern， 如右圖可見表皮層自基底層以上剝離，DIF呈網狀圖案 <a class="header-anchor" href="#直接免疫螢光染色-direct-immunofluorescence-dif-igg和c3沉積在細胞間隙-intercellular-ic-deposition-呈現網狀的ic-pattern-如右圖可見表皮層自基底層以上剝離-dif呈網狀圖案" aria-label="Permalink to &quot;直接免疫螢光染色(Direct immunofluorescence, DIF )：IgG和C3沉積在細胞間隙(intercellular(IC) deposition)呈現網狀的IC pattern， 如右圖可見表皮層自基底層以上剝離，DIF呈網狀圖案&quot;">​</a></h4><h4 id="nikolsky-sign-1" tabindex="-1">Nikolsky sign(+) <a class="header-anchor" href="#nikolsky-sign-1" aria-label="Permalink to &quot;Nikolsky sign(+)&quot;">​</a></h4><h2 id="落葉型天疱瘡-pemphigus-foliaceus-pf-1" tabindex="-1">落葉型天疱瘡(Pemphigus foliaceus, PF)： <a class="header-anchor" href="#落葉型天疱瘡-pemphigus-foliaceus-pf-1" aria-label="Permalink to &quot;落葉型天疱瘡(Pemphigus foliaceus, PF)：&quot;">​</a></h2><h3 id="簡介" tabindex="-1">簡介： <a class="header-anchor" href="#簡介" aria-label="Permalink to &quot;簡介：&quot;">​</a></h3><h4 id="抗體只攻擊desmoglein-1-表皮-故不會有口腔黏膜的病灶-且裂解位置更表淺-水泡通常不明顯易破而形成落屑或結痂的表面" tabindex="-1">抗體只攻擊Desmoglein 1(表皮)，故不會有口腔黏膜的病灶，且裂解位置更表淺，水泡通常不明顯易破而形成落屑或結痂的表面 <a class="header-anchor" href="#抗體只攻擊desmoglein-1-表皮-故不會有口腔黏膜的病灶-且裂解位置更表淺-水泡通常不明顯易破而形成落屑或結痂的表面" aria-label="Permalink to &quot;抗體只攻擊Desmoglein 1(表皮)，故不會有口腔黏膜的病灶，且裂解位置更表淺，水泡通常不明顯易破而形成落屑或結痂的表面&quot;">​</a></h4><h3 id="病因-1" tabindex="-1">病因： <a class="header-anchor" href="#病因-1" aria-label="Permalink to &quot;病因：&quot;">​</a></h3><h4 id="type-2過敏反應-1" tabindex="-1">Type 2過敏反應 <a class="header-anchor" href="#type-2過敏反應-1" aria-label="Permalink to &quot;Type 2過敏反應&quot;">​</a></h4><h4 id="加劇因素-藥物-thiol-drugs" tabindex="-1">加劇因素：藥物(Thiol drugs) <a class="header-anchor" href="#加劇因素-藥物-thiol-drugs" aria-label="Permalink to &quot;加劇因素：藥物(Thiol drugs)&quot;">​</a></h4><h3 id="臨床表現-好發於背、前胸、臉、頭皮和腹部" tabindex="-1">臨床表現：好發於背、前胸、臉、頭皮和腹部 <a class="header-anchor" href="#臨床表現-好發於背、前胸、臉、頭皮和腹部" aria-label="Permalink to &quot;臨床表現：好發於背、前胸、臉、頭皮和腹部&quot;">​</a></h3><h3 id="診斷-1" tabindex="-1">診斷： <a class="header-anchor" href="#診斷-1" aria-label="Permalink to &quot;診斷：&quot;">​</a></h3><h4 id="anti-desmoglein-igg抗體-dsg1" tabindex="-1">Anti-desmoglein IgG抗體：Dsg1 <a class="header-anchor" href="#anti-desmoglein-igg抗體-dsg1" aria-label="Permalink to &quot;Anti-desmoglein IgG抗體：Dsg1&quot;">​</a></h4><h4 id="病理變化" tabindex="-1">病理變化： <a class="header-anchor" href="#病理變化" aria-label="Permalink to &quot;病理變化：&quot;">​</a></h4><ul><li>表皮層分裂的位置在角質層下方(Stratum corneum=Subcorneal)，較PV的基底層上方(Suprabasal)更表淺，所以水泡更薄更容易破裂</li><li>也有acantholytic cell、IC pattern</li></ul><h4 id="nikolsky-sign-2" tabindex="-1">Nikolsky sign(+) <a class="header-anchor" href="#nikolsky-sign-2" aria-label="Permalink to &quot;Nikolsky sign(+)&quot;">​</a></h4><h2 id="紅斑型天疱瘡-pemphigus-erythematosus-pe" tabindex="-1">紅斑型天疱瘡(Pemphigus erythematosus, PE)： <a class="header-anchor" href="#紅斑型天疱瘡-pemphigus-erythematosus-pe" aria-label="Permalink to &quot;紅斑型天疱瘡(Pemphigus erythematosus, PE)：&quot;">​</a></h2><h3 id="簡介-1" tabindex="-1">簡介： <a class="header-anchor" href="#簡介-1" aria-label="Permalink to &quot;簡介：&quot;">​</a></h3><h4 id="少見的亞型" tabindex="-1">少見的亞型 <a class="header-anchor" href="#少見的亞型" aria-label="Permalink to &quot;少見的亞型&quot;">​</a></h4><h4 id="只發生在臉上-分布位置和sle的蝴蝶斑同" tabindex="-1">只發生在臉上，分布位置和SLE的蝴蝶斑同 <a class="header-anchor" href="#只發生在臉上-分布位置和sle的蝴蝶斑同" aria-label="Permalink to &quot;只發生在臉上，分布位置和SLE的蝴蝶斑同&quot;">​</a></h4><h4 id="形成粗紅、有點脫屑的紅斑" tabindex="-1">形成粗紅、有點脫屑的紅斑 <a class="header-anchor" href="#形成粗紅、有點脫屑的紅斑" aria-label="Permalink to &quot;形成粗紅、有點脫屑的紅斑&quot;">​</a></h4><h2 id="流行性落葉型天疱瘡-endemic-pemphigus-foliaceus-叢林之火-fogo-selvagem" tabindex="-1">流行性落葉型天疱瘡(Endemic pemphigus foliaceus )=叢林之火(Fogo Selvagem)： <a class="header-anchor" href="#流行性落葉型天疱瘡-endemic-pemphigus-foliaceus-叢林之火-fogo-selvagem" aria-label="Permalink to &quot;流行性落葉型天疱瘡(Endemic pemphigus foliaceus )=叢林之火(Fogo Selvagem)：&quot;">​</a></h2><h3 id="簡介-2" tabindex="-1">簡介： <a class="header-anchor" href="#簡介-2" aria-label="Permalink to &quot;簡介：&quot;">​</a></h3><h4 id="出現在南美洲亞馬遜河流域森林的一種流行性疾病-可影響同住家人同時罹患" tabindex="-1">出現在南美洲亞馬遜河流域森林的一種流行性疾病，可影響同住家人同時罹患 <a class="header-anchor" href="#出現在南美洲亞馬遜河流域森林的一種流行性疾病-可影響同住家人同時罹患" aria-label="Permalink to &quot;出現在南美洲亞馬遜河流域森林的一種流行性疾病，可影響同住家人同時罹患&quot;">​</a></h4><h4 id="症狀與落葉型天疱瘡-pf-完全相同" tabindex="-1">症狀與落葉型天疱瘡(PF)完全相同 <a class="header-anchor" href="#症狀與落葉型天疱瘡-pf-完全相同" aria-label="Permalink to &quot;症狀與落葉型天疱瘡(PF)完全相同&quot;">​</a></h4><h2 id="drug-induced-pemphigus-1" tabindex="-1">Drug-induced pemphigus： <a class="header-anchor" href="#drug-induced-pemphigus-1" aria-label="Permalink to &quot;Drug-induced pemphigus：&quot;">​</a></h2><h3 id="病生理-藥物誘發產生針對dsg-1、dsg-3的抗體" tabindex="-1">病生理：藥物誘發產生針對Dsg 1、Dsg 3的抗體 <a class="header-anchor" href="#病生理-藥物誘發產生針對dsg-1、dsg-3的抗體" aria-label="Permalink to &quot;病生理：藥物誘發產生針對Dsg 1、Dsg 3的抗體&quot;">​</a></h3><h3 id="病因-2" tabindex="-1">病因： <a class="header-anchor" href="#病因-2" aria-label="Permalink to &quot;病因：&quot;">​</a></h3><h4 id="thiol-drugs-penicillamine、acei-captopril、enalapril-常引起pemphigus-foliaceus" tabindex="-1">Thiol drugs：penicillamine、ACEI(captopril、enalapril)：常引起pemphigus foliaceus <a class="header-anchor" href="#thiol-drugs-penicillamine、acei-captopril、enalapril-常引起pemphigus-foliaceus" aria-label="Permalink to &quot;Thiol drugs：penicillamine、ACEI(captopril、enalapril)：常引起pemphigus foliaceus&quot;">​</a></h4><h4 id="non-thiol-drugs-penicillin、cephalosporins、-vancomycin、antihypertensive-drugs、piroxicam-常引起pemphigus-vulgaris" tabindex="-1">Non-thiol drugs：penicillin、cephalosporins、 vancomycin、antihypertensive drugs、piroxicam：常引起pemphigus vulgaris <a class="header-anchor" href="#non-thiol-drugs-penicillin、cephalosporins、-vancomycin、antihypertensive-drugs、piroxicam-常引起pemphigus-vulgaris" aria-label="Permalink to &quot;Non-thiol drugs：penicillin、cephalosporins、 vancomycin、antihypertensive drugs、piroxicam：常引起pemphigus vulgaris&quot;">​</a></h4><h2 id="類天疱瘡-bullous-pemphigoid-bp-1" tabindex="-1">類天疱瘡(Bullous pemphigoid, BP)： <a class="header-anchor" href="#類天疱瘡-bullous-pemphigoid-bp-1" aria-label="Permalink to &quot;類天疱瘡(Bullous pemphigoid, BP)：&quot;">​</a></h2><h3 id="流行病學-1" tabindex="-1">流行病學： <a class="header-anchor" href="#流行病學-1" aria-label="Permalink to &quot;流行病學：&quot;">​</a></h3><h4 id="自體免疫水泡病中最常見的一種" tabindex="-1">自體免疫水泡病中最常見的一種 <a class="header-anchor" href="#自體免疫水泡病中最常見的一種" aria-label="Permalink to &quot;自體免疫水泡病中最常見的一種&quot;">​</a></h4><h4 id="發病高峰-60-80歲、腦中風及中樞神經病變者" tabindex="-1">發病高峰：60-80歲、腦中風及中樞神經病變者 <a class="header-anchor" href="#發病高峰-60-80歲、腦中風及中樞神經病變者" aria-label="Permalink to &quot;發病高峰：60-80歲、腦中風及中樞神經病變者&quot;">​</a></h4><h3 id="病因-type-2過敏反應" tabindex="-1">病因：Type 2過敏反應 <a class="header-anchor" href="#病因-type-2過敏反應" aria-label="Permalink to &quot;病因：Type 2過敏反應&quot;">​</a></h3><h3 id="病生理-1" tabindex="-1">病生理： <a class="header-anchor" href="#病生理-1" aria-label="Permalink to &quot;病生理：&quot;">​</a></h3><h4 id="自體抗體攻擊類天疱瘡抗原-bullous-pemphigoid-antigen-bpag1、bpag2→活化補體及發炎反應→破壞基底層細胞與其下真皮層的聯結→表皮-epidermis-與真皮-dermis-之間裂開" tabindex="-1">自體抗體攻擊類天疱瘡抗原(Bullous pemphigoid antigen) BPAg1、BPAg2→活化補體及發炎反應→破壞基底層細胞與其下真皮層的聯結→表皮(epidermis)與真皮 (dermis)之間裂開 <a class="header-anchor" href="#自體抗體攻擊類天疱瘡抗原-bullous-pemphigoid-antigen-bpag1、bpag2→活化補體及發炎反應→破壞基底層細胞與其下真皮層的聯結→表皮-epidermis-與真皮-dermis-之間裂開" aria-label="Permalink to &quot;自體抗體攻擊類天疱瘡抗原(Bullous pemphigoid antigen) BPAg1、BPAg2→活化補體及發炎反應→破壞基底層細胞與其下真皮層的聯結→表皮(epidermis)與真皮 (dermis)之間裂開&quot;">​</a></h4><h3 id="臨床特徵" tabindex="-1">臨床特徵： <a class="header-anchor" href="#臨床特徵" aria-label="Permalink to &quot;臨床特徵：&quot;">​</a></h3><h4 id="好發部位-腋下、腹股溝等皮膚皺摺處-約有10-病人會有口腔病灶" tabindex="-1">好發部位：腋下、腹股溝等皮膚皺摺處，約有10%病人會有口腔病灶 <a class="header-anchor" href="#好發部位-腋下、腹股溝等皮膚皺摺處-約有10-病人會有口腔病灶" aria-label="Permalink to &quot;好發部位：腋下、腹股溝等皮膚皺摺處，約有10%病人會有口腔病灶&quot;">​</a></h4><h4 id="一開始皮膚呈現發紅的紅疹或類似尋麻疹的病灶-數月後才慢慢出現水泡" tabindex="-1">一開始皮膚呈現發紅的紅疹或類似尋麻疹的病灶，數月後才慢慢出現水泡 <a class="header-anchor" href="#一開始皮膚呈現發紅的紅疹或類似尋麻疹的病灶-數月後才慢慢出現水泡" aria-label="Permalink to &quot;一開始皮膚呈現發紅的紅疹或類似尋麻疹的病灶，數月後才慢慢出現水泡&quot;">​</a></h4><h4 id="與pv相比-bp裂在較深層-故水泡看起來較大-厚而緊繃-較不容易破掉-為繃緊水泡-tense-bullae" tabindex="-1">與PV相比，BP裂在較深層，故水泡看起來較大，厚而緊繃，較不容易破掉，為繃緊水泡(tense bullae <a class="header-anchor" href="#與pv相比-bp裂在較深層-故水泡看起來較大-厚而緊繃-較不容易破掉-為繃緊水泡-tense-bullae" aria-label="Permalink to &quot;與PV相比，BP裂在較深層，故水泡看起來較大，厚而緊繃，較不容易破掉，為繃緊水泡(tense bullae&quot;">​</a></h4><h3 id="診斷-2" tabindex="-1">診斷： <a class="header-anchor" href="#診斷-2" aria-label="Permalink to &quot;診斷：&quot;">​</a></h3><h4 id="病理變化-1" tabindex="-1">病理變化： <a class="header-anchor" href="#病理變化-1" aria-label="Permalink to &quot;病理變化：&quot;">​</a></h4><ul><li>基底層(Stratum basale )與其下的真皮層分離，形成表皮層下裂縫(Subepidermal cleft )，又稱 subepidermal blister</li><li>嗜中性球、嗜伊紅性細胞和淋巴球浸潤於真皮層上層</li><li>直接免疫螢光染色(Direct immunofluorescence, DIF)： IgG和 C3 沿著表皮與真皮之間的 Basement membrane zone(dermo-epidermal junction)沉積，呈線狀沉積(linear deposition)</li></ul><h4 id="nikolsky-sign-3" tabindex="-1">Nikolsky sign(-) <a class="header-anchor" href="#nikolsky-sign-3" aria-label="Permalink to &quot;Nikolsky sign(-)&quot;">​</a></h4><h2 id="dermatitis-herpetiformis-1" tabindex="-1">Dermatitis herpetiformis <a class="header-anchor" href="#dermatitis-herpetiformis-1" aria-label="Permalink to &quot;Dermatitis herpetiformis&quot;">​</a></h2><h3 id="流行病學-2" tabindex="-1">流行病學： <a class="header-anchor" href="#流行病學-2" aria-label="Permalink to &quot;流行病學：&quot;">​</a></h3><h4 id="發病高峰-15-40歲" tabindex="-1">發病高峰：15-40歲 <a class="header-anchor" href="#發病高峰-15-40歲" aria-label="Permalink to &quot;發病高峰：15-40歲&quot;">​</a></h4><h4 id="♂-♀" tabindex="-1">♂ &gt; ♀ <a class="header-anchor" href="#♂-♀" aria-label="Permalink to &quot;♂ &gt; ♀&quot;">​</a></h4><h4 id="北歐人較常見" tabindex="-1">北歐人較常見 <a class="header-anchor" href="#北歐人較常見" aria-label="Permalink to &quot;北歐人較常見&quot;">​</a></h4><h3 id="病因-3" tabindex="-1">病因： <a class="header-anchor" href="#病因-3" aria-label="Permalink to &quot;病因：&quot;">​</a></h3><h4 id="與麩質腸病變-gluten-sensitive-enteropathy-gse-、表皮iga沉積相關" tabindex="-1">與麩質腸病變(gluten-sensitive enteropathy, GSE)、表皮IgA沉積相關 <a class="header-anchor" href="#與麩質腸病變-gluten-sensitive-enteropathy-gse-、表皮iga沉積相關" aria-label="Permalink to &quot;與麩質腸病變(gluten-sensitive enteropathy, GSE)、表皮IgA沉積相關&quot;">​</a></h4><ul><li>HLA-DQ2、HLA-DQ8</li><li>自體免疫抗體同時攻擊腸胃道及皮膚，免疫複合物(immunocomplex)沉積在皮膚內引發一連串發炎反應</li></ul><h3 id="臨床表現-1" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-1" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h3><h4 id="慢性、反覆發作、很癢的水泡疾病" tabindex="-1">慢性、反覆發作、很癢的水泡疾病 <a class="header-anchor" href="#慢性、反覆發作、很癢的水泡疾病" aria-label="Permalink to &quot;慢性、反覆發作、很癢的水泡疾病&quot;">​</a></h4><h4 id="皮膚症狀" tabindex="-1">皮膚症狀： <a class="header-anchor" href="#皮膚症狀" aria-label="Permalink to &quot;皮膚症狀：&quot;">​</a></h4><ul><li>病灶呈現紅疹、斑塊及群聚的小水泡</li><li>有麻癢刺痛感，抓破的小水泡會結痂， 癒合後的膚色可能較淺或較深(Post-inflammatory hypo- or hyperpigmentation)</li><li>分布位置：對稱性分佈，好發於關節的伸側(extensor site)、背部、臀部，不會發生在口腔黏膜</li></ul><h4 id="腸胃道症狀-長期腹瀉-乳糜瀉-celiac-sprue-、營養不良" tabindex="-1">腸胃道症狀：長期腹瀉(乳糜瀉, celiac sprue)、營養不良 <a class="header-anchor" href="#腸胃道症狀-長期腹瀉-乳糜瀉-celiac-sprue-、營養不良" aria-label="Permalink to &quot;腸胃道症狀：長期腹瀉(乳糜瀉, celiac sprue)、營養不良&quot;">​</a></h4><h3 id="診斷-3" tabindex="-1">診斷： <a class="header-anchor" href="#診斷-3" aria-label="Permalink to &quot;診斷：&quot;">​</a></h3><h4 id="病理變化-2" tabindex="-1">病理變化： <a class="header-anchor" href="#病理變化-2" aria-label="Permalink to &quot;病理變化：&quot;">​</a></h4><ul><li>表皮下水泡(Subepidermal vesicle )</li><li>真皮層中發炎細胞的群聚稱為微小膿瘍(micro-abscesses)，以嗜中性球及嗜伊紅性白血球為主</li><li>直接免疫螢光染色(Direct immunofluorescence, DIF)：在真皮乳突處(dermal papillae)的頂端IgA顆粒狀沉積(granular deposition)</li></ul><h4 id="nikolsky-sign-4" tabindex="-1">Nikolsky sign(-) <a class="header-anchor" href="#nikolsky-sign-4" aria-label="Permalink to &quot;Nikolsky sign(-)&quot;">​</a></h4>',107)]))}const p=e(o,[["render",t]]);export{m as __pageData,p as default};
