import{_ as i,c as a,o as e,ae as r}from"./chunks/framework.Cthp9TdA.js";const m=JSON.parse('{"title":"新生兒感染感染","description":"","frontmatter":{},"headers":[],"relativePath":"ChildDepartment-新生兒感染感染-7.md","filePath":"ChildDepartment-新生兒感染感染-7.md"}'),o={name:"ChildDepartment-新生兒感染感染-7.md"};function t(u,l,n,s,h,c){return e(),a("div",null,l[0]||(l[0]=[r('<h1 id="新生兒感染感染" tabindex="-1">新生兒感染感染 <a class="header-anchor" href="#新生兒感染感染" aria-label="Permalink to &quot;新生兒感染感染&quot;">​</a></h1><h2 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h2><h3 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h3><h3 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h3><h3 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h3><h3 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h3><h2 id="新生兒感染" tabindex="-1">新生兒感染： <a class="header-anchor" href="#新生兒感染" aria-label="Permalink to &quot;新生兒感染：&quot;">​</a></h2><h3 id="torch-infection" tabindex="-1">TORCH infection <a class="header-anchor" href="#torch-infection" aria-label="Permalink to &quot;TORCH infection&quot;">​</a></h3><h4 id="臨床非特異表現-iugr-子宮內胎兒生長遲滯-、肝脾腫大、心臟肥大、胎兒水腫" tabindex="-1">臨床非特異表現：IUGR(子宮內胎兒生長遲滯) 、肝脾腫大、心臟肥大、胎兒水腫 <a class="header-anchor" href="#臨床非特異表現-iugr-子宮內胎兒生長遲滯-、肝脾腫大、心臟肥大、胎兒水腫" aria-label="Permalink to &quot;臨床非特異表現：IUGR(子宮內胎兒生長遲滯) 、肝脾腫大、心臟肥大、胎兒水腫&quot;">​</a></h4><h4 id="弓漿蟲病-toxoplasmosis" tabindex="-1">弓漿蟲病(Toxoplasmosis) : <a class="header-anchor" href="#弓漿蟲病-toxoplasmosis" aria-label="Permalink to &quot;弓漿蟲病(Toxoplasmosis) :&quot;">​</a></h4><ul><li>傳染途徑&amp;時機: <ul><li>貓屎</li><li>胎盤感染(大多於孕晚期)</li></ul></li><li>臨床症狀: 4C 表現 <ul><li>Cerebral calcifications(ring-enhancing 與CMV 不同)</li><li>Chorioretinitis(posterior uveitis)</li><li>hydroCephalus</li><li>Convulsions</li></ul></li><li>診斷: <ul><li>Step 1：查看母親體內有無Toxoplasma-IGM</li><li>Step 2：胎兒期羊水PCR檢測</li><li>Step 3 ：新生兒時期 <ul><li>影像學(CT、MRI)檢測4C表現(Chorioretinitis需另照會眼科)</li><li>腦脊髓液、血液IGM、PCR檢測</li></ul></li></ul></li><li>治療: <ul><li>Pyrimethamine、sulfadiazine 和 folinic acid</li></ul></li></ul><h4 id="rubella" tabindex="-1">Rubella： <a class="header-anchor" href="#rubella" aria-label="Permalink to &quot;Rubella：&quot;">​</a></h4><ul><li>傳染途徑&amp;時機: <ul><li>第一孕期最容易傳染</li></ul></li><li>臨床症狀: <ul><li>最常見的是PDA、Pulmonary artery stenosis</li><li>白內障、Retinopathy</li><li>Deafness(聽力) <ul><li>上面三個症狀合併為Rubella classic triad</li></ul></li><li>Thrombocytopenia</li><li>發燒、疲倦、鼻咽癌、耳後淋巴腫大、全身性皮疹</li></ul></li><li>診斷: <ul><li>羊水檢測IGM或是PCR-RNA</li></ul></li><li>治療: <ul><li>Supportive care即可</li></ul></li></ul><h4 id="巨細胞病毒-cmv" tabindex="-1">巨細胞病毒( CMV)： <a class="header-anchor" href="#巨細胞病毒-cmv" aria-label="Permalink to &quot;巨細胞病毒( CMV)：&quot;">​</a></h4><ul><li>傳染途徑&amp;時機: <ul><li>垂直傳染率： <ul><li>1st/2nd trimester：30-40%</li><li>3rd trimester：40-72%</li></ul></li><li>其他傳染途徑: <ul><li>生產過程</li><li>母乳哺餵</li></ul></li><li>重症率: <ul><li>1st trimester 最高</li></ul></li></ul></li><li>臨床症狀：90%感染者無症狀 <ul><li>Periventricular calcification</li><li>Hydrocephalus</li><li>Microcephaly</li><li>Sensorineural hearing loss(聽力)</li><li>Thrombocytopenia</li><li>Chorioretinitis</li></ul></li><li>考慮篩檢情形: <ul><li>腦室擴大、腦室周圍神經組織鈣化、小腦症、肝臟鈣化、高回音腸道、生長遲滯</li></ul></li><li>治療: <ul><li>Ganciclovir、valganciclovir、foscarnet</li><li>懷孕期間只能用 valacyclovir</li></ul></li></ul><h4 id="hsv" tabindex="-1">HSV： <a class="header-anchor" href="#hsv" aria-label="Permalink to &quot;HSV：&quot;">​</a></h4><ul><li>臨床症狀 <ul><li>水泡</li><li>心肌炎</li><li>Hepatitis</li><li>Pneumonitis</li><li>Keratoconjunctivitis</li><li>Mucocutaneous lesions</li><li>Encephalitis</li><li>DIC</li></ul></li><li>診斷： <ul><li>血液IgM檢查：Toxoplasmosis</li><li>Tzanck smear：HSV、VZV(針對有skin lesions)</li><li>Urine antigen、culture：CMV</li><li>HBV：HBs-Ag、HBs-Ab、HBc-Ag、HBc-Ab、liver enzymes、bililubin</li><li>Pathogen culture</li></ul></li><li>治療及預防: <ul><li>Acyclovir or valacyclovir</li><li>對於確認感染之孕婦於GA 36 週時可預防性給予acyclovir</li><li>若有陰部感染之孕婦可考慮剖腹產</li></ul></li><li>聽力受損的鑑別診斷： <ul><li>感染：CMV、Rubella、腦膜炎</li><li>耳毒性藥物</li></ul></li></ul><h4 id="other-syphillis-varicella皆有皮膚症狀、視網膜脈絡膜炎、肝脾腫大" tabindex="-1">Other：Syphillis/Varicella皆有皮膚症狀、視網膜脈絡膜炎、肝脾腫大 <a class="header-anchor" href="#other-syphillis-varicella皆有皮膚症狀、視網膜脈絡膜炎、肝脾腫大" aria-label="Permalink to &quot;Other：Syphillis/Varicella皆有皮膚症狀、視網膜脈絡膜炎、肝脾腫大&quot;">​</a></h4><ul><li>Syphilis：青光眼、Mucocutaneous lesions(snuffles)、periostitis、osteochondritis、rash</li><li>Varicella：瘢痕(cicatrix)、小眼球、肢體發育不良</li><li>Parvovirus B19：胎兒水腫、貧血</li></ul><h4 id="新生兒細菌感染" tabindex="-1">新生兒細菌感染 <a class="header-anchor" href="#新生兒細菌感染" aria-label="Permalink to &quot;新生兒細菌感染&quot;">​</a></h4><ul><li>陰道病原菌 <ul><li>GBS <ul><li>GA35-37週GBS篩檢</li><li>需於陰道產前4hr IV打預防性抗生素適應症</li></ul></li><li>E. coli</li><li>李斯特菌(Listeria monocytogenes)</li></ul></li><li>泌尿道感染： <ul><li>E . coli 、Klebsiella pneumoniae 、Proteus mirabilis</li></ul></li><li>依發生時間區分(早發、晚發)： <ul><li>早發性感染 <ul><li>產後0~6天</li><li>病因: <ul><li>絨毛膜羊膜炎</li><li>早產</li><li>母體陰道傳播</li><li>UTI</li></ul></li></ul></li><li>晚發性感染 <ul><li>產後7~90天</li><li>常見原因： <ul><li>院內感染</li><li>通常非產科併發症所致</li></ul></li></ul></li></ul></li><li>治療: <ul><li>抗生素: <ul><li>首選penicillin G;</li><li>替代:Ampicillin + gentamicin</li></ul></li><li>Supportive care</li></ul></li></ul>',21)]))}const p=i(o,[["render",t]]);export{m as __pageData,p as default};
