import{_ as e,c as i,o as r,ae as l}from"./chunks/framework.BtEkjhVd.js";const m=JSON.parse('{"title":"ACLS","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-ACLS-7.md","filePath":"InternalMedicineDepartment-ACLS-7.md"}'),t={name:"InternalMedicineDepartment-ACLS-7.md"};function o(h,a,n,s,c,d){return r(),i("div",null,a[0]||(a[0]=[l('<h1 id="acls" tabindex="-1">ACLS <a class="header-anchor" href="#acls" aria-label="Permalink to &quot;ACLS&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="bls" tabindex="-1">BLS <a class="header-anchor" href="#bls" aria-label="Permalink to &quot;BLS&quot;">​</a></h2><h3 id="叫叫cab" tabindex="-1">叫叫CAB <a class="header-anchor" href="#叫叫cab" aria-label="Permalink to &quot;叫叫CAB&quot;">​</a></h3><h4 id="評估反應-呼吸-確認無反應-無呼吸" tabindex="-1">評估反應 &amp; 呼吸（確認無反應+無呼吸） <a class="header-anchor" href="#評估反應-呼吸-確認無反應-無呼吸" aria-label="Permalink to &quot;評估反應 &amp; 呼吸（確認無反應+無呼吸）&quot;">​</a></h4><h4 id="撥打急救電話-activate-ems" tabindex="-1">撥打急救電話（Activate EMS） <a class="header-anchor" href="#撥打急救電話-activate-ems" aria-label="Permalink to &quot;撥打急救電話（Activate EMS）&quot;">​</a></h4><h4 id="檢查脈搏-若無-立即開始-cpr" tabindex="-1">檢查脈搏，若無，立即開始 CPR <a class="header-anchor" href="#檢查脈搏-若無-立即開始-cpr" aria-label="Permalink to &quot;檢查脈搏，若無，立即開始 CPR&quot;">​</a></h4><ul><li>胸外按壓速率：100-120 次/分鐘</li><li>深度：成人至少 5 cm，兒童 5 cm，嬰兒約 4 cm</li><li>壓胸與放鬆時間相等</li><li>成人壓吹比：30:2</li></ul><h4 id="使用-aed-檢查是否為可電擊心律" tabindex="-1">使用 AED，檢查是否為可電擊心律 <a class="header-anchor" href="#使用-aed-檢查是否為可電擊心律" aria-label="Permalink to &quot;使用 AED，檢查是否為可電擊心律&quot;">​</a></h4><ul><li>若為VF或pulseless VT則立即電擊，電擊後立即開始2分鐘CPR</li><li>若為PEA或asystole則不可電擊，立刻CPR 2分鐘，並每兩分鐘檢查心律，直到病人會動或是專業團隊接手。</li></ul><h2 id="acls-1" tabindex="-1">ACLS <a class="header-anchor" href="#acls-1" aria-label="Permalink to &quot;ACLS&quot;">​</a></h2><h3 id="acls-advanced-cardiac-life-support-高級心臟救命術-主要適用於成人心搏驟停-cardiac-arrest-、無脈搏心律異常-如-pea-pulseless-vt-、急性冠症候群-acs-、中風-stroke-等緊急狀況。cardiac-arrest-指的是心臟突然停止有效泵血-導致血流動力學崩潰-hemodynamic-collapse-若未即時處置-將迅速導致死亡。" tabindex="-1">ACLS（Advanced Cardiac Life Support，高級心臟救命術）主要適用於成人心搏驟停（Cardiac Arrest）、無脈搏心律異常（如 PEA, pulseless VT）、急性冠症候群（ACS）、中風（Stroke）等緊急狀況。Cardiac Arrest 指的是心臟突然停止有效泵血，導致血流動力學崩潰（hemodynamic collapse），若未即時處置，將迅速導致死亡。 <a class="header-anchor" href="#acls-advanced-cardiac-life-support-高級心臟救命術-主要適用於成人心搏驟停-cardiac-arrest-、無脈搏心律異常-如-pea-pulseless-vt-、急性冠症候群-acs-、中風-stroke-等緊急狀況。cardiac-arrest-指的是心臟突然停止有效泵血-導致血流動力學崩潰-hemodynamic-collapse-若未即時處置-將迅速導致死亡。" aria-label="Permalink to &quot;ACLS（Advanced Cardiac Life Support，高級心臟救命術）主要適用於成人心搏驟停（Cardiac Arrest）、無脈搏心律異常（如 PEA, pulseless VT）、急性冠症候群（ACS）、中風（Stroke）等緊急狀況。Cardiac Arrest 指的是心臟突然停止有效泵血，導致血流動力學崩潰（hemodynamic collapse），若未即時處置，將迅速導致死亡。&quot;">​</a></h3><h2 id="四種常見的-cardiac-arrest-心律類型" tabindex="-1">四種常見的 Cardiac Arrest 心律類型： <a class="header-anchor" href="#四種常見的-cardiac-arrest-心律類型" aria-label="Permalink to &quot;四種常見的 Cardiac Arrest 心律類型：&quot;">​</a></h2><h3 id="ventricular-fibrillation-vf-心室顫動" tabindex="-1">Ventricular Fibrillation (VF, 心室顫動) <a class="header-anchor" href="#ventricular-fibrillation-vf-心室顫動" aria-label="Permalink to &quot;Ventricular Fibrillation (VF, 心室顫動)&quot;">​</a></h3><h4 id="最常見的可電擊心律" tabindex="-1">最常見的可電擊心律 <a class="header-anchor" href="#最常見的可電擊心律" aria-label="Permalink to &quot;最常見的可電擊心律&quot;">​</a></h4><h4 id="心室無組織性顫動-無有效收縮-→-無心輸出量" tabindex="-1">心室無組織性顫動，無有效收縮 → 無心輸出量 <a class="header-anchor" href="#心室無組織性顫動-無有效收縮-→-無心輸出量" aria-label="Permalink to &quot;心室無組織性顫動，無有效收縮 → 無心輸出量&quot;">​</a></h4><h4 id="ecg-特徵-快速、雜亂的波動-無明顯-p-波、qrs-波或-t-波" tabindex="-1">ECG 特徵： 快速、雜亂的波動，無明顯 P 波、QRS 波或 T 波 <a class="header-anchor" href="#ecg-特徵-快速、雜亂的波動-無明顯-p-波、qrs-波或-t-波" aria-label="Permalink to &quot;ECG 特徵： 快速、雜亂的波動，無明顯 P 波、QRS 波或 T 波&quot;">​</a></h4><h4 id="處置-立即去顫-defibrillation-cpr-epinephrine" tabindex="-1">處置： 立即去顫（Defibrillation）+ CPR + Epinephrine <a class="header-anchor" href="#處置-立即去顫-defibrillation-cpr-epinephrine" aria-label="Permalink to &quot;處置： 立即去顫（Defibrillation）+ CPR + Epinephrine&quot;">​</a></h4><h3 id="pulseless-ventricular-tachycardia-pvt-無脈搏心室頻脈" tabindex="-1">Pulseless Ventricular Tachycardia (pVT, 無脈搏心室頻脈) <a class="header-anchor" href="#pulseless-ventricular-tachycardia-pvt-無脈搏心室頻脈" aria-label="Permalink to &quot;Pulseless Ventricular Tachycardia (pVT, 無脈搏心室頻脈)&quot;">​</a></h3><h4 id="心室快速規律收縮但無效排血-→-無脈搏" tabindex="-1">心室快速規律收縮但無效排血 → 無脈搏 <a class="header-anchor" href="#心室快速規律收縮但無效排血-→-無脈搏" aria-label="Permalink to &quot;心室快速規律收縮但無效排血 → 無脈搏&quot;">​</a></h4><h4 id="ecg-特徵-寬大且規則的-qrs-波-通常-0-12-秒-無-p-波" tabindex="-1">ECG 特徵： 寬大且規則的 QRS 波（通常 &gt;0.12 秒），無 P 波 <a class="header-anchor" href="#ecg-特徵-寬大且規則的-qrs-波-通常-0-12-秒-無-p-波" aria-label="Permalink to &quot;ECG 特徵： 寬大且規則的 QRS 波（通常 &gt;0.12 秒），無 P 波&quot;">​</a></h4><h4 id="處置-立即去顫-defibrillation-cpr-amiodarone" tabindex="-1">處置： 立即去顫（Defibrillation）+ CPR + Amiodarone <a class="header-anchor" href="#處置-立即去顫-defibrillation-cpr-amiodarone" aria-label="Permalink to &quot;處置： 立即去顫（Defibrillation）+ CPR + Amiodarone&quot;">​</a></h4><h3 id="pulseless-electrical-activity-pea-無脈搏電氣活動" tabindex="-1">Pulseless Electrical Activity (PEA, 無脈搏電氣活動) <a class="header-anchor" href="#pulseless-electrical-activity-pea-無脈搏電氣活動" aria-label="Permalink to &quot;Pulseless Electrical Activity (PEA, 無脈搏電氣活動)&quot;">​</a></h3><h4 id="ecg-顯示有組織性電氣活動-如正常竇性或緩慢心律-但無有效脈搏" tabindex="-1">ECG 顯示有組織性電氣活動（如正常竇性或緩慢心律），但無有效脈搏 <a class="header-anchor" href="#ecg-顯示有組織性電氣活動-如正常竇性或緩慢心律-但無有效脈搏" aria-label="Permalink to &quot;ECG 顯示有組織性電氣活動（如正常竇性或緩慢心律），但無有效脈搏&quot;">​</a></h4><h4 id="常見原因-5h-5t-如低血容量、低氧、心包填塞、肺栓塞等" tabindex="-1">常見原因： 5H &amp; 5T（如低血容量、低氧、心包填塞、肺栓塞等） <a class="header-anchor" href="#常見原因-5h-5t-如低血容量、低氧、心包填塞、肺栓塞等" aria-label="Permalink to &quot;常見原因： 5H &amp; 5T（如低血容量、低氧、心包填塞、肺栓塞等）&quot;">​</a></h4><h4 id="處置-cpr-epinephrine-處理可逆原因-h-s-t-s" tabindex="-1">處置： CPR + Epinephrine + 處理可逆原因（H’s &amp; T’s） <a class="header-anchor" href="#處置-cpr-epinephrine-處理可逆原因-h-s-t-s" aria-label="Permalink to &quot;處置： CPR + Epinephrine + 處理可逆原因（H’s &amp; T’s）&quot;">​</a></h4><h4 id="不可電擊心律" tabindex="-1">不可電擊心律 <a class="header-anchor" href="#不可電擊心律" aria-label="Permalink to &quot;不可電擊心律&quot;">​</a></h4><h3 id="asystole-心靜止" tabindex="-1">Asystole (心靜止) <a class="header-anchor" href="#asystole-心靜止" aria-label="Permalink to &quot;Asystole (心靜止)&quot;">​</a></h3><h4 id="完全無心臟電氣活動-→-平直線-flatline" tabindex="-1">完全無心臟電氣活動 → 平直線（Flatline） <a class="header-anchor" href="#完全無心臟電氣活動-→-平直線-flatline" aria-label="Permalink to &quot;完全無心臟電氣活動 → 平直線（Flatline）&quot;">​</a></h4><h4 id="ecg-特徵-等電位直線-注意與-lead-脫落區分" tabindex="-1">ECG 特徵： 等電位直線（注意與 Lead 脫落區分） <a class="header-anchor" href="#ecg-特徵-等電位直線-注意與-lead-脫落區分" aria-label="Permalink to &quot;ECG 特徵： 等電位直線（注意與 Lead 脫落區分）&quot;">​</a></h4><h4 id="處置-cpr-epinephrine-尋找可逆原因" tabindex="-1">處置： CPR + Epinephrine + 尋找可逆原因 <a class="header-anchor" href="#處置-cpr-epinephrine-尋找可逆原因" aria-label="Permalink to &quot;處置： CPR + Epinephrine + 尋找可逆原因&quot;">​</a></h4><h4 id="不可電擊心律-1" tabindex="-1">不可電擊心律 <a class="header-anchor" href="#不可電擊心律-1" aria-label="Permalink to &quot;不可電擊心律&quot;">​</a></h4><h2 id="acls-在心搏驟停時的核心流程" tabindex="-1">ACLS 在心搏驟停時的核心流程 <a class="header-anchor" href="#acls-在心搏驟停時的核心流程" aria-label="Permalink to &quot;ACLS 在心搏驟停時的核心流程&quot;">​</a></h2><h3 id="確認狀況及求援" tabindex="-1">確認狀況及求援 <a class="header-anchor" href="#確認狀況及求援" aria-label="Permalink to &quot;確認狀況及求援&quot;">​</a></h3><h4 id="評估患者反應-無反應-無正常呼吸-喘息" tabindex="-1">評估患者反應（無反應，無正常呼吸/喘息） <a class="header-anchor" href="#評估患者反應-無反應-無正常呼吸-喘息" aria-label="Permalink to &quot;評估患者反應（無反應，無正常呼吸/喘息）&quot;">​</a></h4><h4 id="啟動急救系統-activate-emergency-response-system" tabindex="-1">啟動急救系統（Activate emergency response system） <a class="header-anchor" href="#啟動急救系統-activate-emergency-response-system" aria-label="Permalink to &quot;啟動急救系統（Activate emergency response system）&quot;">​</a></h4><h4 id="開始高品質-cpr-chest-compression-30-2-速率-100-120-min-深度至少-5-cm" tabindex="-1">開始高品質 CPR（Chest compression 30:2，速率 100-120/min，深度至少 5 cm） <a class="header-anchor" href="#開始高品質-cpr-chest-compression-30-2-速率-100-120-min-深度至少-5-cm" aria-label="Permalink to &quot;開始高品質 CPR（Chest compression 30:2，速率 100-120/min，深度至少 5 cm）&quot;">​</a></h4><h4 id="盡快使用-aed-若可用" tabindex="-1">盡快使用 AED（若可用） <a class="header-anchor" href="#盡快使用-aed-若可用" aria-label="Permalink to &quot;盡快使用 AED（若可用）&quot;">​</a></h4><h3 id="供氧與監測" tabindex="-1">供氧與監測 <a class="header-anchor" href="#供氧與監測" aria-label="Permalink to &quot;供氧與監測&quot;">​</a></h3><h4 id="給予氧氣-apply-oxygen" tabindex="-1">給予氧氣（Apply Oxygen）： <a class="header-anchor" href="#給予氧氣-apply-oxygen" aria-label="Permalink to &quot;給予氧氣（Apply Oxygen）：&quot;">​</a></h4><ul><li>使用 Bag-Valve-Mask（BVM）與 100% 氧氣</li><li>若有條件可進行進階氣道（如氣管插管）</li></ul><h4 id="連接心律監測-attach-monitor-defibrillator" tabindex="-1">連接心律監測（Attach Monitor/Defibrillator）： <a class="header-anchor" href="#連接心律監測-attach-monitor-defibrillator" aria-label="Permalink to &quot;連接心律監測（Attach Monitor/Defibrillator）：&quot;">​</a></h4><ul><li>使用 AED 或 Manual Defibrillator</li><li>進行心律分析（Rhythm Analysis）</li></ul><h3 id="分析心律-rhythm-analysis" tabindex="-1">分析心律（Rhythm Analysis） <a class="header-anchor" href="#分析心律-rhythm-analysis" aria-label="Permalink to &quot;分析心律（Rhythm Analysis）&quot;">​</a></h3><h4 id="使用-defibrillator-aed-檢查心律-分為兩大類" tabindex="-1">使用 Defibrillator/AED 檢查心律，分為兩大類： <a class="header-anchor" href="#使用-defibrillator-aed-檢查心律-分為兩大類" aria-label="Permalink to &quot;使用 Defibrillator/AED 檢查心律，分為兩大類：&quot;">​</a></h4><ul><li>可電擊心律（Shockable Rhythms） <ul><li>Ventricular fibrillation (VF, 心室顫動)</li><li>Pulseless ventricular tachycardia (pVT, 無脈搏心室頻脈) <ul><li>立即200J去顫（Defibrillation）</li><li>繼續 CPR 2 分鐘，然後重新評估心律</li><li>同時給予 Epinephrine 1 mg（每 3-5 分鐘一次）</li><li>若仍為可電擊心律，給Amiodarone 300 mg（可追加 150 mg）</li></ul></li></ul></li><li>不可電擊心律（Non-shockable Rhythms） - Asystole（心靜止） - Pulseless Electrical Activity (PEA, 無脈搏電氣活動) - 繼續 CPR 2 分鐘，不電擊 - 立即給 Epinephrine 1 mg（每 3-5 分鐘一次） - 尋找可逆因素（H’s &amp; T’s）</li></ul><h3 id="可逆因素-h-s-t-s" tabindex="-1">可逆因素（H’s &amp; T’s） <a class="header-anchor" href="#可逆因素-h-s-t-s" aria-label="Permalink to &quot;可逆因素（H’s &amp; T’s）&quot;">​</a></h3><h4 id="在處理-pea-或-asystole-時-應積極尋找並處理可逆因素" tabindex="-1">在處理 PEA 或 Asystole 時，應積極尋找並處理可逆因素： <a class="header-anchor" href="#在處理-pea-或-asystole-時-應積極尋找並處理可逆因素" aria-label="Permalink to &quot;在處理 PEA 或 Asystole 時，應積極尋找並處理可逆因素：&quot;">​</a></h4><ul><li>H’s（5H）： <ul><li>Hypovolemia（低血容量）</li><li>Hypoxia（低氧）</li><li>Hydrogen ion (Acidosis)（酸中毒）</li><li>Hyper/Hypokalemia（高鉀/低鉀）</li><li>Hypothermia（低溫）</li></ul></li><li>T’s（5T）： <ul><li>Tension pneumothorax（張力性氣胸）</li><li>Tamponade (cardiac)（心臟填塞）</li><li>Toxins（毒物/藥物過量，如 opioid, TCA）</li><li>Thrombosis (pulmonary or coronary)（肺栓塞/心肌梗塞）</li><li>Trauma（創傷）</li></ul></li></ul><h3 id="循環恢復-rosc-return-of-spontaneous-circulation" tabindex="-1">循環恢復（ROSC, Return of Spontaneous Circulation） <a class="header-anchor" href="#循環恢復-rosc-return-of-spontaneous-circulation" aria-label="Permalink to &quot;循環恢復（ROSC, Return of Spontaneous Circulation）&quot;">​</a></h3><h4 id="若有心跳恢復-進入-post-cardiac-arrest-care" tabindex="-1">若有心跳恢復，進入 Post-Cardiac Arrest Care <a class="header-anchor" href="#若有心跳恢復-進入-post-cardiac-arrest-care" aria-label="Permalink to &quot;若有心跳恢復，進入 Post-Cardiac Arrest Care&quot;">​</a></h4><h4 id="維持-spo2-94-99" tabindex="-1">維持 SpO₂ 94-99% <a class="header-anchor" href="#維持-spo2-94-99" aria-label="Permalink to &quot;維持 SpO₂ 94-99%&quot;">​</a></h4><h4 id="維持血壓-sbp-≥90-mmhg-map-≥65-mmhg" tabindex="-1">維持血壓（SBP ≥90 mmHg, MAP ≥65 mmHg） <a class="header-anchor" href="#維持血壓-sbp-≥90-mmhg-map-≥65-mmhg" aria-label="Permalink to &quot;維持血壓（SBP ≥90 mmHg, MAP ≥65 mmHg）&quot;">​</a></h4><h4 id="若患者無意識-考慮-targeted-temperature-management-ttm-32-36°c-24-48-小時" tabindex="-1">若患者無意識，考慮 Targeted Temperature Management (TTM, 32-36°C, 24-48 小時） <a class="header-anchor" href="#若患者無意識-考慮-targeted-temperature-management-ttm-32-36°c-24-48-小時" aria-label="Permalink to &quot;若患者無意識，考慮 Targeted Temperature Management (TTM, 32-36°C, 24-48 小時）&quot;">​</a></h4><h4 id="尋找原發原因-如心導管檢查" tabindex="-1">尋找原發原因（如心導管檢查） <a class="header-anchor" href="#尋找原發原因-如心導管檢查" aria-label="Permalink to &quot;尋找原發原因（如心導管檢查）&quot;">​</a></h4><h4 id="持續監測-預防再發作" tabindex="-1">持續監測，預防再發作 <a class="header-anchor" href="#持續監測-預防再發作" aria-label="Permalink to &quot;持續監測，預防再發作&quot;">​</a></h4><h2 id="acls-心搏過緩處置流程" tabindex="-1">ACLS 心搏過緩處置流程 <a class="header-anchor" href="#acls-心搏過緩處置流程" aria-label="Permalink to &quot;ACLS 心搏過緩處置流程&quot;">​</a></h2><h3 id="初步評估" tabindex="-1">初步評估 <a class="header-anchor" href="#初步評估" aria-label="Permalink to &quot;初步評估&quot;">​</a></h3><h4 id="確認心搏過緩定義" tabindex="-1">確認心搏過緩定義： <a class="header-anchor" href="#確認心搏過緩定義" aria-label="Permalink to &quot;確認心搏過緩定義：&quot;">​</a></h4><ul><li>心率 &lt; 50 bpm（但臨床意義取決於症狀與血流狀況）</li></ul><h4 id="評估生命徵象與症狀" tabindex="-1">評估生命徵象與症狀： <a class="header-anchor" href="#評估生命徵象與症狀" aria-label="Permalink to &quot;評估生命徵象與症狀：&quot;">​</a></h4><ul><li>血壓、呼吸、意識狀態</li><li>有無低灌流症狀（Poor Perfusion Signs）： <ul><li>意識改變（如意識不清、嗜睡）</li><li>低血壓或休克跡象</li><li>胸痛</li><li>呼吸困難</li><li>急性心衰竭徵象</li></ul></li></ul><h3 id="情況是否穩定" tabindex="-1">情況是否穩定？ <a class="header-anchor" href="#情況是否穩定" aria-label="Permalink to &quot;情況是否穩定？&quot;">​</a></h3><h3 id="積極處置-若出現症狀" tabindex="-1">積極處置（若出現症狀） <a class="header-anchor" href="#積極處置-若出現症狀" aria-label="Permalink to &quot;積極處置（若出現症狀）&quot;">​</a></h3><h4 id="藥物治療順序" tabindex="-1">藥物治療順序： <a class="header-anchor" href="#藥物治療順序" aria-label="Permalink to &quot;藥物治療順序：&quot;">​</a></h4><ul><li>Atropine <ul><li>劑量： 0.5 mg IV 每 3-5 分鐘一次</li><li>最大劑量： 3 mg</li><li>作用： 阻斷迷走神經刺激，增加心率</li></ul></li><li>若 Atropine 無效 → 考慮以下選項： <ul><li>Dopamine（多巴胺） <ul><li>劑量：2-20 mcg/kg/min IV（依反應調整）</li><li>增加心輸出量與心率</li></ul></li><li>Epinephrine（腎上腺素） <ul><li>劑量：2-10 mcg/min IV</li><li>正性心肌作用，增加心率與血壓</li></ul></li></ul></li></ul><h3 id="經皮心臟節律器-transcutaneous-pacing-tcp" tabindex="-1">經皮心臟節律器（Transcutaneous Pacing, TCP） <a class="header-anchor" href="#經皮心臟節律器-transcutaneous-pacing-tcp" aria-label="Permalink to &quot;經皮心臟節律器（Transcutaneous Pacing, TCP）&quot;">​</a></h3><h4 id="適用於" tabindex="-1">適用於： <a class="header-anchor" href="#適用於" aria-label="Permalink to &quot;適用於：&quot;">​</a></h4><ul><li>Atropine 無效或反應不佳</li><li>高度房室傳導阻滯（如 Mobitz II, 完全房室傳導阻滯）</li><li>重度症狀或不穩定狀況</li></ul><h3 id="繼續管理與考慮病因" tabindex="-1">繼續管理與考慮病因 <a class="header-anchor" href="#繼續管理與考慮病因" aria-label="Permalink to &quot;繼續管理與考慮病因&quot;">​</a></h3><h4 id="糾正可能的可逆原因-h-s-t-s" tabindex="-1">糾正可能的可逆原因（H’s &amp; T’s）： <a class="header-anchor" href="#糾正可能的可逆原因-h-s-t-s" aria-label="Permalink to &quot;糾正可能的可逆原因（H’s &amp; T’s）：&quot;">​</a></h4><ul><li>Hypoxia、Hypovolemia、Hypothermia、Hyper/Hypokalemia</li><li>Tension pneumothorax、Tamponade、Toxins、Thrombosis（MI/PE）</li></ul><h4 id="考慮永久性節律器-pacemaker-的適應症" tabindex="-1">考慮永久性節律器（Pacemaker）的適應症 <a class="header-anchor" href="#考慮永久性節律器-pacemaker-的適應症" aria-label="Permalink to &quot;考慮永久性節律器（Pacemaker）的適應症&quot;">​</a></h4><h2 id="acls心博過速處置流程" tabindex="-1">ACLS心博過速處置流程 <a class="header-anchor" href="#acls心博過速處置流程" aria-label="Permalink to &quot;ACLS心博過速處置流程&quot;">​</a></h2><h3 id="初步評估-1" tabindex="-1">初步評估 <a class="header-anchor" href="#初步評估-1" aria-label="Permalink to &quot;初步評估&quot;">​</a></h3><h4 id="確認心搏過速定義" tabindex="-1">確認心搏過速定義： <a class="header-anchor" href="#確認心搏過速定義" aria-label="Permalink to &quot;確認心搏過速定義：&quot;">​</a></h4><ul><li>成人心率 &gt; 150 bpm</li></ul><h4 id="評估生命徵象與症狀-1" tabindex="-1">評估生命徵象與症狀： <a class="header-anchor" href="#評估生命徵象與症狀-1" aria-label="Permalink to &quot;評估生命徵象與症狀：&quot;">​</a></h4><ul><li>血壓、呼吸、意識狀態</li><li>有無低灌流症狀（Poor Perfusion Signs）： <ul><li>意識改變（如意識不清、嗜睡）</li><li>低血壓或休克跡象</li><li>胸痛</li><li>呼吸困難</li><li>急性心衰竭徵象</li></ul></li></ul><h3 id="情況是否穩定-1" tabindex="-1">情況是否穩定？ <a class="header-anchor" href="#情況是否穩定-1" aria-label="Permalink to &quot;情況是否穩定？&quot;">​</a></h3><h3 id="穩定型心搏過速-藥物處置" tabindex="-1">穩定型心搏過速：藥物處置 <a class="header-anchor" href="#穩定型心搏過速-藥物處置" aria-label="Permalink to &quot;穩定型心搏過速：藥物處置&quot;">​</a></h3><h4 id="窄-qrs-複合波-0-12-秒-→-可能為-svt-或-af" tabindex="-1">窄 QRS 複合波（&lt;0.12 秒） → 可能為 SVT 或 AF <a class="header-anchor" href="#窄-qrs-複合波-0-12-秒-→-可能為-svt-或-af" aria-label="Permalink to &quot;窄 QRS 複合波（&lt;0.12 秒） → 可能為 SVT 或 AF&quot;">​</a></h4><h4 id="迷走神經刺激法-vagal-maneuvers" tabindex="-1">迷走神經刺激法（Vagal maneuvers）： <a class="header-anchor" href="#迷走神經刺激法-vagal-maneuvers" aria-label="Permalink to &quot;迷走神經刺激法（Vagal maneuvers）：&quot;">​</a></h4><ul><li>咳嗽、Valsalva maneuver 或冷水刺激</li></ul><h4 id="adenosine-腺苷" tabindex="-1">Adenosine（腺苷）： <a class="header-anchor" href="#adenosine-腺苷" aria-label="Permalink to &quot;Adenosine（腺苷）：&quot;">​</a></h4><ul><li>劑量： 6 mg IV push → 無效可追加 12 mg</li><li>快速注射並緊接 20 mL 生理食鹽水沖洗</li></ul><h4 id="若-adenosine-無效-→-考慮" tabindex="-1">若 Adenosine 無效 → 考慮： <a class="header-anchor" href="#若-adenosine-無效-→-考慮" aria-label="Permalink to &quot;若 Adenosine 無效 → 考慮：&quot;">​</a></h4><ul><li>Beta-blockers（如 Metoprolol）</li><li>Calcium channel blockers（如 Diltiazem）</li><li>針對 AFib/AFlutter： 使用抗心律不整藥（如 Amiodarone）</li></ul><h4 id="寬-qrs-複合波-≥0-12-秒-→-可能為-vt-vf-或-svt-with-aberrancy" tabindex="-1">寬 QRS 複合波（≥0.12 秒） → 可能為 VT/VF 或 SVT with aberrancy <a class="header-anchor" href="#寬-qrs-複合波-≥0-12-秒-→-可能為-vt-vf-或-svt-with-aberrancy" aria-label="Permalink to &quot;寬 QRS 複合波（≥0.12 秒） → 可能為 VT/VF 或 SVT with aberrancy&quot;">​</a></h4><ul><li>考慮 Amiodarone、Procainamide 或 Sotalol： <ul><li>Amiodarone： 150 mg IV over 10 分鐘，必要時可再重複</li></ul></li><li>若懷疑為 Torsades de Pointes（多形性 VT）： <ul><li>Magnesium sulfate 2 g IV</li></ul></li><li>若為 WPW 症候群合併 AFib → 禁用 AV node 阻斷藥物（如 CCB, BB）</li><li>若症狀惡化或無效 → 考慮同步電復律</li></ul><h3 id="不穩定型心搏過速-立即同步電擊-synchronized-cardioversion" tabindex="-1">不穩定型心搏過速：立即同步電擊（synchronized cardioversion） <a class="header-anchor" href="#不穩定型心搏過速-立即同步電擊-synchronized-cardioversion" aria-label="Permalink to &quot;不穩定型心搏過速：立即同步電擊（synchronized cardioversion）&quot;">​</a></h3><h4 id="適用於以下情況" tabindex="-1">適用於以下情況： <a class="header-anchor" href="#適用於以下情況" aria-label="Permalink to &quot;適用於以下情況：&quot;">​</a></h4><ul><li>低血壓、意識改變、胸痛或急性心衰竭</li></ul><h4 id="同步電復律劑量建議" tabindex="-1">同步電復律劑量建議： <a class="header-anchor" href="#同步電復律劑量建議" aria-label="Permalink to &quot;同步電復律劑量建議：&quot;">​</a></h4><ul><li>Narrow Regular（SVT）： 50-100 J</li><li>Narrow Irregular（AFib）： 120-200 J</li><li>Wide Regular（VT）： 100 J</li><li>Wide Irregular（VF 或多形性 VT）： 非同步電擊（Defibrillation）</li></ul><h3 id="考慮可逆原因-h-s-t-s" tabindex="-1">考慮可逆原因（H’s &amp; T’s）： <a class="header-anchor" href="#考慮可逆原因-h-s-t-s" aria-label="Permalink to &quot;考慮可逆原因（H’s &amp; T’s）：&quot;">​</a></h3><h4 id="hypoxia、hypovolemia、hyper-hypokalemia、hypothermia" tabindex="-1">Hypoxia、Hypovolemia、Hyper/Hypokalemia、Hypothermia <a class="header-anchor" href="#hypoxia、hypovolemia、hyper-hypokalemia、hypothermia" aria-label="Permalink to &quot;Hypoxia、Hypovolemia、Hyper/Hypokalemia、Hypothermia&quot;">​</a></h4><h4 id="tension-pneumothorax、tamponade、toxins、thrombosis-mi-pe" tabindex="-1">Tension pneumothorax、Tamponade、Toxins、Thrombosis（MI/PE） <a class="header-anchor" href="#tension-pneumothorax、tamponade、toxins、thrombosis-mi-pe" aria-label="Permalink to &quot;Tension pneumothorax、Tamponade、Toxins、Thrombosis（MI/PE）&quot;">​</a></h4><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h2 id="boards-beyonds-acls-tachycardia" tabindex="-1">Boards &amp; Beyonds：ACLS&amp;tachycardia <a class="header-anchor" href="#boards-beyonds-acls-tachycardia" aria-label="Permalink to &quot;Boards &amp; Beyonds：ACLS&amp;tachycardia&quot;">​</a></h2><h2 id="usmle-step-2-ck" tabindex="-1">USMLE Step 2 CK <a class="header-anchor" href="#usmle-step-2-ck" aria-label="Permalink to &quot;USMLE Step 2 CK&quot;">​</a></h2><h2 id="https-cpr-heart-org-en-resuscitation-science-cpr-and-ecc-guidelines-algorithms" tabindex="-1"><a href="https://cpr.heart.org/en/resuscitation-science/cpr-and-ecc-guidelines/algorithms" target="_blank" rel="noreferrer">https://cpr.heart.org/en/resuscitation-science/cpr-and-ecc-guidelines/algorithms</a> <a class="header-anchor" href="#https-cpr-heart-org-en-resuscitation-science-cpr-and-ecc-guidelines-algorithms" aria-label="Permalink to &quot;https://cpr.heart.org/en/resuscitation-science/cpr-and-ecc-guidelines/algorithms&quot;">​</a></h2><table tabindex="0"><thead><tr><th>情況</th><th>處置</th></tr></thead><tbody><tr><td>無症狀/血流穩定</td><td>觀察、持續監測、尋找病因</td></tr><tr><td>有症狀/血流不穩</td><td>進行藥物或TCP治療</td></tr></tbody></table><table tabindex="0"><thead><tr><th>情況</th><th>處置</th></tr></thead><tbody><tr><td>不穩定（有症狀）</td><td>立即同步電復律（Synchronized Cardioversion）</td></tr><tr><td>穩定（無症狀）</td><td>根據 QRS 波型進行藥物治療</td></tr></tbody></table>',109)]))}const b=e(t,[["render",o]]);export{m as __pageData,b as default};
