import{_ as l,c as i,o as e,ae as r}from"./chunks/framework.Cthp9TdA.js";const b=JSON.parse('{"title":"EKG","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-EKG-5.md","filePath":"InternalMedicineDepartment-EKG-5.md"}'),t={name:"InternalMedicineDepartment-EKG-5.md"};function h(o,a,d,n,u,c){return e(),i("div",null,a[0]||(a[0]=[r('<h1 id="ekg" tabindex="-1">EKG <a class="header-anchor" href="#ekg" aria-label="Permalink to &quot;EKG&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h3 id="字型-大小" tabindex="-1">字型&amp;大小 <a class="header-anchor" href="#字型-大小" aria-label="Permalink to &quot;字型&amp;大小&quot;">​</a></h3><h3 id="ekg-基本概念" tabindex="-1">EKG 基本概念 <a class="header-anchor" href="#ekg-基本概念" aria-label="Permalink to &quot;EKG 基本概念&quot;">​</a></h3><h3 id="_12-導程-ekg-twelve-lead-ekg" tabindex="-1">12 導程 EKG（Twelve Lead EKG） <a class="header-anchor" href="#_12-導程-ekg-twelve-lead-ekg" aria-label="Permalink to &quot;12 導程 EKG（Twelve Lead EKG）&quot;">​</a></h3><h4 id="由-12-個leads-組成-每個導程觀察心臟電活動的不同角度。" tabindex="-1">由 12 個leads 組成，每個導程觀察心臟電活動的不同角度。 <a class="header-anchor" href="#由-12-個leads-組成-每個導程觀察心臟電活動的不同角度。" aria-label="Permalink to &quot;由 12 個leads 組成，每個導程觀察心臟電活動的不同角度。&quot;">​</a></h4><h4 id="導程分類" tabindex="-1">導程分類： <a class="header-anchor" href="#導程分類" aria-label="Permalink to &quot;導程分類：&quot;">​</a></h4><ul><li>肢體導程（Limb Leads）：I、II、III</li><li>增強導程（Augmented Leads）：aVR、aVL、aVF</li><li>胸前導程（Precordial Leads）：V1、V2、V3、V4、V5、V6</li></ul><h4 id="作用" tabindex="-1">作用： <a class="header-anchor" href="#作用" aria-label="Permalink to &quot;作用：&quot;">​</a></h4><ul><li>觀察 左心房、右心房、左心室、右心室的去極化（depolarization）。</li><li>QRS 波群方向可能會因導程位置不同而呈現 向上（正向）或向下（負向）。</li></ul><h3 id="二、ekg-正常波形" tabindex="-1">二、EKG 正常波形 <a class="header-anchor" href="#二、ekg-正常波形" aria-label="Permalink to &quot;二、EKG 正常波形&quot;">​</a></h3><h4 id="_1-主要波形" tabindex="-1">1. 主要波形 <a class="header-anchor" href="#_1-主要波形" aria-label="Permalink to &quot;1. 主要波形&quot;">​</a></h4><ul><li>P 波（P wave）：心房去極化</li><li>QRS 波群（QRS complex）：心室去極化</li><li>T 波（T wave）：心室再極化</li></ul><h4 id="_2-波形方向" tabindex="-1">2. 波形方向 <a class="header-anchor" href="#_2-波形方向" aria-label="Permalink to &quot;2. 波形方向&quot;">​</a></h4><ul><li>向上偏移（positive deflection）：波形上升</li><li>向下偏移（negative deflection）：波形下降</li><li>在 正常 EKG 中，P 波、QRS 波群、T 波 通常呈現正向偏移（向上）。</li></ul><h3 id="三、心率計算" tabindex="-1">三、心率計算 <a class="header-anchor" href="#三、心率計算" aria-label="Permalink to &quot;三、心率計算&quot;">​</a></h3><h4 id="_1-以-qrs-波群計算" tabindex="-1">1. 以 QRS 波群計算 <a class="header-anchor" href="#_1-以-qrs-波群計算" aria-label="Permalink to &quot;1. 以 QRS 波群計算&quot;">​</a></h4><h4 id="方法-計算-相鄰-qrs-波群間的大格數量。" tabindex="-1">方法：計算 相鄰 QRS 波群間的大格數量。 <a class="header-anchor" href="#方法-計算-相鄰-qrs-波群間的大格數量。" aria-label="Permalink to &quot;方法：計算 相鄰 QRS 波群間的大格數量。&quot;">​</a></h4><h4 id="公式" tabindex="-1">公式： <a class="header-anchor" href="#公式" aria-label="Permalink to &quot;公式：&quot;">​</a></h4><h4 id="心率-bpm-300-兩個qrs波群間的大格數" tabindex="-1">心率（𝑏𝑝𝑚）=300/兩個𝑄𝑅𝑆波群間的大格數 <a class="header-anchor" href="#心率-bpm-300-兩個qrs波群間的大格數" aria-label="Permalink to &quot;心率（𝑏𝑝𝑚）=300/兩個𝑄𝑅𝑆波群間的大格數&quot;">​</a></h4><h4 id="_2-大格數與心率對應關係" tabindex="-1">2. 大格數與心率對應關係 <a class="header-anchor" href="#_2-大格數與心率對應關係" aria-label="Permalink to &quot;2. 大格數與心率對應關係&quot;">​</a></h4><h4 id="正常心率範圍-60-100-bpm-通常約-3-5-大格之間-。" tabindex="-1">正常心率範圍：60 - 100 bpm（通常約 3-5 大格之間）。 <a class="header-anchor" href="#正常心率範圍-60-100-bpm-通常約-3-5-大格之間-。" aria-label="Permalink to &quot;正常心率範圍：60 - 100 bpm（通常約 3-5 大格之間）。&quot;">​</a></h4><h4 id="例如-如果-qrs-波群間有-4-個大格-則-心率約為-75-bpm" tabindex="-1">例如：如果 QRS 波群間有 4 個大格，則 心率約為 75 bpm <a class="header-anchor" href="#例如-如果-qrs-波群間有-4-個大格-則-心率約為-75-bpm" aria-label="Permalink to &quot;例如：如果 QRS 波群間有 4 個大格，則 心率約為 75 bpm&quot;">​</a></h4><h3 id="四、qrs-軸向判讀-qrs-axis" tabindex="-1">四、QRS 軸向判讀（QRS Axis） <a class="header-anchor" href="#四、qrs-軸向判讀-qrs-axis" aria-label="Permalink to &quot;四、QRS 軸向判讀（QRS Axis）&quot;">​</a></h3><h4 id="_1-電軸的概念" tabindex="-1">1. 電軸的概念 <a class="header-anchor" href="#_1-電軸的概念" aria-label="Permalink to &quot;1. 電軸的概念&quot;">​</a></h4><ul><li>心室去極化的總向量（Summation Vector）稱為QRS axis</li><li>正常狀況下，QRS 軸方向 向左下方（-30° 到 +90°）。</li><li>電軸偏移代表潛在心臟異常</li></ul><h4 id="_2-軸向的分類" tabindex="-1">2. 軸向的分類 <a class="header-anchor" href="#_2-軸向的分類" aria-label="Permalink to &quot;2. 軸向的分類&quot;">​</a></h4><ul><li>QRS 軸範圍 類型 可能的臨床意義</li><li>-30° 至 +90° Normal QRS Axis: normal</li><li>-30° 至 -90° Left Axis Deviation, LAD: LBBB, Ventricular Rhythm</li><li>+90° 至 +180° Right Axis Deviation, RAD: RBBB, RVH</li><li>+180° 以上 Extreme Axis Deviation: 罕見，可能與右心病變或心室心律異常有關</li></ul><h3 id="五、qrs-軸的測量方法" tabindex="-1">五、QRS 軸的測量方法 <a class="header-anchor" href="#五、qrs-軸的測量方法" aria-label="Permalink to &quot;五、QRS 軸的測量方法&quot;">​</a></h3><h4 id="標準計算方法" tabindex="-1">標準計算方法 <a class="header-anchor" href="#標準計算方法" aria-label="Permalink to &quot;標準計算方法&quot;">​</a></h4><ul><li>選擇 Lead I 和 Lead aVF</li><li>Lead I 負向（QRS 指向右側）→ 右軸偏移（RAD）。</li><li>Lead aVF 負向（QRS 指向上方）→ 左軸偏移（LAD）。</li><li>兩者皆正向（QRS 指向左下方）→ 軸向正常。</li></ul><h4 id="_2-簡化法-quick-method" tabindex="-1">2. 簡化法（Quick Method） <a class="header-anchor" href="#_2-簡化法-quick-method" aria-label="Permalink to &quot;2. 簡化法（Quick Method）&quot;">​</a></h4><ul><li>先看 aVR：aVR 應該為負向，若為正向可能代表 導聯接錯（limb lead reversal） 或 罕見的右心異常（dextrocardia）。</li><li>判斷 Lead I 和 Lead II <ul><li>Lead I、Lead II 皆正向 → 正常 QRS 軸。</li><li>Lead I 正向，Lead II 負向 → 左軸偏移（LAD）。</li><li>Lead I 負向 ，Lead II 正向→ 右軸偏移（RAD）。</li></ul></li></ul><h4 id="_3-例外狀況" tabindex="-1">3. 例外狀況 <a class="header-anchor" href="#_3-例外狀況" aria-label="Permalink to &quot;3. 例外狀況&quot;">​</a></h4><ul><li>若 Lead II 波形為等高雙向（isoelectric）：軸向可能在 0° 到 -30°（生理性左軸偏移，不算異常）。</li></ul><h3 id="六、心電圖軸判讀總結" tabindex="-1">六、心電圖軸判讀總結 <a class="header-anchor" href="#六、心電圖軸判讀總結" aria-label="Permalink to &quot;六、心電圖軸判讀總結&quot;">​</a></h3><h3 id="先看-avr" tabindex="-1">先看 aVR： <a class="header-anchor" href="#先看-avr" aria-label="Permalink to &quot;先看 aVR：&quot;">​</a></h3><h4 id="若-avr-為正向-可能是導聯錯誤或右心異常" tabindex="-1">若 aVR 為正向，可能是導聯錯誤或右心異常 <a class="header-anchor" href="#若-avr-為正向-可能是導聯錯誤或右心異常" aria-label="Permalink to &quot;若 aVR 為正向，可能是導聯錯誤或右心異常&quot;">​</a></h4><h3 id="判斷-lead-i-和-lead-ii" tabindex="-1">判斷 Lead I 和 Lead II： <a class="header-anchor" href="#判斷-lead-i-和-lead-ii" aria-label="Permalink to &quot;判斷 Lead I 和 Lead II：&quot;">​</a></h3><h4 id="i、ii-皆正向-→-正常-qrs-軸。" tabindex="-1">I、II 皆正向 → 正常 QRS 軸。 <a class="header-anchor" href="#i、ii-皆正向-→-正常-qrs-軸。" aria-label="Permalink to &quot;I、II 皆正向 → 正常 QRS 軸。&quot;">​</a></h4><h4 id="i-正向-ii-負向-→-左軸偏移-lad-。" tabindex="-1">I 正向，II 負向 → 左軸偏移（LAD）。 <a class="header-anchor" href="#i-正向-ii-負向-→-左軸偏移-lad-。" aria-label="Permalink to &quot;I 正向，II 負向 → 左軸偏移（LAD）。&quot;">​</a></h4><h4 id="i-負向-ii-正向-→-右軸偏移-rad-。" tabindex="-1">I 負向，II 正向 → 右軸偏移（RAD）。 <a class="header-anchor" href="#i-負向-ii-正向-→-右軸偏移-rad-。" aria-label="Permalink to &quot;I 負向，II 正向 → 右軸偏移（RAD）。&quot;">​</a></h4><h3 id="七、系統化判讀步驟" tabindex="-1">七、系統化判讀步驟 <a class="header-anchor" href="#七、系統化判讀步驟" aria-label="Permalink to &quot;七、系統化判讀步驟&quot;">​</a></h3><h4 id="第一步-確認-p-波是否存在" tabindex="-1">第一步：確認 P 波是否存在 <a class="header-anchor" href="#第一步-確認-p-波是否存在" aria-label="Permalink to &quot;第一步：確認 P 波是否存在&quot;">​</a></h4><h4 id="第二步-判斷節律是否規律" tabindex="-1">第二步：判斷節律是否規律 <a class="header-anchor" href="#第二步-判斷節律是否規律" aria-label="Permalink to &quot;第二步：判斷節律是否規律&quot;">​</a></h4><h4 id="第三步-檢查-qrs-波群是否寬或窄" tabindex="-1">第三步：檢查 QRS 波群是否寬或窄 <a class="header-anchor" href="#第三步-檢查-qrs-波群是否寬或窄" aria-label="Permalink to &quot;第三步：檢查 QRS 波群是否寬或窄&quot;">​</a></h4><h4 id="第四步-綜合判斷心律異常類型" tabindex="-1">第四步：綜合判斷心律異常類型 <a class="header-anchor" href="#第四步-綜合判斷心律異常類型" aria-label="Permalink to &quot;第四步：綜合判斷心律異常類型&quot;">​</a></h4><h3 id="八、p-波與心律" tabindex="-1">八、P 波與心律 <a class="header-anchor" href="#八、p-波與心律" aria-label="Permalink to &quot;八、P 波與心律&quot;">​</a></h3><h4 id="_1-確認-p-波" tabindex="-1">1. 確認 P 波 <a class="header-anchor" href="#_1-確認-p-波" aria-label="Permalink to &quot;1. 確認 P 波&quot;">​</a></h4><ul><li>P 波代表 atrium depolarization， 通常由SA node產生。</li><li>P 波來自竇房結的特徵： <ul><li>在 Lead II、Lead III、aVF 為正向（upright）。</li></ul></li><li>若 P 波的形態異常，可能來自 ectopic focus。</li></ul><h4 id="_2-判斷節律是否規律" tabindex="-1">2. 判斷節律是否規律 <a class="header-anchor" href="#_2-判斷節律是否規律" aria-label="Permalink to &quot;2. 判斷節律是否規律&quot;">​</a></h4><ul><li>規律節律（Regular Rhythm）：R-R 間距相等，例如 竇性心律（Sinus Rhythm）。</li><li>不規律節律（Irregular Rhythm）： <ul><li>完全不規則（Irregularly Irregular）： <ul><li>無 P 波 → 最可能為心房顫動（Atrial Fibrillation, AF)</li><li>部分不規則（Regularly Irregular）： <ul><li>P 波存在 → 可能為竇性心律合併早期收縮（PACs）</li><li>P 波形態不同 → 可能為多源性心房心動過速（MAT）</li></ul></li></ul></li></ul></li></ul><h3 id="九、節律規律與否-regular-or-irregular" tabindex="-1">九、節律規律與否(regular or irregular) <a class="header-anchor" href="#九、節律規律與否-regular-or-irregular" aria-label="Permalink to &quot;九、節律規律與否(regular or irregular)&quot;">​</a></h3><h4 id="_1-p-波存在且節律規律" tabindex="-1">1. P 波存在且節律規律 <a class="header-anchor" href="#_1-p-波存在且節律規律" aria-label="Permalink to &quot;1. P 波存在且節律規律&quot;">​</a></h4><ul><li>竇性心律（Sinus Rhythm）</li><li>P 波呈現正常竇房結形態（Lead II、III、aVF 為正向）。</li></ul><h4 id="其他可能的規律心律" tabindex="-1">其他可能的規律心律： <a class="header-anchor" href="#其他可能的規律心律" aria-label="Permalink to &quot;其他可能的規律心律：&quot;">​</a></h4><ul><li>心房心搏過速（Atrial Tachycardia, AT）</li><li>竇性心律伴有傳導阻滯（Sinoatrial Block）</li></ul><h4 id="_2-無-p-波且節律不規律" tabindex="-1">2. 無 P 波且節律不規律 <a class="header-anchor" href="#_2-無-p-波且節律不規律" aria-label="Permalink to &quot;2. 無 P 波且節律不規律&quot;">​</a></h4><ul><li>心房顫動（Atrial Fibrillation, AF） <ul><li>Ｐ波完全消失，QRS 波間距不規則。</li><li>最常見的不規則心律。</li></ul></li><li>心房撲動（Atrial Flutter, AFL） <ul><li>典型「鋸齒狀 P 波」但有變異傳導（Variable Block），導致不規律心律。</li></ul></li></ul><h4 id="_3-竇性心律-期前收縮-premature-atrial-contractions" tabindex="-1">3. 竇性心律 + 期前收縮（Premature Atrial Contractions） <a class="header-anchor" href="#_3-竇性心律-期前收縮-premature-atrial-contractions" aria-label="Permalink to &quot;3. 竇性心律 + 期前收縮（Premature Atrial Contractions）&quot;">​</a></h4><ul><li>節律大致規則，但有過早心搏。</li></ul><h4 id="_4-多源性心房心動過速-multifocal-atrial-tachycardia" tabindex="-1">4. 多源性心房心動過速（Multifocal Atrial Tachycardia） <a class="header-anchor" href="#_4-多源性心房心動過速-multifocal-atrial-tachycardia" aria-label="Permalink to &quot;4. 多源性心房心動過速（Multifocal Atrial Tachycardia）&quot;">​</a></h4><ul><li>至少 3 種不同形態的 P 波。</li><li>常見於慢性阻塞性肺病（COPD）患者。</li><li>房室傳導阻滯（AV Block）</li></ul><h4 id="_5-mobitz-type-i-wenckebach" tabindex="-1">5. Mobitz Type I（Wenckebach）： <a class="header-anchor" href="#_5-mobitz-type-i-wenckebach" aria-label="Permalink to &quot;5. Mobitz Type I（Wenckebach）：&quot;">​</a></h4><ul><li>P 波逐漸延遲，最後 QRS 波脫落。</li></ul><h4 id="_6-mobitz-type-ii" tabindex="-1">6. Mobitz Type II： <a class="header-anchor" href="#_6-mobitz-type-ii" aria-label="Permalink to &quot;6. Mobitz Type II：&quot;">​</a></h4><ul><li>P 波規律，但部分 P 波未能傳導，導致 QRS 波缺失。</li></ul><h3 id="十、qrs-波判讀-wide-or-narrow" tabindex="-1">十、QRS 波判讀(Wide or narrow) <a class="header-anchor" href="#十、qrs-波判讀-wide-or-narrow" aria-label="Permalink to &quot;十、QRS 波判讀(Wide or narrow)&quot;">​</a></h3><h4 id="_1-確定-qrs-波群是否寬或窄" tabindex="-1">1. 確定 QRS 波群是否寬或窄 <a class="header-anchor" href="#_1-確定-qrs-波群是否寬或窄" aria-label="Permalink to &quot;1. 確定 QRS 波群是否寬或窄&quot;">​</a></h4><ul><li>窄 QRS（&lt;120 ms, ❤️ 小格）： <ul><li>表示傳導系統正常（His-Purkinje 系統完整）。</li><li>常見於 竇性心律、房性心律（Supraventricular Rhythms）。</li></ul></li><li>寬 QRS（≥120 ms, ≥3 小格）： <ul><li>表示 傳導異常或異常心律起源於心室。</li><li>可能為：束支傳導阻滯（Bundle Branch Block, BBB）</li><li>心室性心律（Ventricular Rhythms）</li></ul></li></ul><h4 id="_2-束支傳導阻滯" tabindex="-1">2. 束支傳導阻滯 <a class="header-anchor" href="#_2-束支傳導阻滯" aria-label="Permalink to &quot;2. 束支傳導阻滯&quot;">​</a></h4><ul><li>右束支傳導阻滯（RBBB） <ul><li>QRS 寬，V1 呈現兔耳波（RSR&#39;）。</li><li>V6 呈 寬而鈍的 S 波。</li></ul></li><li>左束支傳導阻滯（LBBB） <ul><li>QRS 寬，V1 呈 整體向下。</li><li>V6 呈 寬而鈍的 R 波。</li></ul></li></ul><h3 id="十一、心室性心律異常" tabindex="-1">十一、心室性心律異常 <a class="header-anchor" href="#十一、心室性心律異常" aria-label="Permalink to &quot;十一、心室性心律異常&quot;">​</a></h3><h4 id="_1-心室頻脈-ventricular-tachycardia-vt" tabindex="-1">1. 心室頻脈（Ventricular Tachycardia, VT） <a class="header-anchor" href="#_1-心室頻脈-ventricular-tachycardia-vt" aria-label="Permalink to &quot;1. 心室頻脈（Ventricular Tachycardia, VT）&quot;">​</a></h4><ul><li>特徵：</li><li>寬 QRS（&gt;120 ms）</li><li>規律心律</li><li>無明顯 P 波（或 P 波與 QRS 無關聯，稱為 AV 解離）</li><li>可能有融合波（Fusion Beat）或捕捉波（Capture Beat）</li></ul><h4 id="_2-心室顫動-ventricular-fibrillation-vf" tabindex="-1">2. 心室顫動（Ventricular Fibrillation, VF） <a class="header-anchor" href="#_2-心室顫動-ventricular-fibrillation-vf" aria-label="Permalink to &quot;2. 心室顫動（Ventricular Fibrillation, VF）&quot;">​</a></h4><ul><li>完全無規律 QRS 波，心電圖呈現快速、不規則波形。</li><li>沒有脈搏、無心輸出（Cardiac Output)，患者需要 CPR 和去顫。</li></ul>',82)]))}const q=l(t,[["render",h]]);export{b as __pageData,q as default};
