import{_ as e,c as i,o as r,ae as t}from"./chunks/framework.Dgg8-8ov.js";const b=JSON.parse('{"title":"心包疾病 Pericarditis disease","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-心包疾病 Pericarditis disease-16.md","filePath":"InternalMedicineDepartment-心包疾病 Pericarditis disease-16.md"}'),d={name:"InternalMedicineDepartment-心包疾病 Pericarditis disease-16.md"};function l(o,a,h,n,c,s){return r(),i("div",null,a[0]||(a[0]=[t('<h1 id="心包疾病-pericarditis-disease" tabindex="-1">心包疾病 Pericarditis disease <a class="header-anchor" href="#心包疾病-pericarditis-disease" aria-label="Permalink to &quot;心包疾病 Pericarditis disease&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="正常心包生理-normal-pericardial-physiology" tabindex="-1">正常心包生理 (Normal Pericardial Physiology) <a class="header-anchor" href="#正常心包生理-normal-pericardial-physiology" aria-label="Permalink to &quot;正常心包生理 (Normal Pericardial Physiology)&quot;">​</a></h2><h3 id="心包-pericardium-是包圍心臟的雙層膜-包含" tabindex="-1">心包（Pericardium）是包圍心臟的雙層膜，包含 <a class="header-anchor" href="#心包-pericardium-是包圍心臟的雙層膜-包含" aria-label="Permalink to &quot;心包（Pericardium）是包圍心臟的雙層膜，包含&quot;">​</a></h3><h4 id="漿膜層-serous-layer-內臟層-visceral-與壁層-parietal" tabindex="-1">漿膜層 (Serous layer)：內臟層 (Visceral) 與壁層 (Parietal) <a class="header-anchor" href="#漿膜層-serous-layer-內臟層-visceral-與壁層-parietal" aria-label="Permalink to &quot;漿膜層 (Serous layer)：內臟層 (Visceral) 與壁層 (Parietal)&quot;">​</a></h4><h4 id="纖維層-fibrous-layer-限制心臟擴張" tabindex="-1">纖維層 (Fibrous layer)：限制心臟擴張 <a class="header-anchor" href="#纖維層-fibrous-layer-限制心臟擴張" aria-label="Permalink to &quot;纖維層 (Fibrous layer)：限制心臟擴張&quot;">​</a></h4><h3 id="心包液-15-50-ml-功能為減少心臟運動摩擦" tabindex="-1">心包液：15-50 mL，功能為減少心臟運動摩擦 <a class="header-anchor" href="#心包液-15-50-ml-功能為減少心臟運動摩擦" aria-label="Permalink to &quot;心包液：15-50 mL，功能為減少心臟運動摩擦&quot;">​</a></h3><h3 id="功能" tabindex="-1">功能： <a class="header-anchor" href="#功能" aria-label="Permalink to &quot;功能：&quot;">​</a></h3><ul><li>固定心臟於縱膈腔（Mediastinum）</li><li>減少摩擦，避免心臟運動受限</li><li>防止感染與過度擴張</li></ul><h2 id="流行病學-epidemiology" tabindex="-1">流行病學（Epidemiology） <a class="header-anchor" href="#流行病學-epidemiology" aria-label="Permalink to &quot;流行病學（Epidemiology）&quot;">​</a></h2><h3 id="根據esc2015心包疾病臨床指南" tabindex="-1">根據ESC2015心包疾病臨床指南： <a class="header-anchor" href="#根據esc2015心包疾病臨床指南" aria-label="Permalink to &quot;根據ESC2015心包疾病臨床指南：&quot;">​</a></h3><h4 id="急性心包炎-acute-pericarditis" tabindex="-1">急性心包炎（Acute Pericarditis） <a class="header-anchor" href="#急性心包炎-acute-pericarditis" aria-label="Permalink to &quot;急性心包炎（Acute Pericarditis）&quot;">​</a></h4><ul><li>發生率： <ul><li>義大利：27.7/100,000 人/年</li><li>芬蘭：住院病例 3.32/100,000 人/年</li></ul></li><li>佔急診胸痛患者：5%</li><li>佔所有住院病例：0.1%</li><li>復發率：30%（18 個月內）</li><li>住院死亡率：1.1%</li><li>高風險族群：16–65 歲男性</li></ul><h2 id="主要臨床表現的分類" tabindex="-1">主要臨床表現的分類： <a class="header-anchor" href="#主要臨床表現的分類" aria-label="Permalink to &quot;主要臨床表現的分類：&quot;">​</a></h2><h3 id="心包炎-pericarditis" tabindex="-1">心包炎（Pericarditis） <a class="header-anchor" href="#心包炎-pericarditis" aria-label="Permalink to &quot;心包炎（Pericarditis）&quot;">​</a></h3><h3 id="心包填塞-cardiac-tamponade-、心包積液-pericardial-effusion" tabindex="-1">心包填塞（Cardiac Tamponade）、心包積液（Pericardial Effusion） <a class="header-anchor" href="#心包填塞-cardiac-tamponade-、心包積液-pericardial-effusion" aria-label="Permalink to &quot;心包填塞（Cardiac Tamponade）、心包積液（Pericardial Effusion）&quot;">​</a></h3><h3 id="限制型心包炎-constrictive-pericarditis" tabindex="-1">限制型心包炎（Constrictive Pericarditis） <a class="header-anchor" href="#限制型心包炎-constrictive-pericarditis" aria-label="Permalink to &quot;限制型心包炎（Constrictive Pericarditis）&quot;">​</a></h3><h2 id="疾病病生理" tabindex="-1">疾病病生理： <a class="header-anchor" href="#疾病病生理" aria-label="Permalink to &quot;疾病病生理：&quot;">​</a></h2><h3 id="急性心包炎-acute-pericarditis-心包膜發炎-產生摩擦" tabindex="-1">急性心包炎 (Acute Pericarditis) - 心包膜發炎，產生摩擦 <a class="header-anchor" href="#急性心包炎-acute-pericarditis-心包膜發炎-產生摩擦" aria-label="Permalink to &quot;急性心包炎 (Acute Pericarditis) - 心包膜發炎，產生摩擦&quot;">​</a></h3><h4 id="想像心臟被包裹在一個雙層的「保護袋」裡-這個袋子內部通常有少量潤滑液來減少摩擦。" tabindex="-1">想像心臟被包裹在一個雙層的「保護袋」裡，這個袋子內部通常有少量潤滑液來減少摩擦。 <a class="header-anchor" href="#想像心臟被包裹在一個雙層的「保護袋」裡-這個袋子內部通常有少量潤滑液來減少摩擦。" aria-label="Permalink to &quot;想像心臟被包裹在一個雙層的「保護袋」裡，這個袋子內部通常有少量潤滑液來減少摩擦。&quot;">​</a></h4><h4 id="當心包發炎時-這層「袋子」變得粗糙-導致心臟每次跳動時都會產生「摩擦聲」-pericardial-friction-rub-。" tabindex="-1">當心包發炎時，這層「袋子」變得粗糙，導致心臟每次跳動時都會產生「摩擦聲」(pericardial friction rub)。 <a class="header-anchor" href="#當心包發炎時-這層「袋子」變得粗糙-導致心臟每次跳動時都會產生「摩擦聲」-pericardial-friction-rub-。" aria-label="Permalink to &quot;當心包發炎時，這層「袋子」變得粗糙，導致心臟每次跳動時都會產生「摩擦聲」(pericardial friction rub)。&quot;">​</a></h4><h4 id="病人可能會感覺到胸痛-特別是在吸氣或躺下時更明顯-因為這些動作讓發炎的心包膜更容易互相摩擦。" tabindex="-1">病人可能會感覺到胸痛，特別是在吸氣或躺下時更明顯，因為這些動作讓發炎的心包膜更容易互相摩擦。 <a class="header-anchor" href="#病人可能會感覺到胸痛-特別是在吸氣或躺下時更明顯-因為這些動作讓發炎的心包膜更容易互相摩擦。" aria-label="Permalink to &quot;病人可能會感覺到胸痛，特別是在吸氣或躺下時更明顯，因為這些動作讓發炎的心包膜更容易互相摩擦。&quot;">​</a></h4><h3 id="心包積液-pericardial-effusion-心包腔積水-可能壓迫心臟" tabindex="-1">心包積液 (Pericardial Effusion) - 心包腔積水，可能壓迫心臟 <a class="header-anchor" href="#心包積液-pericardial-effusion-心包腔積水-可能壓迫心臟" aria-label="Permalink to &quot;心包積液 (Pericardial Effusion) - 心包腔積水，可能壓迫心臟&quot;">​</a></h3><h4 id="想像心臟被包在一個塑膠袋裡-正常情況下裡面只有一點點水分來潤滑。" tabindex="-1">想像心臟被包在一個塑膠袋裡，正常情況下裡面只有一點點水分來潤滑。 <a class="header-anchor" href="#想像心臟被包在一個塑膠袋裡-正常情況下裡面只有一點點水分來潤滑。" aria-label="Permalink to &quot;想像心臟被包在一個塑膠袋裡，正常情況下裡面只有一點點水分來潤滑。&quot;">​</a></h4><h4 id="但當發炎或其他病理因素影響時-袋子裡的水越積越多-開始壓迫心臟-影響它的正常擴張。" tabindex="-1">但當發炎或其他病理因素影響時，袋子裡的水越積越多，開始壓迫心臟，影響它的正常擴張。 <a class="header-anchor" href="#但當發炎或其他病理因素影響時-袋子裡的水越積越多-開始壓迫心臟-影響它的正常擴張。" aria-label="Permalink to &quot;但當發炎或其他病理因素影響時，袋子裡的水越積越多，開始壓迫心臟，影響它的正常擴張。&quot;">​</a></h4><h4 id="如果急性出血-少量液體即可引起心臟填塞-cardiac-tamponade-症狀-癌症相關心包積液可容納較大量液體-症狀較晚出現" tabindex="-1">如果急性出血，少量液體即可引起心臟填塞 (Cardiac Tamponade)症狀；癌症相關心包積液可容納較大量液體，症狀較晚出現 <a class="header-anchor" href="#如果急性出血-少量液體即可引起心臟填塞-cardiac-tamponade-症狀-癌症相關心包積液可容納較大量液體-症狀較晚出現" aria-label="Permalink to &quot;如果急性出血，少量液體即可引起心臟填塞 (Cardiac Tamponade)症狀；癌症相關心包積液可容納較大量液體，症狀較晚出現&quot;">​</a></h4><h4 id="這時心臟像是被「勒住」一樣-無法有效充盈血液-導致低血壓、呼吸困難-甚至休克。" tabindex="-1">這時心臟像是被「勒住」一樣，無法有效充盈血液，導致低血壓、呼吸困難，甚至休克。 <a class="header-anchor" href="#這時心臟像是被「勒住」一樣-無法有效充盈血液-導致低血壓、呼吸困難-甚至休克。" aria-label="Permalink to &quot;這時心臟像是被「勒住」一樣，無法有效充盈血液，導致低血壓、呼吸困難，甚至休克。&quot;">​</a></h4><h3 id="縮窄性心包炎-constrictive-pericarditis-心包變硬-限制心臟擴張" tabindex="-1">縮窄性心包炎 (Constrictive Pericarditis) - 心包變硬，限制心臟擴張 <a class="header-anchor" href="#縮窄性心包炎-constrictive-pericarditis-心包變硬-限制心臟擴張" aria-label="Permalink to &quot;縮窄性心包炎 (Constrictive Pericarditis) - 心包變硬，限制心臟擴張&quot;">​</a></h3><h4 id="如果心包發炎沒有完全痊癒-它可能會變得「硬化」-像是一層緊緊包住心臟的殼-甚至鈣化。" tabindex="-1">如果心包發炎沒有完全痊癒，它可能會變得「硬化」，像是一層緊緊包住心臟的殼，甚至鈣化。 <a class="header-anchor" href="#如果心包發炎沒有完全痊癒-它可能會變得「硬化」-像是一層緊緊包住心臟的殼-甚至鈣化。" aria-label="Permalink to &quot;如果心包發炎沒有完全痊癒，它可能會變得「硬化」，像是一層緊緊包住心臟的殼，甚至鈣化。&quot;">​</a></h4><h4 id="這就像是心臟被一層緊繃的「石膏」包住-使它在跳動時無法正常擴張-導致心臟無法充滿足夠的血液。" tabindex="-1">這就像是心臟被一層緊繃的「石膏」包住，使它在跳動時無法正常擴張，導致心臟無法充滿足夠的血液。 <a class="header-anchor" href="#這就像是心臟被一層緊繃的「石膏」包住-使它在跳動時無法正常擴張-導致心臟無法充滿足夠的血液。" aria-label="Permalink to &quot;這就像是心臟被一層緊繃的「石膏」包住，使它在跳動時無法正常擴張，導致心臟無法充滿足夠的血液。&quot;">​</a></h4><h4 id="病人會有下肢水腫、腹水、頸靜脈怒張等類似心衰竭的症狀-因為心臟的充盈受到限制-靜脈血無法有效回流。" tabindex="-1">病人會有下肢水腫、腹水、頸靜脈怒張等類似心衰竭的症狀，因為心臟的充盈受到限制，靜脈血無法有效回流。 <a class="header-anchor" href="#病人會有下肢水腫、腹水、頸靜脈怒張等類似心衰竭的症狀-因為心臟的充盈受到限制-靜脈血無法有效回流。" aria-label="Permalink to &quot;病人會有下肢水腫、腹水、頸靜脈怒張等類似心衰竭的症狀，因為心臟的充盈受到限制，靜脈血無法有效回流。&quot;">​</a></h4><h3 id="這三種心包疾病的核心問題都是影響心臟的正常活動-但病理機轉不同" tabindex="-1">這三種心包疾病的核心問題都是影響心臟的正常活動，但病理機轉不同： <a class="header-anchor" href="#這三種心包疾病的核心問題都是影響心臟的正常活動-但病理機轉不同" aria-label="Permalink to &quot;這三種心包疾病的核心問題都是影響心臟的正常活動，但病理機轉不同：&quot;">​</a></h3><h4 id="急性心包炎-摩擦、發炎-→-疼痛" tabindex="-1">急性心包炎：摩擦、發炎 → 疼痛 <a class="header-anchor" href="#急性心包炎-摩擦、發炎-→-疼痛" aria-label="Permalink to &quot;急性心包炎：摩擦、發炎 → 疼痛&quot;">​</a></h4><h4 id="心包積液-液體壓迫心臟-→-心輸出受影響" tabindex="-1">心包積液：液體壓迫心臟 → 心輸出受影響 <a class="header-anchor" href="#心包積液-液體壓迫心臟-→-心輸出受影響" aria-label="Permalink to &quot;心包積液：液體壓迫心臟 → 心輸出受影響&quot;">​</a></h4><h4 id="縮窄性心包炎-心包硬化-→-心室無法擴張" tabindex="-1">縮窄性心包炎：心包硬化 → 心室無法擴張 <a class="header-anchor" href="#縮窄性心包炎-心包硬化-→-心室無法擴張" aria-label="Permalink to &quot;縮窄性心包炎：心包硬化 → 心室無法擴張&quot;">​</a></h4><h2 id="急性心包炎-acute-pericarditis-最常見的心包疾病" tabindex="-1">急性心包炎 (Acute Pericarditis)：最常見的心包疾病 <a class="header-anchor" href="#急性心包炎-acute-pericarditis-最常見的心包疾病" aria-label="Permalink to &quot;急性心包炎 (Acute Pericarditis)：最常見的心包疾病&quot;">​</a></h2><h3 id="病因" tabindex="-1">病因 <a class="header-anchor" href="#病因" aria-label="Permalink to &quot;病因&quot;">​</a></h3><h3 id="臨床表現" tabindex="-1">臨床表現 <a class="header-anchor" href="#臨床表現" aria-label="Permalink to &quot;臨床表現&quot;">​</a></h3><h3 id="診斷標準-需滿足-≥2-4" tabindex="-1">診斷標準 (需滿足 ≥2/4) <a class="header-anchor" href="#診斷標準-需滿足-≥2-4" aria-label="Permalink to &quot;診斷標準 (需滿足 ≥2/4)&quot;">​</a></h3><h4 id="胸痛-pleuritic-chest-pain" tabindex="-1">胸痛 (Pleuritic chest pain) <a class="header-anchor" href="#胸痛-pleuritic-chest-pain" aria-label="Permalink to &quot;胸痛 (Pleuritic chest pain)&quot;">​</a></h4><ul><li>前胸部疼痛（sharp），可放射至肩膀、背部</li><li>吸氣或咳嗽時加劇（擴張的肺會牽動發炎的心包膜，導致疼痛加劇），平躺時加劇（心臟與發炎的心包接觸面增加，摩擦加劇），坐起前傾時減輕（減少心包膜的摩擦，疼痛減輕）</li></ul><h4 id="心包摩擦音-pericardial-friction-rub" tabindex="-1">心包摩擦音 (Pericardial friction rub) <a class="header-anchor" href="#心包摩擦音-pericardial-friction-rub" aria-label="Permalink to &quot;心包摩擦音 (Pericardial friction rub)&quot;">​</a></h4><ul><li>心臟週期內可能出現3相 (triphasic)</li><li>最清楚的位置：左胸骨下緣，病人前傾時</li></ul><h4 id="心電圖變化-ecg-changes" tabindex="-1">心電圖變化 (ECG changes) <a class="header-anchor" href="#心電圖變化-ecg-changes" aria-label="Permalink to &quot;心電圖變化 (ECG changes)&quot;">​</a></h4><ul><li>ST段廣泛抬高 + PR段下降</li><li>不同於STEMI：ST段凸向上、局部改變、PR 段正常</li></ul><h4 id="心包積液-pericardial-effusion" tabindex="-1">心包積液 (Pericardial effusion) <a class="header-anchor" href="#心包積液-pericardial-effusion" aria-label="Permalink to &quot;心包積液 (Pericardial effusion)&quot;">​</a></h4><ul><li>Ewart’s sign：左肩胛下濁音</li><li>CXR：心影增大呈 &quot;水壺狀 (Water bottle sign)&quot;</li></ul><h3 id="影像學評估" tabindex="-1">影像學評估 <a class="header-anchor" href="#影像學評估" aria-label="Permalink to &quot;影像學評估&quot;">​</a></h3><h4 id="超音波心動圖-echocardiography-首選-評估心包積液" tabindex="-1">超音波心動圖（Echocardiography）：首選，評估心包積液 <a class="header-anchor" href="#超音波心動圖-echocardiography-首選-評估心包積液" aria-label="Permalink to &quot;超音波心動圖（Echocardiography）：首選，評估心包積液&quot;">​</a></h4><h4 id="心臟磁振造影-cmr-檢測心包發炎-late-gadolinium-enhancement" tabindex="-1">心臟磁振造影（CMR）：檢測心包發炎（Late Gadolinium Enhancement） <a class="header-anchor" href="#心臟磁振造影-cmr-檢測心包發炎-late-gadolinium-enhancement" aria-label="Permalink to &quot;心臟磁振造影（CMR）：檢測心包發炎（Late Gadolinium Enhancement）&quot;">​</a></h4><h4 id="ct-檢測鈣化-慢性限制型心包炎" tabindex="-1">CT：檢測鈣化（慢性限制型心包炎） <a class="header-anchor" href="#ct-檢測鈣化-慢性限制型心包炎" aria-label="Permalink to &quot;CT：檢測鈣化（慢性限制型心包炎）&quot;">​</a></h4><h3 id="治療-treatment" tabindex="-1">治療 (Treatment) <a class="header-anchor" href="#治療-treatment" aria-label="Permalink to &quot;治療 (Treatment)&quot;">​</a></h3><h4 id="nsaids-colchicine-esc-2015-指南建議" tabindex="-1">NSAIDs + Colchicine (ESC 2015 指南建議) <a class="header-anchor" href="#nsaids-colchicine-esc-2015-指南建議" aria-label="Permalink to &quot;NSAIDs + Colchicine (ESC 2015 指南建議)&quot;">​</a></h4><ul><li>NSAIDs (持續 1-2 週) <ul><li>Aspirin 650-1000 mg q6-8h</li><li>Ibuprofen 600-800 mg TID</li><li>Indomethacin 25-50 mg TID</li></ul></li><li>Colchicine（0.5 mg BID，持續 3 個月） <ul><li>減少復發風險</li></ul></li></ul><h4 id="glucocorticoids-僅限於自體免疫、尿毒症、nsaids無效者" tabindex="-1">Glucocorticoids（僅限於自體免疫、尿毒症、NSAIDs無效者） <a class="header-anchor" href="#glucocorticoids-僅限於自體免疫、尿毒症、nsaids無效者" aria-label="Permalink to &quot;Glucocorticoids（僅限於自體免疫、尿毒症、NSAIDs無效者）&quot;">​</a></h4><ul><li>Prednisone 0.5-1 mg/kg/day，逐漸減量</li><li>避免濫用，因會增加復發機率</li></ul><h4 id="避免抗凝血劑-anticoagulants-避免出血性心包填塞" tabindex="-1">避免抗凝血劑 (Anticoagulants)（避免出血性心包填塞） <a class="header-anchor" href="#避免抗凝血劑-anticoagulants-避免出血性心包填塞" aria-label="Permalink to &quot;避免抗凝血劑 (Anticoagulants)（避免出血性心包填塞）&quot;">​</a></h4><h4 id="治療原發病因" tabindex="-1">治療原發病因 <a class="header-anchor" href="#治療原發病因" aria-label="Permalink to &quot;治療原發病因&quot;">​</a></h4><ul><li>例如抗結核治療、腫瘤治療</li></ul><h3 id="藥物整理" tabindex="-1">藥物整理： <a class="header-anchor" href="#藥物整理" aria-label="Permalink to &quot;藥物整理：&quot;">​</a></h3><h2 id="心包填塞-cardiac-tamponade" tabindex="-1">心包填塞 (Cardiac Tamponade) <a class="header-anchor" href="#心包填塞-cardiac-tamponade" aria-label="Permalink to &quot;心包填塞 (Cardiac Tamponade)&quot;">​</a></h2><h3 id="病因-1" tabindex="-1">病因 <a class="header-anchor" href="#病因-1" aria-label="Permalink to &quot;病因&quot;">​</a></h3><h4 id="急性或大量心包積液-→-限制心室舒張-→-降低心輸出量" tabindex="-1">急性或大量心包積液 → 限制心室舒張 → 降低心輸出量 <a class="header-anchor" href="#急性或大量心包積液-→-限制心室舒張-→-降低心輸出量" aria-label="Permalink to &quot;急性或大量心包積液 → 限制心室舒張 → 降低心輸出量&quot;">​</a></h4><h4 id="快速發生的積液-如主動脈剝離-可能少量積液-200-ml-即致命" tabindex="-1">快速發生的積液 (如主動脈剝離) 可能少量積液 (&lt;200 mL) 即致命 <a class="header-anchor" href="#快速發生的積液-如主動脈剝離-可能少量積液-200-ml-即致命" aria-label="Permalink to &quot;快速發生的積液 (如主動脈剝離) 可能少量積液 (&lt;200 mL) 即致命&quot;">​</a></h4><h4 id="慢性漸進性積液-如癌症-可能可達1-2-l而無症狀" tabindex="-1">慢性漸進性積液（如癌症）可能可達1-2 L而無症狀 <a class="header-anchor" href="#慢性漸進性積液-如癌症-可能可達1-2-l而無症狀" aria-label="Permalink to &quot;慢性漸進性積液（如癌症）可能可達1-2 L而無症狀&quot;">​</a></h4><h3 id="臨床表現-1" tabindex="-1">臨床表現 <a class="header-anchor" href="#臨床表現-1" aria-label="Permalink to &quot;臨床表現&quot;">​</a></h3><h3 id="診斷" tabindex="-1">診斷 <a class="header-anchor" href="#診斷" aria-label="Permalink to &quot;診斷&quot;">​</a></h3><h4 id="echocardiography" tabindex="-1">Echocardiography <a class="header-anchor" href="#echocardiography" aria-label="Permalink to &quot;Echocardiography&quot;">​</a></h4><ul><li>右心室舒張早期塌陷</li><li>右心房晚期塌陷</li><li>IVC 擴張，呼吸期變化 &lt;50%</li><li>評估心包膜積液</li></ul><h4 id="心導管測壓" tabindex="-1">心導管測壓 <a class="header-anchor" href="#心導管測壓" aria-label="Permalink to &quot;心導管測壓&quot;">​</a></h4><ul><li>右心室舒張壓 = 右心房壓</li><li>壓力等化 (Equalization of pressures)</li></ul><h4 id="x-ray" tabindex="-1">X-ray <a class="header-anchor" href="#x-ray" aria-label="Permalink to &quot;X-ray&quot;">​</a></h4><ul><li>&quot;水壺狀&quot; 心影，提示大量積液</li></ul><h3 id="治療" tabindex="-1">治療 <a class="header-anchor" href="#治療" aria-label="Permalink to &quot;治療&quot;">​</a></h3><h4 id="靜脈輸液-intravenous-fluids-iv-fluids" tabindex="-1">靜脈輸液 (Intravenous Fluids, IV Fluids) <a class="header-anchor" href="#靜脈輸液-intravenous-fluids-iv-fluids" aria-label="Permalink to &quot;靜脈輸液 (Intravenous Fluids, IV Fluids)&quot;">​</a></h4><ul><li>增加靜脈回流 (venous return)，提高心室前負荷 (preload)</li><li>避免給利尿劑，利尿劑會減少血容量，進一步降低心輸出量 (cardiac output)，加重低血壓與休克</li></ul><h4 id="緊急心包穿刺術-pericardiocentesis" tabindex="-1">緊急心包穿刺術 (Pericardiocentesis) <a class="header-anchor" href="#緊急心包穿刺術-pericardiocentesis" aria-label="Permalink to &quot;緊急心包穿刺術 (Pericardiocentesis)&quot;">​</a></h4><ul><li>即時減壓，改善血流動力學</li><li>透過超音波引導</li></ul><h4 id="外科心包開窗術-pericardial-window" tabindex="-1">外科心包開窗術 (Pericardial window) <a class="header-anchor" href="#外科心包開窗術-pericardial-window" aria-label="Permalink to &quot;外科心包開窗術 (Pericardial window)&quot;">​</a></h4><ul><li>適用於復發性積液或惡性病因</li></ul><h4 id="治療原發病因-1" tabindex="-1">治療原發病因 <a class="header-anchor" href="#治療原發病因-1" aria-label="Permalink to &quot;治療原發病因&quot;">​</a></h4><ul><li>腫瘤、結核、主動脈剝離等</li></ul><h2 id="縮窄性心包炎-constrictive-pericarditis" tabindex="-1">縮窄性心包炎 (Constrictive Pericarditis) <a class="header-anchor" href="#縮窄性心包炎-constrictive-pericarditis" aria-label="Permalink to &quot;縮窄性心包炎 (Constrictive Pericarditis)&quot;">​</a></h2><h3 id="病因-2" tabindex="-1">病因 <a class="header-anchor" href="#病因-2" aria-label="Permalink to &quot;病因&quot;">​</a></h3><h4 id="慢性心包發炎-→-心包鈣化、纖維化-失去彈性" tabindex="-1">慢性心包發炎 → 心包鈣化、纖維化，失去彈性 <a class="header-anchor" href="#慢性心包發炎-→-心包鈣化、纖維化-失去彈性" aria-label="Permalink to &quot;慢性心包發炎 → 心包鈣化、纖維化，失去彈性&quot;">​</a></h4><h4 id="常見病因" tabindex="-1">常見病因： <a class="header-anchor" href="#常見病因" aria-label="Permalink to &quot;常見病因：&quot;">​</a></h4><ul><li>結核、放射線治療、心臟手術、心包積液後遺症</li><li>需與 Restrictive Cardiomyopathy 鑑別</li></ul><h3 id="臨床表現-2" tabindex="-1">臨床表現 <a class="header-anchor" href="#臨床表現-2" aria-label="Permalink to &quot;臨床表現&quot;">​</a></h3><h3 id="診斷-1" tabindex="-1">診斷 <a class="header-anchor" href="#診斷-1" aria-label="Permalink to &quot;診斷&quot;">​</a></h3><h4 id="echocardiography-1" tabindex="-1">Echocardiography <a class="header-anchor" href="#echocardiography-1" aria-label="Permalink to &quot;Echocardiography&quot;">​</a></h4><ul><li>心室舒張受限，IVC 擴張</li><li>Septal bounce（心室中隔擺動）</li></ul><h4 id="ct-mri" tabindex="-1">CT / MRI <a class="header-anchor" href="#ct-mri" aria-label="Permalink to &quot;CT / MRI&quot;">​</a></h4><ul><li>心包增厚 &gt;4 mm（診斷縮窄的金標準）</li></ul><h4 id="心導管" tabindex="-1">心導管 <a class="header-anchor" href="#心導管" aria-label="Permalink to &quot;心導管&quot;">​</a></h4><ul><li>Dip-and-plateau sign (&quot;Square root sign&quot;)</li><li>右心與左心舒張壓相近</li></ul><h3 id="治療-1" tabindex="-1">治療 <a class="header-anchor" href="#治療-1" aria-label="Permalink to &quot;治療&quot;">​</a></h3><h4 id="利尿劑-diuretics-暫時性緩解症狀" tabindex="-1">利尿劑 (Diuretics)（暫時性緩解症狀） <a class="header-anchor" href="#利尿劑-diuretics-暫時性緩解症狀" aria-label="Permalink to &quot;利尿劑 (Diuretics)（暫時性緩解症狀）&quot;">​</a></h4><h4 id="心包剝離術-pericardiectomy" tabindex="-1">心包剝離術 (Pericardiectomy) <a class="header-anchor" href="#心包剝離術-pericardiectomy" aria-label="Permalink to &quot;心包剝離術 (Pericardiectomy)&quot;">​</a></h4><ul><li>唯一治癒方法</li><li>高併發症與死亡率 (6-12%)，需謹慎評估</li></ul><h2 id="心包疾病總覽" tabindex="-1">心包疾病總覽 <a class="header-anchor" href="#心包疾病總覽" aria-label="Permalink to &quot;心包疾病總覽&quot;">​</a></h2><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h3 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h3><h3 id="usmle-step-2-ck" tabindex="-1">USMLE step 2 CK <a class="header-anchor" href="#usmle-step-2-ck" aria-label="Permalink to &quot;USMLE step 2 CK&quot;">​</a></h3><h3 id="boards-beyonds-pericardial-disease" tabindex="-1">Boards &amp; beyonds：Pericardial disease <a class="header-anchor" href="#boards-beyonds-pericardial-disease" aria-label="Permalink to &quot;Boards &amp; beyonds：Pericardial disease&quot;">​</a></h3><h3 id="_2015-esc-guidelines-for-the-diagnosis-and-management-of-pericardial-diseases-https-academic-oup-com-eurheartj-article-36-42-2921-2293375" tabindex="-1">2015 ESC Guidelines for the diagnosis and management of pericardial diseases <a href="https://academic.oup.com/eurheartj/article/36/42/2921/2293375" target="_blank" rel="noreferrer">https://academic.oup.com/eurheartj/article/36/42/2921/2293375</a> <a class="header-anchor" href="#_2015-esc-guidelines-for-the-diagnosis-and-management-of-pericardial-diseases-https-academic-oup-com-eurheartj-article-36-42-2921-2293375" aria-label="Permalink to &quot;2015 ESC Guidelines for the diagnosis and management of pericardial diseases https://academic.oup.com/eurheartj/article/36/42/2921/2293375&quot;">​</a></h3><table tabindex="0"><thead><tr><th>類別</th><th>病因</th></tr></thead><tbody><tr><td>感染性</td><td>病毒（Coxsackie B、HIV）、結核、細菌性</td></tr><tr><td>心血管</td><td>心肌梗塞後 (Dressler’s syndrome)、主動脈剝離</td></tr><tr><td>代謝</td><td>尿毒症、黏液水腫</td></tr><tr><td>腫瘤</td><td>乳癌、肺癌、淋巴瘤</td></tr><tr><td>自體免疫</td><td>SLE、RA、藥物誘發（Procainamide、Hydralazine）</td></tr><tr><td>醫源性</td><td>心臟手術後、放射治療</td></tr></tbody></table><table tabindex="0"><thead><tr><th>症狀 / 體徵</th><th>描述</th></tr></thead><tbody><tr><td>胸痛 (Chest Pain)</td><td>典型鋒利刺痛 (sharp pain)，不同於心肌缺血的鈍痛</td></tr><tr><td>發燒 (Fever)</td><td>可能伴隨發炎反應</td></tr><tr><td>白血球增多 (Leukocytosis)</td><td>反映急性感染或發炎</td></tr><tr><td>紅血球沉降速率升高 (Elevated ESR)</td><td>提示發炎持續存在</td></tr><tr><td>心包積液 (Pericardial Effusion)</td><td>約 50% 病人會有心包積液，嚴重時可能導致心臟填塞 (Cardiac Tamponade)</td></tr></tbody></table><table tabindex="0"><thead><tr><th>治療</th><th>機制</th><th>適應症</th><th>主要副作用</th></tr></thead><tbody><tr><td>NSAIDs (Ibuprofen, Aspirin)</td><td>抑制 COX 減少發炎</td><td>第一線治療</td><td>胃潰瘍、腎功能損害</td></tr><tr><td>Colchicine</td><td>減少微管聚合，降低發炎</td><td>減少復發</td><td>腹瀉、肝功能異常</td></tr><tr><td>Corticosteroids</td><td>抑制免疫反應</td><td>反應不佳或自體免疫病患</td><td>免疫抑制、骨質疏鬆</td></tr></tbody></table><table tabindex="0"><thead><tr><th>症狀/體徵</th><th>特點</th></tr></thead><tbody><tr><td>Beck’s triad</td><td>低血壓 + 頸靜脈怒張 + 心音減弱</td></tr><tr><td>奇脈 (Pulsus paradoxus)</td><td>吸氣時收縮壓下降 &gt;10 mmHg （氣喘和COPD也會看到）</td></tr><tr><td>Kussmaul’s sign</td><td>吸氣時頸靜脈怒張（不常見）</td></tr><tr><td>Electrical alternans</td><td>EKG QRS交替變化，提示大量積液</td></tr></tbody></table><table tabindex="0"><thead><tr><th>症狀</th><th>特點</th></tr></thead><tbody><tr><td>右心衰竭表現</td><td>頸靜脈怒張、腹水、下肢水腫、肝衰竭（nutmeg liver）</td></tr><tr><td>Kussmaul’s sign</td><td>吸氣時頸靜脈怒張（與 Restrictive CM 不同）</td></tr><tr><td>心音</td><td>&quot;Knock sound&quot;（心包約束心室舒張的聲音）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>疾病</th><th>病因</th><th>病理生理</th><th>臨床表現</th><th>診斷</th><th>治療</th></tr></thead><tbody><tr><td>急性心包炎 (Acute Pericarditis)</td><td>病毒感染（柯薩奇病毒、回聲病毒）細菌感染（結核、化膿性心包炎）自身免疫疾病（SLE、RA）尿毒症心肌梗塞後（Dressler&#39;s syndrome）惡性腫瘤</td><td>心包膜發炎導致滲出，可能形成心包積液，刺激體表心包膜神經</td><td>胸痛（吸氣加劇，前傾可緩解）心包摩擦音發燒</td><td>心電圖（ST抬高、PR下降）超音波（心包積液）血液檢查（CRP、ESR上升）</td><td>NSAIDsColchicin類固醇</td></tr><tr><td>心包填塞 (Pericardial Tamponade)</td><td>心包積液大量累積（惡性腫瘤、胸部創傷、主動脈夾層破裂、尿毒症性心包炎）</td><td>心包腔壓力升高，影響心室充盈，導致心輸出量降低</td><td>Beck&#39;s triad（低血壓、頸靜脈怒張、心音低弱）脈壓變異（Pulsus Paradoxus）呼吸困難休克</td><td>超音波（右心房右心室塌陷）心導管（心包壓力升高）</td><td>緊急心包穿刺術（Pericardiocentesis）手術引流（Pericardial Window）</td></tr><tr><td>縮窄性心包炎 (Constrictive Pericarditis)</td><td>慢性心包炎後遺症（結核、病毒、心臟手術、放射治療）惡性腫瘤</td><td>心包纖維化、鈣化，導致心室舒張受限，回心血量減少</td><td>頸靜脈怒張Kussmaul sign腹水下肢水腫呼吸困難</td><td>心導管檢查（dip and plateau sign）超音波（心室舒張受限）CT/MRI（心包增厚、鈣化）</td><td>心包切除術（Pericardiectomy）利尿劑</td></tr></tbody></table>',116)]))}const m=e(d,[["render",l]]);export{b as __pageData,m as default};
