import{_ as e,c as r,o as i,ae as t}from"./chunks/framework.Cthp9TdA.js";const b=JSON.parse('{"title":"心律不整","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-心律不整-6.md","filePath":"InternalMedicineDepartment-心律不整-6.md"}'),o={name:"InternalMedicineDepartment-心律不整-6.md"};function l(h,a,n,s,d,c){return i(),r("div",null,a[0]||(a[0]=[t('<h1 id="心律不整" tabindex="-1">心律不整 <a class="header-anchor" href="#心律不整" aria-label="Permalink to &quot;心律不整&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="定義" tabindex="-1">定義 <a class="header-anchor" href="#定義" aria-label="Permalink to &quot;定義&quot;">​</a></h2><h3 id="心律不整是因為electric-impulse生成異常或傳導異常所導致。" tabindex="-1">心律不整是因為electric impulse生成異常或傳導異常所導致。 <a class="header-anchor" href="#心律不整是因為electric-impulse生成異常或傳導異常所導致。" aria-label="Permalink to &quot;心律不整是因為electric impulse生成異常或傳導異常所導致。&quot;">​</a></h3><h2 id="心律不整的分類與成因" tabindex="-1">心律不整的分類與成因 <a class="header-anchor" href="#心律不整的分類與成因" aria-label="Permalink to &quot;心律不整的分類與成因&quot;">​</a></h2><h3 id="bradyarrhythmias-心搏過緩" tabindex="-1">Bradyarrhythmias（心搏過緩） <a class="header-anchor" href="#bradyarrhythmias-心搏過緩" aria-label="Permalink to &quot;Bradyarrhythmias（心搏過緩）&quot;">​</a></h3><h4 id="bradyarrhythmias是指心跳速率超過每分鐘小於60次-通常因為sa-node訊號生成異常或是訊號傳導異常。" tabindex="-1">Bradyarrhythmias是指心跳速率超過每分鐘小於60次，通常因為SA node訊號生成異常或是訊號傳導異常。 <a class="header-anchor" href="#bradyarrhythmias是指心跳速率超過每分鐘小於60次-通常因為sa-node訊號生成異常或是訊號傳導異常。" aria-label="Permalink to &quot;Bradyarrhythmias是指心跳速率超過每分鐘小於60次，通常因為SA node訊號生成異常或是訊號傳導異常。&quot;">​</a></h4><h4 id="訊號傳導異常可以發生於以下部位" tabindex="-1">訊號傳導異常可以發生於以下部位： <a class="header-anchor" href="#訊號傳導異常可以發生於以下部位" aria-label="Permalink to &quot;訊號傳導異常可以發生於以下部位：&quot;">​</a></h4><ul><li>竇房結出口阻滯（exit block from the sinus node）</li><li>房室結傳導阻滯（AV node conduction block）</li><li>His-Purkinje system傳導受損</li></ul><h3 id="tachyarrhythmias-心搏過速" tabindex="-1">Tachyarrhythmias（心搏過速） <a class="header-anchor" href="#tachyarrhythmias-心搏過速" aria-label="Permalink to &quot;Tachyarrhythmias（心搏過速）&quot;">​</a></h3><h4 id="tachyarrhythmias-是指心跳速率超過每分鐘大於100-次的異常心律-可能來自心房、房室結或心室的異常興奮源或傳導異常。" tabindex="-1">Tachyarrhythmias 是指心跳速率超過每分鐘大於100 次的異常心律，可能來自心房、房室結或心室的異常興奮源或傳導異常。 <a class="header-anchor" href="#tachyarrhythmias-是指心跳速率超過每分鐘大於100-次的異常心律-可能來自心房、房室結或心室的異常興奮源或傳導異常。" aria-label="Permalink to &quot;Tachyarrhythmias 是指心跳速率超過每分鐘大於100 次的異常心律，可能來自心房、房室結或心室的異常興奮源或傳導異常。&quot;">​</a></h4><h4 id="根據發生的位置分成common-supraventricular和ventricular-tachyarrhythmias。" tabindex="-1">根據發生的位置分成common supraventricular和ventricular tachyarrhythmias。 <a class="header-anchor" href="#根據發生的位置分成common-supraventricular和ventricular-tachyarrhythmias。" aria-label="Permalink to &quot;根據發生的位置分成common supraventricular和ventricular tachyarrhythmias。&quot;">​</a></h4><h2 id="bradyarrhythmias-and-conduction-abnormalities" tabindex="-1">Bradyarrhythmias and conduction abnormalities <a class="header-anchor" href="#bradyarrhythmias-and-conduction-abnormalities" aria-label="Permalink to &quot;Bradyarrhythmias and conduction abnormalities&quot;">​</a></h2><h2 id="av-block-心房到心室傳導變慢或是被blocked" tabindex="-1">AV block：心房到心室傳導變慢或是被blocked <a class="header-anchor" href="#av-block-心房到心室傳導變慢或是被blocked" aria-label="Permalink to &quot;AV block：心房到心室傳導變慢或是被blocked&quot;">​</a></h2><h3 id="主要可以根據阻滯部位分為兩大類" tabindex="-1">主要可以根據阻滯部位分為兩大類： <a class="header-anchor" href="#主要可以根據阻滯部位分為兩大類" aria-label="Permalink to &quot;主要可以根據阻滯部位分為兩大類：&quot;">​</a></h3><h3 id="av-node-disease-房室結病變" tabindex="-1">AV Node Disease（房室結病變 <a class="header-anchor" href="#av-node-disease-房室結病變" aria-label="Permalink to &quot;AV Node Disease（房室結病變&quot;">​</a></h3><h4 id="阻滯位置-發生在av-node-房室結" tabindex="-1">阻滯位置：發生在AV node（房室結） <a class="header-anchor" href="#阻滯位置-發生在av-node-房室結" aria-label="Permalink to &quot;阻滯位置：發生在AV node（房室結）&quot;">​</a></h4><h4 id="常見特徵" tabindex="-1">常見特徵： <a class="header-anchor" href="#常見特徵" aria-label="Permalink to &quot;常見特徵：&quot;">​</a></h4><ul><li>通常是功能性或可逆性</li><li>受迷走神經影響較大（如睡眠、運動後心跳減慢）</li><li>QRS 波形正常（窄 QRS），因為 His-Purkinje 傳導正常</li></ul><h4 id="常見的-ecg-表現" tabindex="-1">常見的 ECG 表現： <a class="header-anchor" href="#常見的-ecg-表現" aria-label="Permalink to &quot;常見的 ECG 表現：&quot;">​</a></h4><ul><li>一度 AV Block（PR 間期延長 &gt;200ms，所有 P 波均傳導）</li><li>二度 AV Block Mobitz Type I（Wenckebach）（PR 間期逐漸延長，最終 P 波未傳導）</li><li>罕見進展為完全性AV block，多數為良性</li></ul><h4 id="預後與治療" tabindex="-1">預後與治療： <a class="header-anchor" href="#預後與治療" aria-label="Permalink to &quot;預後與治療：&quot;">​</a></h4><ul><li>通常為良性，可能無需治療，conduction在運動或交感神經興奮時改善</li><li>若為藥物引起，停藥即可恢復</li><li>若症狀嚴重或心搏過緩，考慮Atropine或臨時心律調節器（Temporary Pacemaker）</li></ul><h3 id="his-purkinje-disease-希氏束-浦金耶系統病變" tabindex="-1">His-Purkinje Disease（希氏束-浦金耶系統病變） <a class="header-anchor" href="#his-purkinje-disease-希氏束-浦金耶系統病變" aria-label="Permalink to &quot;His-Purkinje Disease（希氏束-浦金耶系統病變）&quot;">​</a></h3><h4 id="阻滯位置-發生在-his-bundle-希氏束-或其分支-左右束支" tabindex="-1">阻滯位置：發生在 His bundle（希氏束）或其分支（左右束支） <a class="header-anchor" href="#阻滯位置-發生在-his-bundle-希氏束-或其分支-左右束支" aria-label="Permalink to &quot;阻滯位置：發生在 His bundle（希氏束）或其分支（左右束支）&quot;">​</a></h4><h4 id="常見特徵-1" tabindex="-1">常見特徵： <a class="header-anchor" href="#常見特徵-1" aria-label="Permalink to &quot;常見特徵：&quot;">​</a></h4><ul><li>多為器質性病變，通常不可逆</li><li>不受迷走神經影響，症狀較嚴重</li><li>QRS 延長（寬 QRS），因為 His-Purkinje 傳導異常</li><li>更容易進展為完全性 AV block（Complete Heart Block）</li></ul><h4 id="常見的-ecg-表現-1" tabindex="-1">常見的 ECG 表現： <a class="header-anchor" href="#常見的-ecg-表現-1" aria-label="Permalink to &quot;常見的 ECG 表現：&quot;">​</a></h4><ul><li>二度 AV Block Mobitz Type II（PR 間期固定，部分 P 波未傳導）</li><li>三度 AV Block（Complete Heart Block）（心房與心室完全解離，心室逸搏心律 &lt;40 bpm）</li><li>常合併束支傳導阻滯（BBB），如右束支阻滯（RBBB）或左束支阻滯（LBBB）</li></ul><h4 id="預後與治療-1" tabindex="-1">預後與治療： <a class="header-anchor" href="#預後與治療-1" aria-label="Permalink to &quot;預後與治療：&quot;">​</a></h4><ul><li>預後較差，進展為完全性 AV block 的風險高</li><li>需要植入永久性心律調節器（Pacemaker）</li></ul><h2 id="tachyarrhythmias" tabindex="-1">Tachyarrhythmias <a class="header-anchor" href="#tachyarrhythmias" aria-label="Permalink to &quot;Tachyarrhythmias&quot;">​</a></h2><h3 id="supraventricular-tachyarrhythmias" tabindex="-1">Supraventricular Tachyarrhythmias <a class="header-anchor" href="#supraventricular-tachyarrhythmias" aria-label="Permalink to &quot;Supraventricular Tachyarrhythmias&quot;">​</a></h3><h3 id="ventricular-tachyarrhythmias" tabindex="-1">Ventricular Tachyarrhythmias <a class="header-anchor" href="#ventricular-tachyarrhythmias" aria-label="Permalink to &quot;Ventricular Tachyarrhythmias&quot;">​</a></h3><h2 id="心律不整的藥物" tabindex="-1">心律不整的藥物 <a class="header-anchor" href="#心律不整的藥物" aria-label="Permalink to &quot;心律不整的藥物&quot;">​</a></h2><h2 id="重點" tabindex="-1">重點： <a class="header-anchor" href="#重點" aria-label="Permalink to &quot;重點：&quot;">​</a></h2><h3 id="af" tabindex="-1">AF <a class="header-anchor" href="#af" aria-label="Permalink to &quot;AF&quot;">​</a></h3><h4 id="rate-control-β-blocker-ccb-verapamil-diltiazem-digoxin-合併-hfref" tabindex="-1">Rate control：β-blocker, CCB (Verapamil, Diltiazem), Digoxin (合併 HFrEF) <a class="header-anchor" href="#rate-control-β-blocker-ccb-verapamil-diltiazem-digoxin-合併-hfref" aria-label="Permalink to &quot;Rate control：β-blocker, CCB (Verapamil, Diltiazem), Digoxin (合併 HFrEF)&quot;">​</a></h4><h4 id="rhythm-control-class-ic-flecainide-但無結構性心臟病才用-、class-iii-amiodarone-sotalol" tabindex="-1">Rhythm control：Class IC（Flecainide，但無結構性心臟病才用）、Class III（Amiodarone, Sotalol） <a class="header-anchor" href="#rhythm-control-class-ic-flecainide-但無結構性心臟病才用-、class-iii-amiodarone-sotalol" aria-label="Permalink to &quot;Rhythm control：Class IC（Flecainide，但無結構性心臟病才用）、Class III（Amiodarone, Sotalol）&quot;">​</a></h4><h4 id="急性-af-轉律-ibutilide" tabindex="-1">急性 AF 轉律：Ibutilide <a class="header-anchor" href="#急性-af-轉律-ibutilide" aria-label="Permalink to &quot;急性 AF 轉律：Ibutilide&quot;">​</a></h4><h3 id="vt" tabindex="-1">VT <a class="header-anchor" href="#vt" aria-label="Permalink to &quot;VT&quot;">​</a></h3><h4 id="急性-lidocaine-mi-後首選-amiodarone-最強效" tabindex="-1">急性：Lidocaine（MI 後首選），Amiodarone（最強效） <a class="header-anchor" href="#急性-lidocaine-mi-後首選-amiodarone-最強效" aria-label="Permalink to &quot;急性：Lidocaine（MI 後首選），Amiodarone（最強效）&quot;">​</a></h4><h4 id="慢性-amiodarone-sotalol" tabindex="-1">慢性：Amiodarone, Sotalol <a class="header-anchor" href="#慢性-amiodarone-sotalol" aria-label="Permalink to &quot;慢性：Amiodarone, Sotalol&quot;">​</a></h4><h3 id="avnrt-av-結性心搏過速" tabindex="-1">AVNRT（AV 結性心搏過速） <a class="header-anchor" href="#avnrt-av-結性心搏過速" aria-label="Permalink to &quot;AVNRT（AV 結性心搏過速）&quot;">​</a></h3><h4 id="adenosine-首選" tabindex="-1">Adenosine（首選） <a class="header-anchor" href="#adenosine-首選" aria-label="Permalink to &quot;Adenosine（首選）&quot;">​</a></h4><h3 id="torsades-de-pointes" tabindex="-1">Torsades de Pointes <a class="header-anchor" href="#torsades-de-pointes" aria-label="Permalink to &quot;Torsades de Pointes&quot;">​</a></h3><h4 id="iv-mgso4-首選" tabindex="-1">IV MgSO₄（首選） <a class="header-anchor" href="#iv-mgso4-首選" aria-label="Permalink to &quot;IV MgSO₄（首選）&quot;">​</a></h4><h3 id="class-i-na-阻斷-主要用於-vt-ic-適用於-af" tabindex="-1">Class I（Na⁺ 阻斷）：主要用於 VT（IC 適用於 AF） <a class="header-anchor" href="#class-i-na-阻斷-主要用於-vt-ic-適用於-af" aria-label="Permalink to &quot;Class I（Na⁺ 阻斷）：主要用於 VT（IC 適用於 AF）&quot;">​</a></h3><h3 id="class-ii-iv-β-blocker-ccb-rate-control-af-avnrt" tabindex="-1">Class II &amp; IV（β-blocker &amp; CCB）：Rate control（AF, AVNRT） <a class="header-anchor" href="#class-ii-iv-β-blocker-ccb-rate-control-af-avnrt" aria-label="Permalink to &quot;Class II &amp; IV（β-blocker &amp; CCB）：Rate control（AF, AVNRT）&quot;">​</a></h3><h3 id="class-iii-k-阻斷-qt-延長風險高-但適用於-af-vt" tabindex="-1">Class III（K⁺ 阻斷）：QT 延長風險高，但適用於 AF &amp; VT <a class="header-anchor" href="#class-iii-k-阻斷-qt-延長風險高-但適用於-af-vt" aria-label="Permalink to &quot;Class III（K⁺ 阻斷）：QT 延長風險高，但適用於 AF &amp; VT&quot;">​</a></h3><h3 id="adenosine-適用於-avnrt-mgso4-適用於-torsades-de-pointes" tabindex="-1">Adenosine 適用於 AVNRT，MgSO₄ 適用於 Torsades de Pointes <a class="header-anchor" href="#adenosine-適用於-avnrt-mgso4-適用於-torsades-de-pointes" aria-label="Permalink to &quot;Adenosine 適用於 AVNRT，MgSO₄ 適用於 Torsades de Pointes&quot;">​</a></h3><h2 id="chads2-vasc-score" tabindex="-1">CHADS₂-VASc Score <a class="header-anchor" href="#chads2-vasc-score" aria-label="Permalink to &quot;CHADS₂-VASc Score&quot;">​</a></h2><h3 id="是用來評估-心房顫動-atrial-fibrillation-af-患者的中風風險-決定是否需要抗凝治療-anticoagulation-therapy-。" tabindex="-1">是用來評估 心房顫動（Atrial Fibrillation, AF）患者的中風風險，決定是否需要抗凝治療（Anticoagulation therapy）。 <a class="header-anchor" href="#是用來評估-心房顫動-atrial-fibrillation-af-患者的中風風險-決定是否需要抗凝治療-anticoagulation-therapy-。" aria-label="Permalink to &quot;是用來評估 心房顫動（Atrial Fibrillation, AF）患者的中風風險，決定是否需要抗凝治療（Anticoagulation therapy）。&quot;">​</a></h3><h2 id="最高分數-9-分-更精確區分低風險與高風險患者。" tabindex="-1">最高分數：9 分，更精確區分低風險與高風險患者。 <a class="header-anchor" href="#最高分數-9-分-更精確區分低風險與高風險患者。" aria-label="Permalink to &quot;最高分數：9 分，更精確區分低風險與高風險患者。&quot;">​</a></h2><h3 id="治療建議-根據分數" tabindex="-1">治療建議（根據分數） <a class="header-anchor" href="#治療建議-根據分數" aria-label="Permalink to &quot;治療建議（根據分數）&quot;">​</a></h3><h2 id="doac-首選-apixaban-rivaroxaban-dabigatran-edoxaban" tabindex="-1">DOAC（首選）：Apixaban, Rivaroxaban, Dabigatran, Edoxaban <a class="header-anchor" href="#doac-首選-apixaban-rivaroxaban-dabigatran-edoxaban" aria-label="Permalink to &quot;DOAC（首選）：Apixaban, Rivaroxaban, Dabigatran, Edoxaban&quot;">​</a></h2><p>Warfarin（如有機械瓣膜或特定適應症）：目標 INR 2.0-3.0</p><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h2 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h2><h2 id="boards-beyonds-arrhythimias" tabindex="-1">Boards &amp; Beyonds：Arrhythimias <a class="header-anchor" href="#boards-beyonds-arrhythimias" aria-label="Permalink to &quot;Boards &amp; Beyonds：Arrhythimias&quot;">​</a></h2><h2 id="first-aid-step-2-ck" tabindex="-1">First Aid Step 2 CK <a class="header-anchor" href="#first-aid-step-2-ck" aria-label="Permalink to &quot;First Aid Step 2 CK&quot;">​</a></h2>',66)]))}const m=e(o,[["render",l]]);export{b as __pageData,m as default};
