import{_ as t,c as e,o as r,ae as i}from"./chunks/framework.BtEkjhVd.js";const u=JSON.parse('{"title":"心律不整","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-心律不整-6.md","filePath":"InternalMedicineDepartment-心律不整-6.md"}'),d={name:"InternalMedicineDepartment-心律不整-6.md"};function o(l,a,n,h,s,c){return r(),e("div",null,a[0]||(a[0]=[i('<h1 id="心律不整" tabindex="-1">心律不整 <a class="header-anchor" href="#心律不整" aria-label="Permalink to &quot;心律不整&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="定義" tabindex="-1">定義 <a class="header-anchor" href="#定義" aria-label="Permalink to &quot;定義&quot;">​</a></h2><h3 id="心律不整是因為electric-impulse生成異常或傳導異常所導致。" tabindex="-1">心律不整是因為electric impulse生成異常或傳導異常所導致。 <a class="header-anchor" href="#心律不整是因為electric-impulse生成異常或傳導異常所導致。" aria-label="Permalink to &quot;心律不整是因為electric impulse生成異常或傳導異常所導致。&quot;">​</a></h3><h2 id="心律不整的分類與成因" tabindex="-1">心律不整的分類與成因 <a class="header-anchor" href="#心律不整的分類與成因" aria-label="Permalink to &quot;心律不整的分類與成因&quot;">​</a></h2><h3 id="bradyarrhythmias-心搏過緩" tabindex="-1">Bradyarrhythmias（心搏過緩） <a class="header-anchor" href="#bradyarrhythmias-心搏過緩" aria-label="Permalink to &quot;Bradyarrhythmias（心搏過緩）&quot;">​</a></h3><h4 id="bradyarrhythmias是指心跳速率超過每分鐘小於60次-通常因為sa-node訊號生成異常或是訊號傳導異常。" tabindex="-1">Bradyarrhythmias是指心跳速率超過每分鐘小於60次，通常因為SA node訊號生成異常或是訊號傳導異常。 <a class="header-anchor" href="#bradyarrhythmias是指心跳速率超過每分鐘小於60次-通常因為sa-node訊號生成異常或是訊號傳導異常。" aria-label="Permalink to &quot;Bradyarrhythmias是指心跳速率超過每分鐘小於60次，通常因為SA node訊號生成異常或是訊號傳導異常。&quot;">​</a></h4><h4 id="訊號傳導異常可以發生於以下部位" tabindex="-1">訊號傳導異常可以發生於以下部位： <a class="header-anchor" href="#訊號傳導異常可以發生於以下部位" aria-label="Permalink to &quot;訊號傳導異常可以發生於以下部位：&quot;">​</a></h4><ul><li>竇房結出口阻滯（exit block from the sinus node）</li><li>房室結傳導阻滯（AV node conduction block）</li><li>His-Purkinje system傳導受損</li></ul><h3 id="tachyarrhythmias-心搏過速" tabindex="-1">Tachyarrhythmias（心搏過速） <a class="header-anchor" href="#tachyarrhythmias-心搏過速" aria-label="Permalink to &quot;Tachyarrhythmias（心搏過速）&quot;">​</a></h3><h4 id="tachyarrhythmias-是指心跳速率超過每分鐘大於100-次的異常心律-可能來自心房、房室結或心室的異常興奮源或傳導異常。" tabindex="-1">Tachyarrhythmias 是指心跳速率超過每分鐘大於100 次的異常心律，可能來自心房、房室結或心室的異常興奮源或傳導異常。 <a class="header-anchor" href="#tachyarrhythmias-是指心跳速率超過每分鐘大於100-次的異常心律-可能來自心房、房室結或心室的異常興奮源或傳導異常。" aria-label="Permalink to &quot;Tachyarrhythmias 是指心跳速率超過每分鐘大於100 次的異常心律，可能來自心房、房室結或心室的異常興奮源或傳導異常。&quot;">​</a></h4><h4 id="根據發生的位置分成common-supraventricular和ventricular-tachyarrhythmias。" tabindex="-1">根據發生的位置分成common supraventricular和ventricular tachyarrhythmias。 <a class="header-anchor" href="#根據發生的位置分成common-supraventricular和ventricular-tachyarrhythmias。" aria-label="Permalink to &quot;根據發生的位置分成common supraventricular和ventricular tachyarrhythmias。&quot;">​</a></h4><h2 id="bradyarrhythmias-and-conduction-abnormalities" tabindex="-1">Bradyarrhythmias and conduction abnormalities <a class="header-anchor" href="#bradyarrhythmias-and-conduction-abnormalities" aria-label="Permalink to &quot;Bradyarrhythmias and conduction abnormalities&quot;">​</a></h2><h2 id="av-block-心房到心室傳導變慢或是被blocked" tabindex="-1">AV block：心房到心室傳導變慢或是被blocked <a class="header-anchor" href="#av-block-心房到心室傳導變慢或是被blocked" aria-label="Permalink to &quot;AV block：心房到心室傳導變慢或是被blocked&quot;">​</a></h2><h3 id="主要可以根據阻滯部位分為兩大類" tabindex="-1">主要可以根據阻滯部位分為兩大類： <a class="header-anchor" href="#主要可以根據阻滯部位分為兩大類" aria-label="Permalink to &quot;主要可以根據阻滯部位分為兩大類：&quot;">​</a></h3><h3 id="av-node-disease-房室結病變" tabindex="-1">AV Node Disease（房室結病變 <a class="header-anchor" href="#av-node-disease-房室結病變" aria-label="Permalink to &quot;AV Node Disease（房室結病變&quot;">​</a></h3><h4 id="阻滯位置-發生在av-node-房室結" tabindex="-1">阻滯位置：發生在AV node（房室結） <a class="header-anchor" href="#阻滯位置-發生在av-node-房室結" aria-label="Permalink to &quot;阻滯位置：發生在AV node（房室結）&quot;">​</a></h4><h4 id="常見特徵" tabindex="-1">常見特徵： <a class="header-anchor" href="#常見特徵" aria-label="Permalink to &quot;常見特徵：&quot;">​</a></h4><ul><li>通常是功能性或可逆性</li><li>受迷走神經影響較大（如睡眠、運動後心跳減慢）</li><li>QRS 波形正常（窄 QRS），因為 His-Purkinje 傳導正常</li></ul><h4 id="常見的-ecg-表現" tabindex="-1">常見的 ECG 表現： <a class="header-anchor" href="#常見的-ecg-表現" aria-label="Permalink to &quot;常見的 ECG 表現：&quot;">​</a></h4><ul><li>一度 AV Block（PR 間期延長 &gt;200ms，所有 P 波均傳導）</li><li>二度 AV Block Mobitz Type I（Wenckebach）（PR 間期逐漸延長，最終 P 波未傳導）</li><li>罕見進展為完全性AV block，多數為良性</li></ul><h4 id="預後與治療" tabindex="-1">預後與治療： <a class="header-anchor" href="#預後與治療" aria-label="Permalink to &quot;預後與治療：&quot;">​</a></h4><ul><li>通常為良性，可能無需治療，conduction在運動或交感神經興奮時改善</li><li>若為藥物引起，停藥即可恢復</li><li>若症狀嚴重或心搏過緩，考慮Atropine或臨時心律調節器（Temporary Pacemaker）</li></ul><h3 id="his-purkinje-disease-希氏束-浦金耶系統病變" tabindex="-1">His-Purkinje Disease（希氏束-浦金耶系統病變） <a class="header-anchor" href="#his-purkinje-disease-希氏束-浦金耶系統病變" aria-label="Permalink to &quot;His-Purkinje Disease（希氏束-浦金耶系統病變）&quot;">​</a></h3><h4 id="阻滯位置-發生在-his-bundle-希氏束-或其分支-左右束支" tabindex="-1">阻滯位置：發生在 His bundle（希氏束）或其分支（左右束支） <a class="header-anchor" href="#阻滯位置-發生在-his-bundle-希氏束-或其分支-左右束支" aria-label="Permalink to &quot;阻滯位置：發生在 His bundle（希氏束）或其分支（左右束支）&quot;">​</a></h4><h4 id="常見特徵-1" tabindex="-1">常見特徵： <a class="header-anchor" href="#常見特徵-1" aria-label="Permalink to &quot;常見特徵：&quot;">​</a></h4><ul><li>多為器質性病變，通常不可逆</li><li>不受迷走神經影響，症狀較嚴重</li><li>QRS 延長（寬 QRS），因為 His-Purkinje 傳導異常</li><li>更容易進展為完全性 AV block（Complete Heart Block）</li></ul><h4 id="常見的-ecg-表現-1" tabindex="-1">常見的 ECG 表現： <a class="header-anchor" href="#常見的-ecg-表現-1" aria-label="Permalink to &quot;常見的 ECG 表現：&quot;">​</a></h4><ul><li>二度 AV Block Mobitz Type II（PR 間期固定，部分 P 波未傳導）</li><li>三度 AV Block（Complete Heart Block）（心房與心室完全解離，心室逸搏心律 &lt;40 bpm）</li><li>常合併束支傳導阻滯（BBB），如右束支阻滯（RBBB）或左束支阻滯（LBBB）</li></ul><h4 id="預後與治療-1" tabindex="-1">預後與治療： <a class="header-anchor" href="#預後與治療-1" aria-label="Permalink to &quot;預後與治療：&quot;">​</a></h4><ul><li>預後較差，進展為完全性 AV block 的風險高</li><li>需要植入永久性心律調節器（Pacemaker）</li></ul><h2 id="tachyarrhythmias" tabindex="-1">Tachyarrhythmias <a class="header-anchor" href="#tachyarrhythmias" aria-label="Permalink to &quot;Tachyarrhythmias&quot;">​</a></h2><h3 id="supraventricular-tachyarrhythmias" tabindex="-1">Supraventricular Tachyarrhythmias <a class="header-anchor" href="#supraventricular-tachyarrhythmias" aria-label="Permalink to &quot;Supraventricular Tachyarrhythmias&quot;">​</a></h3><h3 id="ventricular-tachyarrhythmias" tabindex="-1">Ventricular Tachyarrhythmias <a class="header-anchor" href="#ventricular-tachyarrhythmias" aria-label="Permalink to &quot;Ventricular Tachyarrhythmias&quot;">​</a></h3><h2 id="心律不整的藥物" tabindex="-1">心律不整的藥物 <a class="header-anchor" href="#心律不整的藥物" aria-label="Permalink to &quot;心律不整的藥物&quot;">​</a></h2><h2 id="重點" tabindex="-1">重點： <a class="header-anchor" href="#重點" aria-label="Permalink to &quot;重點：&quot;">​</a></h2><h3 id="af" tabindex="-1">AF <a class="header-anchor" href="#af" aria-label="Permalink to &quot;AF&quot;">​</a></h3><h4 id="rate-control-β-blocker-ccb-verapamil-diltiazem-digoxin-合併-hfref" tabindex="-1">Rate control：β-blocker, CCB (Verapamil, Diltiazem), Digoxin (合併 HFrEF) <a class="header-anchor" href="#rate-control-β-blocker-ccb-verapamil-diltiazem-digoxin-合併-hfref" aria-label="Permalink to &quot;Rate control：β-blocker, CCB (Verapamil, Diltiazem), Digoxin (合併 HFrEF)&quot;">​</a></h4><h4 id="rhythm-control-class-ic-flecainide-但無結構性心臟病才用-、class-iii-amiodarone-sotalol" tabindex="-1">Rhythm control：Class IC（Flecainide，但無結構性心臟病才用）、Class III（Amiodarone, Sotalol） <a class="header-anchor" href="#rhythm-control-class-ic-flecainide-但無結構性心臟病才用-、class-iii-amiodarone-sotalol" aria-label="Permalink to &quot;Rhythm control：Class IC（Flecainide，但無結構性心臟病才用）、Class III（Amiodarone, Sotalol）&quot;">​</a></h4><h4 id="急性-af-轉律-ibutilide" tabindex="-1">急性 AF 轉律：Ibutilide <a class="header-anchor" href="#急性-af-轉律-ibutilide" aria-label="Permalink to &quot;急性 AF 轉律：Ibutilide&quot;">​</a></h4><h3 id="vt" tabindex="-1">VT <a class="header-anchor" href="#vt" aria-label="Permalink to &quot;VT&quot;">​</a></h3><h4 id="急性-lidocaine-mi-後首選-amiodarone-最強效" tabindex="-1">急性：Lidocaine（MI 後首選），Amiodarone（最強效） <a class="header-anchor" href="#急性-lidocaine-mi-後首選-amiodarone-最強效" aria-label="Permalink to &quot;急性：Lidocaine（MI 後首選），Amiodarone（最強效）&quot;">​</a></h4><h4 id="慢性-amiodarone-sotalol" tabindex="-1">慢性：Amiodarone, Sotalol <a class="header-anchor" href="#慢性-amiodarone-sotalol" aria-label="Permalink to &quot;慢性：Amiodarone, Sotalol&quot;">​</a></h4><h3 id="avnrt-av-結性心搏過速" tabindex="-1">AVNRT（AV 結性心搏過速） <a class="header-anchor" href="#avnrt-av-結性心搏過速" aria-label="Permalink to &quot;AVNRT（AV 結性心搏過速）&quot;">​</a></h3><h4 id="adenosine-首選" tabindex="-1">Adenosine（首選） <a class="header-anchor" href="#adenosine-首選" aria-label="Permalink to &quot;Adenosine（首選）&quot;">​</a></h4><h3 id="torsades-de-pointes" tabindex="-1">Torsades de Pointes <a class="header-anchor" href="#torsades-de-pointes" aria-label="Permalink to &quot;Torsades de Pointes&quot;">​</a></h3><h4 id="iv-mgso4-首選" tabindex="-1">IV MgSO₄（首選） <a class="header-anchor" href="#iv-mgso4-首選" aria-label="Permalink to &quot;IV MgSO₄（首選）&quot;">​</a></h4><h3 id="class-i-na-阻斷-主要用於-vt-ic-適用於-af" tabindex="-1">Class I（Na⁺ 阻斷）：主要用於 VT（IC 適用於 AF） <a class="header-anchor" href="#class-i-na-阻斷-主要用於-vt-ic-適用於-af" aria-label="Permalink to &quot;Class I（Na⁺ 阻斷）：主要用於 VT（IC 適用於 AF）&quot;">​</a></h3><h3 id="class-ii-iv-β-blocker-ccb-rate-control-af-avnrt" tabindex="-1">Class II &amp; IV（β-blocker &amp; CCB）：Rate control（AF, AVNRT） <a class="header-anchor" href="#class-ii-iv-β-blocker-ccb-rate-control-af-avnrt" aria-label="Permalink to &quot;Class II &amp; IV（β-blocker &amp; CCB）：Rate control（AF, AVNRT）&quot;">​</a></h3><h3 id="class-iii-k-阻斷-qt-延長風險高-但適用於-af-vt" tabindex="-1">Class III（K⁺ 阻斷）：QT 延長風險高，但適用於 AF &amp; VT <a class="header-anchor" href="#class-iii-k-阻斷-qt-延長風險高-但適用於-af-vt" aria-label="Permalink to &quot;Class III（K⁺ 阻斷）：QT 延長風險高，但適用於 AF &amp; VT&quot;">​</a></h3><h3 id="adenosine-適用於-avnrt-mgso4-適用於-torsades-de-pointes" tabindex="-1">Adenosine 適用於 AVNRT，MgSO₄ 適用於 Torsades de Pointes <a class="header-anchor" href="#adenosine-適用於-avnrt-mgso4-適用於-torsades-de-pointes" aria-label="Permalink to &quot;Adenosine 適用於 AVNRT，MgSO₄ 適用於 Torsades de Pointes&quot;">​</a></h3><h2 id="chads2-vasc-score" tabindex="-1">CHADS₂-VASc Score <a class="header-anchor" href="#chads2-vasc-score" aria-label="Permalink to &quot;CHADS₂-VASc Score&quot;">​</a></h2><h3 id="是用來評估-心房顫動-atrial-fibrillation-af-患者的中風風險-決定是否需要抗凝治療-anticoagulation-therapy-。" tabindex="-1">是用來評估 心房顫動（Atrial Fibrillation, AF）患者的中風風險，決定是否需要抗凝治療（Anticoagulation therapy）。 <a class="header-anchor" href="#是用來評估-心房顫動-atrial-fibrillation-af-患者的中風風險-決定是否需要抗凝治療-anticoagulation-therapy-。" aria-label="Permalink to &quot;是用來評估 心房顫動（Atrial Fibrillation, AF）患者的中風風險，決定是否需要抗凝治療（Anticoagulation therapy）。&quot;">​</a></h3><h2 id="最高分數-9-分-更精確區分低風險與高風險患者。" tabindex="-1">最高分數：9 分，更精確區分低風險與高風險患者。 <a class="header-anchor" href="#最高分數-9-分-更精確區分低風險與高風險患者。" aria-label="Permalink to &quot;最高分數：9 分，更精確區分低風險與高風險患者。&quot;">​</a></h2><h3 id="治療建議-根據分數" tabindex="-1">治療建議（根據分數） <a class="header-anchor" href="#治療建議-根據分數" aria-label="Permalink to &quot;治療建議（根據分數）&quot;">​</a></h3><h2 id="doac-首選-apixaban-rivaroxaban-dabigatran-edoxaban" tabindex="-1">DOAC（首選）：Apixaban, Rivaroxaban, Dabigatran, Edoxaban <a class="header-anchor" href="#doac-首選-apixaban-rivaroxaban-dabigatran-edoxaban" aria-label="Permalink to &quot;DOAC（首選）：Apixaban, Rivaroxaban, Dabigatran, Edoxaban&quot;">​</a></h2><p>Warfarin（如有機械瓣膜或特定適應症）：目標 INR 2.0-3.0</p><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h2 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h2><h2 id="boards-beyonds-arrhythimias" tabindex="-1">Boards &amp; Beyonds：Arrhythimias <a class="header-anchor" href="#boards-beyonds-arrhythimias" aria-label="Permalink to &quot;Boards &amp; Beyonds：Arrhythimias&quot;">​</a></h2><h2 id="first-aid-step-2-ck" tabindex="-1">First Aid Step 2 CK <a class="header-anchor" href="#first-aid-step-2-ck" aria-label="Permalink to &quot;First Aid Step 2 CK&quot;">​</a></h2><table tabindex="0"><thead><tr><th>Types</th><th>機轉</th><th>EKG sign</th><th>治療</th></tr></thead><tbody><tr><td>Sinus bradycardia</td><td>經常是意外發現運動員可見藥物：Beta-blockers, CCB</td><td>Sinus rhythm, rate&lt;60bpm</td><td>通常不需要治療嚴重者可植入心律調節器（Pacemaker）</td></tr><tr><td>Sick sinus syndrome/Tachycardia-bradycardia syndrome</td><td>SA node功能異常常是年紀老化造成</td><td>竇房結停頓（SA pause）沒有 P 波的產生，代表竇房結沒有activation。Junctional escape beat出現出現 QRS 波，前面沒有出現P波P 波重新出現表示竇房結功能恢復，重新產生正常的心房去極化。</td><td>常需要植入永久性心律調節器（Pacemaker）</td></tr><tr><td>First degree AV block</td><td>可能為正常變異，特別是在年輕人或運動員Beta-blockers，CCB</td><td>PR 間期延長 (&gt;200 ms, &gt;5 小格)，所有 P 波均傳導到心室。固定延長的 PR 間期所有 P 波均有對應 QRSQRS 形態正常</td><td>通常不需治療</td></tr><tr><td>Second degree AV blockSubtype 1: Mobitz type I/Wenkebach</td><td>阻滯發生於 AV node，常因迷走神經作用或藥物影響較為良性，通常不進展至 Complete AV Block可受運動、Atropine 改善</td><td>部分 P 波未傳導，導致部分心搏脫落（Dropped Beats）。PR 間期逐漸延長，最終 P 波無對應 QRS（Dropped Beat）RR 間期不規則QRS 形態通常正常（窄 QRS）</td><td>通常不需治療</td></tr><tr><td>Second degree AV block Subtype 2: Mobitz type II</td><td>發生於 His-Purkinje 系統（通常在 His bundle 或束支水平）通常與結構性心臟病（如心肌梗塞、纖維化）相關</td><td>PR 間期固定，但部分 P 波無 QRS 傳導（Dropped Beats）RR間期規則QRS 可能寬（因 His-Purkinje 傳導受損）比 Mobitz I 更危險，可能進展至 Complete AV Block</td><td>需要植入永久性 Pacemaker</td></tr><tr><td>Third degree AV block (Complete AV block)</td><td>心房與心室完全解離，無任何 P 波傳導至心室。AV node 或 His-Purkinje 完全阻滯，心房與心室彼此獨立跳動高度危險，可能導致心搏停止（Asystole）或 Adams-Stokes syncope（突發性暈厥）</td><td>P 波與 QRS 無關（心房規律，心室規律，但兩者不同步）QRS 形態取決於心室逸搏起源（Narrow → AV node；Wide → His-Purkinje）心室速率通常 &lt;40 bpm</td><td>需緊急治療（暫時性 Pacemaker）並植入永久性 Pacemaker</td></tr></tbody></table><table tabindex="0"><thead><tr><th>Type:</th><th>機轉</th><th>EKG</th><th>治療</th></tr></thead><tbody><tr><td>Sinus tachycardia</td><td>運動、壓力或疾病誘發</td><td>Sinus rhythm, rate&gt;100bpm</td><td>治underlying causes</td></tr><tr><td>Focal atrial tachycardia</td><td>來自單一異位心房焦點的心動過速，通常表現為規則性心律，心率通常介於 100–250 bpm 之間</td><td>規律的心房心動過速(Regular AT)，有清楚的 P 波</td><td>Vagal maneuver, Adenosine, β-blocker/CCB, Synchronized Cardioversion (不穩定時)</td></tr><tr><td>Atrial flutter</td><td>Typical: 右心房折返傳導（Right artery reentry），最常見的路徑為右心房內下腔靜脈（IVC）與三尖瓣環（Tricuspid annulus）之間的峽部（Cavotricuspid isthmus）Typical: 多見於左心房或右心房，與手術或導管消融後疤痕相關，或可為特發性（idiopathic）</td><td>規律的心律（Regular rhythm）&quot;鋸齒狀&quot; P 波（Sawtooth appearance）心房頻率 通常在 240–320 bpm心室頻率依賴AV node 傳導阻斷</td><td>抗心律不整藥物導管消融（Catheter ablation）可破壞Cavotricuspid isthmus的路徑</td></tr><tr><td>Atrial fibrillation</td><td>心房電活動極度混亂（chaotic atrial activity），心室率不規則（variable ventricular rate）最常見的持續性心律不整，常常和高齡、高血壓、心臟病、甲狀腺功能亢進、酒精攝取、感染（如 sepsis）、肺栓塞等相關主要併發症為血栓栓塞（如中風, stroke）</td><td>無 P 波（Absent P waves）心房以 350–600 bpm 無序顫動，無法產生明確P 波。心室節律完全不規則（Irregularly Irregular R-R intervals）AV node 不規則傳導心房衝動至心室，導致心室率變異性大。</td><td>Rate Control：Beta blockers，CCB，digoxin預防血栓栓塞（Stroke Prevention）：warfarin，NOAC治療原發病因</td></tr><tr><td>Multifocal atrial tachycardia (MAT)</td><td>多個心房起搏點或折返路徑（Multiple atrial pacemakers or reentrant pathways）常見於 慢性肺病（COPD）急性惡化時</td><td>多個不同 P 波形態（Multiple discrete P waves）至少 3 種不同形態的 P 波（Three or more unique P-wave morphologies）</td><td>Treat underlying condition和AF的治療方式一樣，可是要避免Beta-blockers（chronic lung disease 不可用）</td></tr><tr><td>Atrioventricular nodal reentry tachycardia(AVNRT)</td><td>AVNRT是最常見的陣發性心動過速(PSVT)Typical AVNRT: 主要造成短 RP tachycardia 的原因電傳導機轉：順向(antegrade)傳導經 Slow Pathway逆向(retrograde)傳導經 Fast PathwayAtypical AVNRT較少見電傳導機轉：順向(antegrade)傳導經 Fast Pathway逆向(retrograde)傳導經 Slow Pathway</td><td>P 波消失(因為心房激動與 QRS 同步)短 RP 間距(Short RP tachycardia)QRS 形態與正常竇性節律下相似P 波落在 RR 間距後半段(Retrograde P wave appears well after QRS)長 RP 間距（Long RP tachycardia）</td><td>Catheter ablation highly successful藥物治療：Beta blocker, CCB, digoxinVagal maeuver，失敗了再用adenosine</td></tr><tr><td>Atrioventricular reentrant tachycardia(AVRT)</td><td>AVRT 需要異常的旁路（Accessory Pathway），與 AV Node 共同形成折返環，因此是一種旁路介導的折返心律失常。與 Wolff-Parkinson-White（WPW）症候群最相關</td><td>短RP間距（Short RP tachycardia）QRS 窄（Narrow QRS, 除非合併傳導異常）逆行 P 波（Retrograde P waves） 通常可在 QRS 之後看到</td><td>Catheter ablation highly successful藥物治療：Beta blocker, CCB, digoxinVagal maeuver，失敗了再用adenosine</td></tr><tr><td>Wolff-Parkinson-White syndrome</td><td>WPW 症候群的核心問題是異常的旁路（Accessory Pathway）直接將心房衝動傳遞至心室（Bundle of Kent），繞過 AV node，造成心室預激(preexcitation)AVRT 和 WPW密切相關，但並不完全相同。WPW 是一種解剖異常，心房與心室之間有旁路(Accessory Pathway)，患者在竇性心律時 EKG 有 Delta 波（預激現象），但不一定會發作心律不整。AVRT 是一種心律不整，是因為旁路參與形成折返路徑。發作時會產生 折返性心搏過速（Reentrant Tachycardia），可發生在 有 WPW 的患者（但非所有 WPW 患者都會發生 AVRT）。</td><td>短 PR 間距（Shortened PR interval）Delta 波（Delta wave）寬 QRS 波（Widened QRS complex）</td><td>無症狀 WPW可觀察急性發作治療首選Procainamide 或 amiodarone禁用 AV node blocker（β-blockers, CCBs, Digoxin, Adenosine），這些藥物可能誘發 VF根治性治療，電燒消融（RFA），用於有症狀的 WPW</td></tr></tbody></table><table tabindex="0"><thead><tr><th>Type</th><th>機轉</th><th>EKG</th><th>治療</th></tr></thead><tbody><tr><td>Premature ventricular contraction(PVC)</td><td>心室異常焦點（ectopic focus）提前激發心室收縮可能來自 reentry 或異常自律性（automaticity）</td><td>寬 QRS（&gt;120ms），因心室異常激發無 P 波或 P 波未與 QRS 相關聯完全代償間歇（compensatory pause）</td><td>若無症狀，通常不需治療有症狀或高風險者若頻繁 PVC（&gt;10% daily beats）影響心功能，可考慮射頻消融（Catheter Ablation）藥物治療：beta blockers</td></tr><tr><td>Ventricular tachycardia(VT)</td><td>異常 reentry circuit 或異常自律性導致連續 ≥3 次 PVC 可分為 Monomorphic VT（單形性）與 Polymorphic VT（多形性）有機會進展到VF，導致死亡</td><td>連續 ≥3 次寬 QRS（&gt;120ms）Monomorphic VT：所有 QRS 形態一致Polymorphic VT：QRS 形態變化可能見到AV 解離(AV dissociation)可能見到 fusion beats 或 capture beats</td><td>不穩定（hypotension, shock, angina, HF）：立即同步電擊（Synchronized Cardioversion）穩定 VT：藥物治療（Amiodarone, Lidocaine, Procainamide）長期預防：ICD（植入式心律轉復除顫器）或 Catheter Ablation</td></tr><tr><td>Ventricular fibrillation(VF)</td><td>多重 reentry circuit 使心室無序顫動，心輸出接近 0致命性心律不整，最常見的心臟驟停（Cardiac Arrest）原因</td><td>無可辨識 P 波、QRS、T 波完全不規則顫動波（Fibrillatory waves）</td><td>立即 CPR + 無同步電擊（Defibrillation, 200–360J）若持續 VF：Epinephrine + Amiodarone/Lidocaine</td></tr><tr><td>Torsades de pointes</td><td>QT 延長（Long QT）導致 EAD（early afterdepolarization）可能因低血鉀、低血鎂、抗心律不整藥物（Class IA, III）</td><td>多形性 VT（QRS 波形改變）&quot;Twisting of the points&quot;（QRS 軸心不斷旋轉）QTc 延長 &gt;500ms</td><td>避免QT prolonged藥物Magnesium sulfate（首選）若發作頻繁，植入 ICD 預防猝死</td></tr></tbody></table><table tabindex="0"><thead><tr><th></th><th>代表藥物</th><th>機轉</th><th>臨床應用</th><th>副作用</th></tr></thead><tbody><tr><td>Class IA</td><td>Quinidine,Procainamide, Disopyramide</td><td>阻斷 Na⁺，並延長 APD &amp; QT（亦有 K⁺ 阻斷作用）</td><td>WPW（首選：Procainamide）、SVT、VT</td><td>QT prolonged（Torsades de Pointes）、Procainamide 可能導致 藥物誘發性 SLE</td></tr><tr><td>Class IB</td><td>Lidocaine, Mexiletine</td><td>阻斷 Na⁺，縮短 APD（影響 心室）</td><td>MI 後的急性 VT（首選）</td><td>神經毒性（眩暈、癲癇）、低血壓</td></tr><tr><td>Class IC</td><td>Flecainide, Propafenone</td><td>Na⁺ 阻斷，顯著減慢傳導，但對 APD 影響不大 Class I: 作用於心肌細胞的 Phase 0（去極化），減慢 Na⁺ 流入，降低 傳導速度。</td><td>SVT（AF, AVNRT），但禁用於結構性心臟病（MI、HFrEF）</td><td>心衰惡化、VT/VF</td></tr><tr><td>Class II</td><td>Metoprolol, Esmolol, Propranolol, Atenolol</td><td>抑制 β1 受體，減少 cAMP，降低 Ca²⁺ 流入，減慢 AV node 速度Class II: 作用於Phase 4（減少 If 電流），降低心率，減少 AV node 傳導。</td><td>Rate control（AF, Aflutter, AVNRT），心肌梗塞後降低猝死率</td><td>氣喘惡化（β2 阻斷）、低血壓、心動過緩</td></tr><tr><td>Class III</td><td>Amiodarone, Sotalol, Dofetilide, Ibutilide</td><td>K⁺ Channel Blockers Class III: 作用於Phase 3（延長再極化），延長 APD &amp; QT interval。</td><td>AF（維持心律）、VT/VF</td><td>Amiodarone 最強，但副作用多（肺纖維化、肝毒性、甲狀腺問題）QT prolonged</td></tr><tr><td>Class IV</td><td>Verapamil, Diltiazem</td><td>阻斷 L-type Ca²⁺ 通道，降低 AV node 傳導</td><td>Rate control（AF, Aflutter, AVNRT）</td><td>低血壓、心衰惡化（禁用於 HFrEF）</td></tr><tr><td></td><td>Adenosine</td><td>阻斷 AV node，K⁺ 外流增加</td><td>AVNRT 首選，可診斷 Wide-QRS Tachycardia</td><td>Flushing, 心搏停止感（數秒）</td></tr><tr><td></td><td>Digoxin</td><td>抑制 Na⁺/K⁺ ATPase，增加 迷走神經張力</td><td>AF rate control（合併 HFrEF 時</td><td>心律不整（如 AV block）</td></tr><tr><td></td><td>MgSO4</td><td>穩定細胞膜，減少 Ca²⁺ 流入</td><td>Torsades de Pointes（首選）</td><td>低血壓</td></tr></tbody></table><table tabindex="0"><thead><tr><th>危險因子</th><th>分數</th></tr></thead><tbody><tr><td>Congestive Heart Failure（充血性心衰 CHF）</td><td>1</td></tr><tr><td>Hypertension（高血壓）</td><td>1</td></tr><tr><td>Age ≥75 歲</td><td>2</td></tr><tr><td>Diabetes Mellitus（糖尿病）</td><td>1</td></tr><tr><td>Stroke/TIA（中風或短暫性腦缺血發作）</td><td>2</td></tr><tr><td>Vascular disease（血管疾病，如 MI、PAD、Aortic plaque）</td><td>1</td></tr><tr><td>Age 65-74 歲</td><td>1</td></tr><tr><td>Sex category（女性）</td><td>1</td></tr></tbody></table><table tabindex="0"><thead><tr><th>CHADS₂-VASc Score</th><th>中風風險</th><th>建議治療</th></tr></thead><tbody><tr><td>0（男性） / 1（女性）</td><td>低風險</td><td>不需要抗凝血劑（可考慮 Aspirin）</td></tr><tr><td>1（男性） / 2（女性）</td><td>中等風險</td><td>可考慮抗凝血劑</td></tr><tr><td>≥2（男性） / ≥3（女性）</td><td>高風險</td><td>建議抗凝血劑（DOAC &gt; Warfarin）</td></tr></tbody></table>',72)]))}const m=t(d,[["render",o]]);export{u as __pageData,m as default};
