import{_ as a,c as e,o as r,ae as d}from"./chunks/framework.Dgg8-8ov.js";const f=JSON.parse('{"title":"心衰竭","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-心衰竭-14.md","filePath":"InternalMedicineDepartment-心衰竭-14.md"}'),i={name:"InternalMedicineDepartment-心衰竭-14.md"};function o(l,t,h,n,c,s){return r(),e("div",null,t[0]||(t[0]=[d('<h1 id="心衰竭" tabindex="-1">心衰竭 <a class="header-anchor" href="#心衰竭" aria-label="Permalink to &quot;心衰竭&quot;">​</a></h1><h2 id="定義-definition" tabindex="-1">定義（Definition） <a class="header-anchor" href="#定義-definition" aria-label="Permalink to &quot;定義（Definition）&quot;">​</a></h2><h3 id="心臟衰竭-hf-是一種臨床症候群-特徵為心臟無法以適當的速度輸送足夠的血流-以滿足身體代謝需求-或僅能透過異常高的心室充盈壓來維持適當的心輸出量-cardiac-output-co-。依據左心室射出分數-left-ventricular-ejection-fraction-lvef-可分為" tabindex="-1">心臟衰竭（HF）是一種臨床症候群，特徵為心臟無法以適當的速度輸送足夠的血流，以滿足身體代謝需求，或僅能透過異常高的心室充盈壓來維持適當的心輸出量（Cardiac Output, CO）。依據左心室射出分數（Left Ventricular Ejection Fraction, LVEF），可分為： <a class="header-anchor" href="#心臟衰竭-hf-是一種臨床症候群-特徵為心臟無法以適當的速度輸送足夠的血流-以滿足身體代謝需求-或僅能透過異常高的心室充盈壓來維持適當的心輸出量-cardiac-output-co-。依據左心室射出分數-left-ventricular-ejection-fraction-lvef-可分為" aria-label="Permalink to &quot;心臟衰竭（HF）是一種臨床症候群，特徵為心臟無法以適當的速度輸送足夠的血流，以滿足身體代謝需求，或僅能透過異常高的心室充盈壓來維持適當的心輸出量（Cardiac Output, CO）。依據左心室射出分數（Left Ventricular Ejection Fraction, LVEF），可分為：&quot;">​</a></h3><h4 id="射出分數降低型心衰-hfref-hf-with-reduced-ejection-fraction" tabindex="-1">射出分數降低型心衰（HFrEF, HF with Reduced Ejection Fraction） <a class="header-anchor" href="#射出分數降低型心衰-hfref-hf-with-reduced-ejection-fraction" aria-label="Permalink to &quot;射出分數降低型心衰（HFrEF, HF with Reduced Ejection Fraction）&quot;">​</a></h4><ul><li>LVEF ≤ 40%</li><li>主要因心肌收縮功能受損，導致心臟無法有效泵血</li></ul><h4 id="射出分數保留型心衰-hfpef-hf-with-preserved-ejection-fraction" tabindex="-1">射出分數保留型心衰（HFpEF, HF with Preserved Ejection Fraction） <a class="header-anchor" href="#射出分數保留型心衰-hfpef-hf-with-preserved-ejection-fraction" aria-label="Permalink to &quot;射出分數保留型心衰（HFpEF, HF with Preserved Ejection Fraction）&quot;">​</a></h4><ul><li>LVEF ≥ 50%</li><li>主要因左心室順應性降低，導致舒張功能障礙</li></ul><h4 id="邊界型心衰-hfmref-hf-with-mildly-reduced-ejection-fraction" tabindex="-1">邊界型心衰（HFmrEF, HF with Mildly Reduced Ejection Fraction） <a class="header-anchor" href="#邊界型心衰-hfmref-hf-with-mildly-reduced-ejection-fraction" aria-label="Permalink to &quot;邊界型心衰（HFmrEF, HF with Mildly Reduced Ejection Fraction）&quot;">​</a></h4><ul><li>LVEF 41-49%，介於 HFrEF和HFpEF之間</li></ul><h2 id="流行病學-epidemiology" tabindex="-1">流行病學（Epidemiology） <a class="header-anchor" href="#流行病學-epidemiology" aria-label="Permalink to &quot;流行病學（Epidemiology）&quot;">​</a></h2><h3 id="全球概況" tabindex="-1">全球概況 <a class="header-anchor" href="#全球概況" aria-label="Permalink to &quot;全球概況&quot;">​</a></h3><h4 id="盛行率-全球約-1-3-65-歲以上可達-6-10-。" tabindex="-1">盛行率： 全球約 1-3%，65 歲以上可達 6-10%。 <a class="header-anchor" href="#盛行率-全球約-1-3-65-歲以上可達-6-10-。" aria-label="Permalink to &quot;盛行率： 全球約 1-3%，65 歲以上可達 6-10%。&quot;">​</a></h4><h4 id="死亡率-初次診斷後-1-年內死亡率約-30-40-5-年內死亡率-60-70-。" tabindex="-1">死亡率： 初次診斷後 1 年內死亡率約 30-40%，5 年內死亡率 60-70%。 <a class="header-anchor" href="#死亡率-初次診斷後-1-年內死亡率約-30-40-5-年內死亡率-60-70-。" aria-label="Permalink to &quot;死亡率： 初次診斷後 1 年內死亡率約 30-40%，5 年內死亡率 60-70%。&quot;">​</a></h4><h4 id="常見病因-冠狀動脈疾病-cad-為主要病因-其他病因包括高血壓、糖尿病、瓣膜性心臟病及心肌病變。" tabindex="-1">常見病因： 冠狀動脈疾病（CAD）為主要病因，其他病因包括高血壓、糖尿病、瓣膜性心臟病及心肌病變。 <a class="header-anchor" href="#常見病因-冠狀動脈疾病-cad-為主要病因-其他病因包括高血壓、糖尿病、瓣膜性心臟病及心肌病變。" aria-label="Permalink to &quot;常見病因： 冠狀動脈疾病（CAD）為主要病因，其他病因包括高血壓、糖尿病、瓣膜性心臟病及心肌病變。&quot;">​</a></h4><h3 id="台灣" tabindex="-1">台灣 <a class="header-anchor" href="#台灣" aria-label="Permalink to &quot;台灣&quot;">​</a></h3><h4 id="_2023-年《台灣心臟學會慢性心衰竭藥物治療共識》指出-心衰竭在台灣的盛行率約為-3-估計患者約70萬人。" tabindex="-1">2023 年《台灣心臟學會慢性心衰竭藥物治療共識》指出，心衰竭在台灣的盛行率約為 3%，估計患者約70萬人。 <a class="header-anchor" href="#_2023-年《台灣心臟學會慢性心衰竭藥物治療共識》指出-心衰竭在台灣的盛行率約為-3-估計患者約70萬人。" aria-label="Permalink to &quot;2023 年《台灣心臟學會慢性心衰竭藥物治療共識》指出，心衰竭在台灣的盛行率約為 3%，估計患者約70萬人。&quot;">​</a></h4><h4 id="主要病因-高血壓、冠狀動脈疾病、心律不整、糖尿病與瓣膜性心臟病。" tabindex="-1">主要病因： 高血壓、冠狀動脈疾病、心律不整、糖尿病與瓣膜性心臟病。 <a class="header-anchor" href="#主要病因-高血壓、冠狀動脈疾病、心律不整、糖尿病與瓣膜性心臟病。" aria-label="Permalink to &quot;主要病因： 高血壓、冠狀動脈疾病、心律不整、糖尿病與瓣膜性心臟病。&quot;">​</a></h4><h4 id="高危險族群-老年人、糖尿病患、高血壓患者。" tabindex="-1">高危險族群： 老年人、糖尿病患、高血壓患者。 <a class="header-anchor" href="#高危險族群-老年人、糖尿病患、高血壓患者。" aria-label="Permalink to &quot;高危險族群： 老年人、糖尿病患、高血壓患者。&quot;">​</a></h4><h2 id="病因-etiology" tabindex="-1">病因（Etiology） <a class="header-anchor" href="#病因-etiology" aria-label="Permalink to &quot;病因（Etiology）&quot;">​</a></h2><h3 id="心衰的主要病因可分為心臟相關與非心臟相關兩大類" tabindex="-1">心衰的主要病因可分為心臟相關與非心臟相關兩大類： <a class="header-anchor" href="#心衰的主要病因可分為心臟相關與非心臟相關兩大類" aria-label="Permalink to &quot;心衰的主要病因可分為心臟相關與非心臟相關兩大類：&quot;">​</a></h3><h2 id="心臟相關病因" tabindex="-1">心臟相關病因 <a class="header-anchor" href="#心臟相關病因" aria-label="Permalink to &quot;心臟相關病因&quot;">​</a></h2><h2 id="非心臟相關病因" tabindex="-1">非心臟相關病因 <a class="header-anchor" href="#非心臟相關病因" aria-label="Permalink to &quot;非心臟相關病因&quot;">​</a></h2><h2 id="病理生理-pathophysiology" tabindex="-1">病理生理（Pathophysiology） <a class="header-anchor" href="#病理生理-pathophysiology" aria-label="Permalink to &quot;病理生理（Pathophysiology）&quot;">​</a></h2><h3 id="心臟衰竭-hf-是一種進行性疾病-其發展涉及初始事件-index-event-導致心肌功能下降-進而引發一系列的代償機制-最後因為左心室重塑而使症狀顯著惡化。" tabindex="-1">心臟衰竭（HF）是一種進行性疾病，其發展涉及初始事件（Index Event）導致心肌功能下降，進而引發一系列的代償機制，最後因為左心室重塑而使症狀顯著惡化。 <a class="header-anchor" href="#心臟衰竭-hf-是一種進行性疾病-其發展涉及初始事件-index-event-導致心肌功能下降-進而引發一系列的代償機制-最後因為左心室重塑而使症狀顯著惡化。" aria-label="Permalink to &quot;心臟衰竭（HF）是一種進行性疾病，其發展涉及初始事件（Index Event）導致心肌功能下降，進而引發一系列的代償機制，最後因為左心室重塑而使症狀顯著惡化。&quot;">​</a></h3><h4 id="初始事件-index-event" tabindex="-1">初始事件（Index Event）： <a class="header-anchor" href="#初始事件-index-event" aria-label="Permalink to &quot;初始事件（Index Event）：&quot;">​</a></h4><ul><li>可能是突發性（如 心肌梗塞, Myocardial Infarction, MI），導致心肌細胞壞死。</li><li>可能是緩慢進展（如 慢性高血壓、瓣膜性心臟病），造成血流負荷增加。</li><li>可能是遺傳性（如 遺傳性心肌病變），影響心肌收縮功能。</li><li>無論何種機制，這些事件最終會導致心臟泵功能下降。</li></ul><h3 id="代償機制-compensatory-mechanisms" tabindex="-1">代償機制（Compensatory Mechanisms） <a class="header-anchor" href="#代償機制-compensatory-mechanisms" aria-label="Permalink to &quot;代償機制（Compensatory Mechanisms）&quot;">​</a></h3><h4 id="由於心輸出量-cardiac-output-co-下降-身體會啟動代償機制來維持血流灌注" tabindex="-1">由於心輸出量（Cardiac Output, CO）下降，身體會啟動代償機制來維持血流灌注： <a class="header-anchor" href="#由於心輸出量-cardiac-output-co-下降-身體會啟動代償機制來維持血流灌注" aria-label="Permalink to &quot;由於心輸出量（Cardiac Output, CO）下降，身體會啟動代償機制來維持血流灌注：&quot;">​</a></h4><ul><li>神經內分泌活化（Neurohormonal Activation） <ul><li>腎素-血管收縮素-醛固酮系統（RAAS）活化</li><li>交感神經系統（SNS）活化 <ul><li>心輸出量下降 → 交感神經活化 → 腎上腺素（Epinephrine）與正腎上腺素（Norepinephrine）釋放，增加心率（HR），提高心肌收縮力。但長期作用會導致 β-腎上腺素受體去敏感化（Desensitization），導致心臟反應下降。</li></ul></li></ul></li><li>心肌收縮力（Myocardial Contractility）增加</li><li>血管舒張系統（Vasodilatory System）活化 <ul><li>心房鈉尿肽（ANP）與腦鈉尿肽（BNP）：由心室釋放，促進利尿、鈉排泄、血管舒張。</li><li>前列腺素（Prostaglandins, PGE2 &amp; PGI2）：降低動脈阻力，減少心臟後負荷。</li><li>一氧化氮（NO）：降低血管阻力，減少心臟負擔。</li></ul></li><li>心室重塑（LV Remodeling） <ul><li>長期代償機制導致心室結構與功能異常變化，包括： <ul><li>心室擴張（Ventricular Dilation）</li><li>心肌細胞凋亡（Myocyte Apoptosis）</li><li>心肌纖維化（Fibrosis）</li></ul></li></ul></li></ul><h4 id="結果-心臟收縮與舒張功能進一步惡化-導致心衰竭症狀加重。" tabindex="-1">結果：心臟收縮與舒張功能進一步惡化，導致心衰竭症狀加重。 <a class="header-anchor" href="#結果-心臟收縮與舒張功能進一步惡化-導致心衰竭症狀加重。" aria-label="Permalink to &quot;結果：心臟收縮與舒張功能進一步惡化，導致心衰竭症狀加重。&quot;">​</a></h4><h2 id="臨床進展" tabindex="-1">臨床進展 <a class="header-anchor" href="#臨床進展" aria-label="Permalink to &quot;臨床進展&quot;">​</a></h2><h3 id="患者可能在心肌功能下降初期無症狀-直至代償機制無法維持後-才出現明顯心衰症狀-如呼吸困難、周邊水腫" tabindex="-1">患者可能在心肌功能下降初期無症狀，直至代償機制無法維持後，才出現明顯心衰症狀（如呼吸困難、周邊水腫） <a class="header-anchor" href="#患者可能在心肌功能下降初期無症狀-直至代償機制無法維持後-才出現明顯心衰症狀-如呼吸困難、周邊水腫" aria-label="Permalink to &quot;患者可能在心肌功能下降初期無症狀，直至代償機制無法維持後，才出現明顯心衰症狀（如呼吸困難、周邊水腫）&quot;">​</a></h3><h4 id="早期無症狀-asymptomatic-phase" tabindex="-1">早期無症狀（Asymptomatic Phase）： <a class="header-anchor" href="#早期無症狀-asymptomatic-phase" aria-label="Permalink to &quot;早期無症狀（Asymptomatic Phase）：&quot;">​</a></h4><ul><li>代償機制仍可維持心輸出量，患者活動能力不受影響。</li></ul><h4 id="nyha-分級-new-york-heart-association-classification" tabindex="-1">NYHA 分級（New York Heart Association Classification） <a class="header-anchor" href="#nyha-分級-new-york-heart-association-classification" aria-label="Permalink to &quot;NYHA 分級（New York Heart Association Classification）&quot;">​</a></h4><ul><li>Class I（無症狀）：無活動限制。</li><li>Class II（輕微症狀）：日常活動導致疲勞或氣促。</li><li>Class III（明顯症狀）：日常活動受限，輕度活動即產生症狀。</li><li>Class IV（嚴重症狀）：休息時即出現心衰竭症狀，無法進行任何體力活動。</li></ul><h2 id="hfref與hfpef的比較" tabindex="-1">HFrEF與HFpEF的比較 <a class="header-anchor" href="#hfref與hfpef的比較" aria-label="Permalink to &quot;HFrEF與HFpEF的比較&quot;">​</a></h2><h2 id="臨床表現-clinical-manifestations" tabindex="-1">臨床表現（Clinical Manifestations） <a class="header-anchor" href="#臨床表現-clinical-manifestations" aria-label="Permalink to &quot;臨床表現（Clinical Manifestations）&quot;">​</a></h2><h2 id="理學檢查-physical-examination" tabindex="-1">理學檢查（Physical Examination） <a class="header-anchor" href="#理學檢查-physical-examination" aria-label="Permalink to &quot;理學檢查（Physical Examination）&quot;">​</a></h2><h3 id="一般表現與生命徵象-general-appearance-vital-signs" tabindex="-1">一般表現與生命徵象（General Appearance &amp; Vital Signs） <a class="header-anchor" href="#一般表現與生命徵象-general-appearance-vital-signs" aria-label="Permalink to &quot;一般表現與生命徵象（General Appearance &amp; Vital Signs）&quot;">​</a></h3><h3 id="頸靜脈壓-jugular-venous-pressure-jvp" tabindex="-1">頸靜脈壓（Jugular Venous Pressure, JVP） <a class="header-anchor" href="#頸靜脈壓-jugular-venous-pressure-jvp" aria-label="Permalink to &quot;頸靜脈壓（Jugular Venous Pressure, JVP）&quot;">​</a></h3><h4 id="測量方式-病人仰臥-45°-測量-jvp-水柱高度-正常-≤8-cm-h2o" tabindex="-1">測量方式：病人仰臥 45°，測量 JVP 水柱高度（正常 ≤8 cm H2O） <a class="header-anchor" href="#測量方式-病人仰臥-45°-測量-jvp-水柱高度-正常-≤8-cm-h2o" aria-label="Permalink to &quot;測量方式：病人仰臥 45°，測量 JVP 水柱高度（正常 ≤8 cm H2O）&quot;">​</a></h4><h4 id="異常表現" tabindex="-1">異常表現 <a class="header-anchor" href="#異常表現" aria-label="Permalink to &quot;異常表現&quot;">​</a></h4><h3 id="肺部檢查-pulmonary-examination" tabindex="-1">肺部檢查（Pulmonary Examination） <a class="header-anchor" href="#肺部檢查-pulmonary-examination" aria-label="Permalink to &quot;肺部檢查（Pulmonary Examination）&quot;">​</a></h3><h3 id="心臟檢查-cardiac-examination" tabindex="-1">心臟檢查（Cardiac Examination） <a class="header-anchor" href="#心臟檢查-cardiac-examination" aria-label="Permalink to &quot;心臟檢查（Cardiac Examination）&quot;">​</a></h3><h3 id="腹部與四肢-abdomen-extremities" tabindex="-1">腹部與四肢（Abdomen &amp; Extremities） <a class="header-anchor" href="#腹部與四肢-abdomen-extremities" aria-label="Permalink to &quot;腹部與四肢（Abdomen &amp; Extremities）&quot;">​</a></h3><h2 id="診斷-diagnosis" tabindex="-1">診斷（Diagnosis） <a class="header-anchor" href="#診斷-diagnosis" aria-label="Permalink to &quot;診斷（Diagnosis）&quot;">​</a></h2><h3 id="實驗室檢查-laboratory-tests" tabindex="-1">實驗室檢查（Laboratory Tests） <a class="header-anchor" href="#實驗室檢查-laboratory-tests" aria-label="Permalink to &quot;實驗室檢查（Laboratory Tests）&quot;">​</a></h3><h2 id="檢查" tabindex="-1">檢查： <a class="header-anchor" href="#檢查" aria-label="Permalink to &quot;檢查：&quot;">​</a></h2><h3 id="ef-ejection-fraction" tabindex="-1">EF（Ejection Fraction） <a class="header-anchor" href="#ef-ejection-fraction" aria-label="Permalink to &quot;EF（Ejection Fraction）&quot;">​</a></h3><h4 id="ef-≥-50-舒張性-hf" tabindex="-1">EF ≥ 50%：舒張性 HF <a class="header-anchor" href="#ef-≥-50-舒張性-hf" aria-label="Permalink to &quot;EF ≥ 50%：舒張性 HF&quot;">​</a></h4><h4 id="ef-40-收縮性-hf" tabindex="-1">EF &lt; 40%：收縮性 HF <a class="header-anchor" href="#ef-40-收縮性-hf" aria-label="Permalink to &quot;EF &lt; 40%：收縮性 HF&quot;">​</a></h4><h3 id="生物標誌物-biomarkers" tabindex="-1">生物標誌物（Biomarkers）： <a class="header-anchor" href="#生物標誌物-biomarkers" aria-label="Permalink to &quot;生物標誌物（Biomarkers）：&quot;">​</a></h3><h2 id="心衰竭治療" tabindex="-1">心衰竭治療： <a class="header-anchor" href="#心衰竭治療" aria-label="Permalink to &quot;心衰竭治療：&quot;">​</a></h2><h3 id="心衰竭的治療根據病程可分為急性心衰竭-acute-heart-failure-ahf-與慢性心衰竭-chronic-heart-failure-chf-後者再細分為射出分數保留型心衰竭-hfpef-與射出分數降低型心衰竭-hfref-。" tabindex="-1">心衰竭的治療根據病程可分為急性心衰竭（Acute Heart Failure, AHF）與慢性心衰竭（Chronic Heart Failure, CHF），後者再細分為射出分數保留型心衰竭（HFpEF）與射出分數降低型心衰竭（HFrEF）。 <a class="header-anchor" href="#心衰竭的治療根據病程可分為急性心衰竭-acute-heart-failure-ahf-與慢性心衰竭-chronic-heart-failure-chf-後者再細分為射出分數保留型心衰竭-hfpef-與射出分數降低型心衰竭-hfref-。" aria-label="Permalink to &quot;心衰竭的治療根據病程可分為急性心衰竭（Acute Heart Failure, AHF）與慢性心衰竭（Chronic Heart Failure, CHF），後者再細分為射出分數保留型心衰竭（HFpEF）與射出分數降低型心衰竭（HFrEF）。&quot;">​</a></h3><h2 id="急性心衰竭-acute-heart-failure-ahf" tabindex="-1">急性心衰竭（Acute Heart Failure, AHF） <a class="header-anchor" href="#急性心衰竭-acute-heart-failure-ahf" aria-label="Permalink to &quot;急性心衰竭（Acute Heart Failure, AHF）&quot;">​</a></h2><h3 id="治療目標" tabindex="-1">治療目標 <a class="header-anchor" href="#治療目標" aria-label="Permalink to &quot;治療目標&quot;">​</a></h3><h4 id="減少左心房壓力-left-atrial-pressure-lap" tabindex="-1">減少左心房壓力（Left Atrial Pressure, LAP） <a class="header-anchor" href="#減少左心房壓力-left-atrial-pressure-lap" aria-label="Permalink to &quot;減少左心房壓力（Left Atrial Pressure, LAP）&quot;">​</a></h4><h4 id="緩解肺鬱血-pulmonary-congestion" tabindex="-1">緩解肺鬱血（Pulmonary Congestion） <a class="header-anchor" href="#緩解肺鬱血-pulmonary-congestion" aria-label="Permalink to &quot;緩解肺鬱血（Pulmonary Congestion）&quot;">​</a></h4><h4 id="增加心輸出量-cardiac-output-co" tabindex="-1">增加心輸出量（Cardiac Output, CO） <a class="header-anchor" href="#增加心輸出量-cardiac-output-co" aria-label="Permalink to &quot;增加心輸出量（Cardiac Output, CO）&quot;">​</a></h4><h2 id="主要治療藥物" tabindex="-1">主要治療藥物 <a class="header-anchor" href="#主要治療藥物" aria-label="Permalink to &quot;主要治療藥物&quot;">​</a></h2><h2 id="慢性心衰竭-chronic-heart-failure-chf" tabindex="-1">慢性心衰竭（Chronic Heart Failure, CHF） <a class="header-anchor" href="#慢性心衰竭-chronic-heart-failure-chf" aria-label="Permalink to &quot;慢性心衰竭（Chronic Heart Failure, CHF）&quot;">​</a></h2><h3 id="根據射出分數-ejection-fraction-ef-分類" tabindex="-1">根據射出分數（Ejection Fraction, EF）分類： <a class="header-anchor" href="#根據射出分數-ejection-fraction-ef-分類" aria-label="Permalink to &quot;根據射出分數（Ejection Fraction, EF）分類：&quot;">​</a></h3><h4 id="hfpef-ef-≥-50" tabindex="-1">HFpEF（EF ≥ 50%） <a class="header-anchor" href="#hfpef-ef-≥-50" aria-label="Permalink to &quot;HFpEF（EF ≥ 50%）&quot;">​</a></h4><ul><li>無特定療法</li><li>目標為控制血壓與血糖，減少心衰加重因子</li><li>利尿劑（Furosemide）用於控制體液滯留</li></ul><h4 id="hfref-ef-≤-40" tabindex="-1">HFrEF（EF ≤ 40%） <a class="header-anchor" href="#hfref-ef-≤-40" aria-label="Permalink to &quot;HFrEF（EF ≤ 40%）&quot;">​</a></h4><ul><li>目標為減少住院率與降低死亡率</li><li>主要藥物針對減少交感神經與 RAAS 活化，防止心臟重塑</li></ul><h2 id="hfref-主要治療藥物" tabindex="-1">HFrEF 主要治療藥物 <a class="header-anchor" href="#hfref-主要治療藥物" aria-label="Permalink to &quot;HFrEF 主要治療藥物&quot;">​</a></h2><h2 id="在慢性心衰竭-chf-患者中-特定高風險族群可能需要植入式裝置來降低猝死風險或改善心臟功能。主要包括" tabindex="-1">在慢性心衰竭（CHF）患者中，特定高風險族群可能需要植入式裝置來降低猝死風險或改善心臟功能。主要包括： <a class="header-anchor" href="#在慢性心衰竭-chf-患者中-特定高風險族群可能需要植入式裝置來降低猝死風險或改善心臟功能。主要包括" aria-label="Permalink to &quot;在慢性心衰竭（CHF）患者中，特定高風險族群可能需要植入式裝置來降低猝死風險或改善心臟功能。主要包括：&quot;">​</a></h2><h3 id="植入式心律去顫器-implantable-cardioverter-defibrillator-icd" tabindex="-1">植入式心律去顫器（Implantable Cardioverter Defibrillator, ICD） <a class="header-anchor" href="#植入式心律去顫器-implantable-cardioverter-defibrillator-icd" aria-label="Permalink to &quot;植入式心律去顫器（Implantable Cardioverter Defibrillator, ICD）&quot;">​</a></h3><h3 id="雙心室起搏器-biventricular-pacemaker-即心臟再同步治療-cardiac-resynchronization-therapy-crt" tabindex="-1">雙心室起搏器（Biventricular Pacemaker），即心臟再同步治療（Cardiac Resynchronization Therapy, CRT） <a class="header-anchor" href="#雙心室起搏器-biventricular-pacemaker-即心臟再同步治療-cardiac-resynchronization-therapy-crt" aria-label="Permalink to &quot;雙心室起搏器（Biventricular Pacemaker），即心臟再同步治療（Cardiac Resynchronization Therapy, CRT）&quot;">​</a></h3><h2 id="crt-vs-icd" tabindex="-1">CRT vs ICD <a class="header-anchor" href="#crt-vs-icd" aria-label="Permalink to &quot;CRT vs ICD&quot;">​</a></h2><h2 id="晚期心衰竭-advanced-heart-failure" tabindex="-1">晚期心衰竭（Advanced Heart Failure） <a class="header-anchor" href="#晚期心衰竭-advanced-heart-failure" aria-label="Permalink to &quot;晚期心衰竭（Advanced Heart Failure）&quot;">​</a></h2><h3 id="病徵" tabindex="-1">病徵： <a class="header-anchor" href="#病徵" aria-label="Permalink to &quot;病徵：&quot;">​</a></h3><h4 id="極度低心輸出-severely-reduced-co" tabindex="-1">極度低心輸出（Severely Reduced CO） <a class="header-anchor" href="#極度低心輸出-severely-reduced-co" aria-label="Permalink to &quot;極度低心輸出（Severely Reduced CO）&quot;">​</a></h4><h4 id="症狀-疲倦、低血壓、四肢冰冷、心因性惡病質-cardiac-cachexia" tabindex="-1">症狀：疲倦、低血壓、四肢冰冷、心因性惡病質（Cardiac Cachexia） <a class="header-anchor" href="#症狀-疲倦、低血壓、四肢冰冷、心因性惡病質-cardiac-cachexia" aria-label="Permalink to &quot;症狀：疲倦、低血壓、四肢冰冷、心因性惡病質（Cardiac Cachexia）&quot;">​</a></h4><h2 id="治療選項" tabindex="-1">治療選項 <a class="header-anchor" href="#治療選項" aria-label="Permalink to &quot;治療選項&quot;">​</a></h2><h2 id="避免使用的藥物-drugs-to-avoid-in-hf" tabindex="-1">避免使用的藥物（Drugs to Avoid in HF） <a class="header-anchor" href="#避免使用的藥物-drugs-to-avoid-in-hf" aria-label="Permalink to &quot;避免使用的藥物（Drugs to Avoid in HF）&quot;">​</a></h2><h2 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h2><h2 id="boards-beyonds-heart-failure" tabindex="-1">Boards &amp; Beyonds：Heart failure <a class="header-anchor" href="#boards-beyonds-heart-failure" aria-label="Permalink to &quot;Boards &amp; Beyonds：Heart failure&quot;">​</a></h2><h2 id="_2023-focused-update-of-the-2021-esc-guidelines-for-the-diagnosis-and-treatment-of-acute-and-chronic-heart-failure" tabindex="-1">2023 Focused Update of the 2021 ESC Guidelines for the diagnosis and treatment of acute and chronic heart failure <a class="header-anchor" href="#_2023-focused-update-of-the-2021-esc-guidelines-for-the-diagnosis-and-treatment-of-acute-and-chronic-heart-failure" aria-label="Permalink to &quot;2023 Focused Update of the 2021 ESC Guidelines for the diagnosis and treatment of acute and chronic heart failure&quot;">​</a></h2><h2 id="_2023-consensus-of-taiwan-society-of-cardiology-on-the-pharmacological-treatment-of-chronic-heart-failure" tabindex="-1">2023 Consensus of Taiwan Society of Cardiology on the Pharmacological Treatment of Chronic Heart Failure <a class="header-anchor" href="#_2023-consensus-of-taiwan-society-of-cardiology-on-the-pharmacological-treatment-of-chronic-heart-failure" aria-label="Permalink to &quot;2023 Consensus of Taiwan Society of Cardiology on the Pharmacological Treatment of Chronic Heart Failure&quot;">​</a></h2><h2 id="_2022-aha-acc-hfsa-guideline-for-the-management-of-heart-failure" tabindex="-1">2022 AHA/ACC/HFSA Guideline for the Management of Heart Failure <a class="header-anchor" href="#_2022-aha-acc-hfsa-guideline-for-the-management-of-heart-failure" aria-label="Permalink to &quot;2022 AHA/ACC/HFSA Guideline for the Management of Heart Failure&quot;">​</a></h2><table tabindex="0"><thead><tr><th>疾病類別</th><th>主要疾病</th></tr></thead><tbody><tr><td>缺血性心臟病</td><td>冠狀動脈疾病（CAD）、心肌梗塞（MI）</td></tr><tr><td>心肌病變</td><td>擴張型心肌病（DCM）、肥厚型心肌病（HCM）、限制型心肌病（RCM）</td></tr><tr><td>高血壓</td><td>長期高血壓導致左心室肥厚，最終導致心衰</td></tr><tr><td>瓣膜性心臟病</td><td>主動脈狹窄（AS）、二尖瓣反流（MR）等</td></tr><tr><td>心律不整</td><td>心房顫動（AF）、室性心律不整</td></tr><tr><td>心包疾病</td><td>心包填塞（Tamponade）、縮窄性心包炎</td></tr></tbody></table><table tabindex="0"><thead><tr><th>疾病類別</th><th>主要疾病</th></tr></thead><tbody><tr><td>代謝性疾病</td><td>糖尿病（DM）、甲狀腺疾病（Hyper-/Hypothyroidism）</td></tr><tr><td>感染</td><td>心內膜炎（IE）、病毒性心肌炎（Viral Myocarditis）</td></tr><tr><td>毒性物質</td><td>酒精性心肌病變、化療藥物（Anthracyclines）</td></tr><tr><td>肺血管疾病</td><td>肺動脈高壓（PAH）、肺栓塞（PE）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類型</th><th>HFrEF（射出分數降低型心衰竭）</th><th>HFpEF（射出分數保留型心衰竭）</th></tr></thead><tbody><tr><td>定義</td><td>LVEF &lt; 40%</td><td>LVEF ≥ 50%</td></tr><tr><td>主要病因</td><td>心肌病變、心肌梗塞</td><td>高血壓、代謝疾病（糖尿病）、老化</td></tr><tr><td>主要機制</td><td>收縮功能異常（Systolic Dysfunction），導致心輸出量降低</td><td>舒張功能異常（Diastolic Dysfunction），導致心室充盈受限</td></tr><tr><td>心室結構</td><td>心室擴大（Dilation），心肌變薄</td><td>心室肥厚（Hypertrophy），心肌變硬</td></tr><tr><td>血流動力學</td><td>前負荷與後負荷增加，射出分數下降</td><td>心室順應性降低（Compliance Decrease），舒張壓升高</td></tr><tr><td>治療目標</td><td>減少心肌重塑，改善收縮功能</td><td>改善舒張功能與血流動力學</td></tr></tbody></table><table tabindex="0"><thead><tr><th>症狀</th><th>成因</th></tr></thead><tbody><tr><td>Fatigue（疲勞）</td><td>傳統上歸因於低心輸出（low cardiac output），但骨骼肌異常、貧血等因素也可能影響。</td></tr><tr><td>Dyspnea（呼吸困難）</td><td>最初僅發生在運動時，隨病程進展，逐漸在輕微活動甚至休息時發生。主要成因包括：肺充血（pulmonary congestion），引起快速、淺呼吸（cardiac dyspnea）。其他因素：肺順應性下降、氣道阻力增加、呼吸肌疲勞、貧血等。</td></tr><tr><td>Orthopnea（端坐呼吸）</td><td>仰臥時，腹內臟器及下肢靜脈回流增加，導致肺毛細血管壓升高，引起呼吸困難。可伴隨夜間咳嗽（nocturnal cough）。緩解方式：坐起來或墊高枕頭睡覺。</td></tr><tr><td>Paroxysmal Nocturnal Dyspnea (PND)（陣發性夜間呼吸困難）</td><td>夜間睡眠 1-3 小時後突然發作的嚴重呼吸困難，常伴隨咳嗽或喘鳴（wheezing）。可能因為支氣管動脈壓上升，導致氣道壓迫與阻力增加。與 Orthopnea 的不同：即使坐起來，症狀可能持續一段時間。</td></tr><tr><td>Cheyne-Stokes Respiration（陳-施氏呼吸）</td><td>40% 晚期 HF 患者出現，與低心輸出（low cardiac output）相關。由於呼吸中樞對 PCO2 的敏感度增加，以及循環時間延長，導致週期性呼吸模式：無呼吸期（apneic phase）：動脈 PO2 下降，PCO2 上升。過度換氣期（hyperventilation phase）：導致低碳酸血症（hypocapnia）。再次進入無呼吸期，形成循環。</td></tr><tr><td>Gastrointestinal Symptoms（腸胃道症狀）</td><td>食慾不振、噁心、早飽：腸道水腫、腸道充血導致消化不良。右上腹痛：肝臟充血，肝包膜拉扯引起不適。</td></tr><tr><td>Cerebral Symptoms（中樞神經症狀）</td><td>意識混亂、定向障礙、睡眠與情緒問題：特別見於老年患者，可能與腦動脈硬化、腦灌流不足有關。</td></tr><tr><td>Nocturia（夜尿）</td><td>HF患者平躺時靜脈回流增加，腎血流量改善，導致尿量增加，影響睡眠品質。</td></tr></tbody></table><table tabindex="0"><thead><tr><th>指標</th><th>特徵</th></tr></thead><tbody><tr><td>姿勢（Posture）</td><td>重度 HF 患者可能須端坐呼吸（Orthopnea）</td></tr><tr><td>血壓（Blood Pressure）</td><td>早期正常或偏高，晚期因 LV 功能不全而降低</td></tr><tr><td>脈壓（Pulse Pressure）</td><td>低脈壓提示心搏輸出量降低</td></tr><tr><td>周邊灌流（Peripheral Perfusion）</td><td>末梢冷、口唇與指甲發紺</td></tr></tbody></table><table tabindex="0"><thead><tr><th>體徵</th><th>特徵</th></tr></thead><tbody><tr><td>肺部濕囉音（Pulmonary Crackles/Rales）</td><td>肺水腫，表現為吸氣時爆裂音</td></tr><tr><td>心因性哮喘（Cardiac Asthma）</td><td>呼氣性喘鳴</td></tr><tr><td>胸腔積液（Pleural Effusion）</td><td>常見於雙側，若單側較多，右側更常見</td></tr></tbody></table><table tabindex="0"><thead><tr><th>體徵</th><th>臨床意義</th></tr></thead><tbody><tr><td>心尖搏動位移（PMI Displacement）</td><td>向下/左移提示 LV 擴大</td></tr><tr><td>第三心音（S3）</td><td>可能提示容量超載（Volume Overload），與心功能不全相關</td></tr><tr><td>第四心音（S4）</td><td>常見於舒張功能不全（Diastolic Dysfunction）</td></tr><tr><td>心雜音（Murmur）</td><td>常見於二尖瓣、三尖瓣逆流（Mitral/Tricuspid Regurgitation）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>體徵</th><th>臨床意義</th></tr></thead><tbody><tr><td>肝腫大（Hepatomegaly）</td><td>肝鬱血，可能伴隨壓痛</td></tr><tr><td>腹水（Ascites）</td><td>晚期 HF 表現</td></tr><tr><td>黃疸（Jaundice）</td><td>肝充血導致肝細胞缺氧</td></tr><tr><td>周邊水腫（Peripheral Edema）</td><td>對稱性，嚴重時可能影響陰囊或骶部</td></tr></tbody></table><table tabindex="0"><thead><tr><th>檢查項目</th><th>臨床意義</th></tr></thead><tbody><tr><td>血液常規（CBC）</td><td>貧血、感染</td></tr><tr><td>腎功能（BUN, Cr）</td><td>評估腎灌流與腎功能</td></tr><tr><td>肝功能（LFTs）</td><td>肝鬱血指標</td></tr><tr><td>尿液分析（Urinalysis）</td><td>可能發現蛋白尿</td></tr><tr><td>甲狀腺功能（TSH）</td><td>甲狀腺功能亢進/低下皆可影響心臟</td></tr><tr><td>血糖（Fasting glucose）</td><td>評估糖尿病</td></tr></tbody></table><table tabindex="0"><thead><tr><th>方法</th><th>用途</th></tr></thead><tbody><tr><td>心電圖</td><td>評估心房顫動，左心室肥厚（LVH）或舊 MI 痕跡（Q 波），QRS 時間</td></tr><tr><td>胸部X光</td><td>評估心臟大小（Cardiomegaly），肺充血或間質性水腫</td></tr><tr><td>超音波心動圖（Echocardiography）</td><td>評估 EF、壁運動、瓣膜</td></tr><tr><td>心臟 MRI</td><td>評估 LV 質量、體積、心肌病變</td></tr></tbody></table><table tabindex="0"><thead><tr><th>生物標誌物</th><th>主要用途</th></tr></thead><tbody><tr><td>BNP, NT-proBNP</td><td>診斷 HF，監測治療反應</td></tr><tr><td>Soluble ST-2, Galectin-3</td><td>評估預後</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物</th><th>機制</th><th>適應症</th><th>主要副作用</th></tr></thead><tbody><tr><td>利尿劑（Diuretics）</td><td></td><td></td><td></td></tr><tr><td>Furosemide, Torsemide (環利尿劑, Loop diuretics)</td><td>抑制腎臟 Henle’s loop 厚上升支的 Na⁺-K⁺-2Cl⁻ 同向轉運體，增加尿液排出</td><td>急性肺水腫、體液滯留</td><td>低血鉀（Hypokalemia）、低血壓、腎功能惡化</td></tr><tr><td>血管擴張劑（Vasodilators）</td><td></td><td></td><td></td></tr><tr><td>Nitroglycerin (硝酸酯類)</td><td>靜脈擴張，使血液滯留於靜脈系統，降低左心房壓力</td><td>急性心衰竭合併肺水腫</td><td>低血壓、頭痛、耐受性發展</td></tr><tr><td>Hydralazine</td><td>動脈擴張，降低後負荷</td><td>對 ACEI/ARB 不耐受的 HFrEF 患者</td><td>反射性心搏過速、藥物性紅斑性狼瘡</td></tr><tr><td>後負荷降低劑（Afterload Reducers）</td><td></td><td></td><td></td></tr><tr><td>ACEI（Captopril, Lisinopril）</td><td>抑制 ACE，降低 Angiotensin II，減少後負荷</td><td>HFrEF, 急性心衰竭</td><td>低血壓、高血鉀、乾咳、血管性水腫</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物</th><th>機制</th><th>適應症</th><th>主要副作用</th></tr></thead><tbody><tr><td>利尿劑（Diuretics）</td><td></td><td></td><td></td></tr><tr><td>Furosemide, Torsemide</td><td>增加尿量，降低心內壓</td><td>HFrEF 伴隨水腫</td><td>低血鉀、低血壓、腎衰竭</td></tr><tr><td>RAAS 抑制劑（RAAS Inhibitors）</td><td></td><td></td><td></td></tr><tr><td>ACEI（Captopril, Lisinopril, Enalapril）</td><td>抑制 Angiotensin II 生成，減少心臟重塑</td><td>HFrEF（NYHA I-IV）</td><td>低血壓、高血鉀、乾咳</td></tr><tr><td>ARB（Losartan, Valsartan）</td><td>阻斷 Angiotensin II 受體</td><td>ACEI 不耐受者</td><td>低血壓、高血鉀</td></tr><tr><td>Sacubitril/Valsartan</td><td>Sacubitril 抑制 Neprilysin，增加 ANP, BNP</td><td>取代 ACEI/ARB 以降低死亡率</td><td>低血壓、高血鉀、血管性水腫</td></tr><tr><td>β-阻斷劑（Beta-blockers）</td><td></td><td></td><td></td></tr><tr><td>Metoprolol, Carvedilol, Bisoprolol</td><td>抑制交感神經過度活化，減少心肌耗氧</td><td>HFrEF（NYHA II-III）</td><td>低血壓、心搏過緩、心衰加重（開始治療時）</td></tr><tr><td>Aldosterone 拮抗劑</td><td></td><td></td><td></td></tr><tr><td>Spironolactone, Eplerenone</td><td>抑制醛固酮，減少鈉水滯留</td><td>HFrEF（NYHA II-IV）</td><td>高血鉀、男性女乳症（Spironolactone）</td></tr><tr><td>特殊藥物</td><td></td><td></td><td></td></tr><tr><td>Ivabradine</td><td>抑制 SA node 的 If 電流，降低心率</td><td>HFrEF，心率 &gt;70 bpm 且不適用 β-blocker</td><td>心搏過緩、視覺異常</td></tr><tr><td>Nitrates + Hydralazine</td><td>靜脈擴張 + 動脈擴張，降低前負荷與後負荷</td><td>HFrEF，ACEI/ARB 不耐受者</td><td>低血壓、頭痛</td></tr></tbody></table><table tabindex="0"><thead><tr><th>項目</th><th>ICD</th><th>CRT</th></tr></thead><tbody><tr><td>主要功能</td><td>預防致命性心律不整</td><td>改善心室收縮同步性</td></tr><tr><td>適應症</td><td>HFrEF, 高風險 SCD</td><td>HFrEF, QRS 延長</td></tr><tr><td>主要作用</td><td>心律監測、電擊去顫</td><td>增加心輸出量，減少症狀</td></tr><tr><td>主要獲益</td><td>降低 SCD 發生率</td><td>降低 HF 住院率，提高生活品質</td></tr></tbody></table><table tabindex="0"><thead><tr><th>治療</th><th>適應症</th><th>副作用 / 風險</th></tr></thead><tbody><tr><td>強心劑（Inotropes）</td><td></td><td></td></tr><tr><td>Dobutamine</td><td>β1 促效劑，增加心肌收縮</td><td>心律不整、低血壓</td></tr><tr><td>Milrinone</td><td>PDE-3 抑制劑，增加心肌收縮力</td><td>低血壓、心律不整</td></tr><tr><td>Digoxin</td><td>抑制 Na⁺/K⁺ ATPase，提高 Ca²⁺</td><td>心律不整、低血鉀增加毒性</td></tr><tr><td>機械支持與外科治療</td><td></td><td></td></tr><tr><td>Left Ventricular Assist Devices (LVADs)</td><td>末期 HFrEF，作為橋接移植或終末治療</td><td>血栓、感染、出血</td></tr><tr><td>心臟移植（Heart Transplantation）</td><td>末期 HFrEF，無其他治療選項時</td><td>排斥反應、終生免疫抑制劑</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物</th><th>原因</th></tr></thead><tbody><tr><td>Metformin</td><td>增加乳酸中毒風險（特別是腎功能不全者）</td></tr><tr><td>Thiazolidinediones（Pioglitazone, Rosiglitazone）</td><td>造成體液滯留，加重心衰</td></tr><tr><td>非二氫吡啶類 CCB（Verapamil, Diltiazem）</td><td>負向肌力作用，惡化 HFrEF</td></tr><tr><td>NSAIDs</td><td>抑制前列腺素，導致體液滯留與腎衰竭</td></tr></tbody></table>',99)]))}const b=a(i,[["render",o]]);export{f as __pageData,b as default};
