import{_ as a,c as e,o as r,ae as i}from"./chunks/framework.Dgg8-8ov.js";const b=JSON.parse('{"title":"瓣膜性心臟病","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-瓣膜性心臟病-8.md","filePath":"InternalMedicineDepartment-瓣膜性心臟病-8.md"}'),l={name:"InternalMedicineDepartment-瓣膜性心臟病-8.md"};function d(o,t,h,s,n,u){return r(),e("div",null,t[0]||(t[0]=[i('<h1 id="瓣膜性心臟病" tabindex="-1">瓣膜性心臟病 <a class="header-anchor" href="#瓣膜性心臟病" aria-label="Permalink to &quot;瓣膜性心臟病&quot;">​</a></h1><h2 id="編輯格式" tabindex="-1">編輯格式 <a class="header-anchor" href="#編輯格式" aria-label="Permalink to &quot;編輯格式&quot;">​</a></h2><h3 id="顏色註記方式" tabindex="-1">顏色註記方式 <a class="header-anchor" href="#顏色註記方式" aria-label="Permalink to &quot;顏色註記方式&quot;">​</a></h3><h4 id="重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" tabindex="-1">重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記 <a class="header-anchor" href="#重要標題-母標題藍色-最暗-標記-子標題藍色-較暗-25-標記" aria-label="Permalink to &quot;重要標題 : 母標題藍色(最暗)標記/子標題藍色(較暗,25%)標記&quot;">​</a></h4><h4 id="重要術式治療藥物-綠色標記-粗體" tabindex="-1">重要術式治療藥物 : 綠色標記+粗體 <a class="header-anchor" href="#重要術式治療藥物-綠色標記-粗體" aria-label="Permalink to &quot;重要術式治療藥物 : 綠色標記+粗體&quot;">​</a></h4><h4 id="重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" tabindex="-1">重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間) <a class="header-anchor" href="#重要內文-紅色-標準色彩-橘色-粗體-橘色那一欄第一個-如果同色太醜就紅橘相間" aria-label="Permalink to &quot;重要內文 : 紅色(標準色彩)/橘色+粗體(橘色那一欄第一個)(如果同色太醜就紅橘相間)&quot;">​</a></h4><h4 id="直接來複製顏色格式一定不會錯xd" tabindex="-1">直接來複製顏色格式一定不會錯xd <a class="header-anchor" href="#直接來複製顏色格式一定不會錯xd" aria-label="Permalink to &quot;直接來複製顏色格式一定不會錯xd&quot;">​</a></h4><h2 id="心音" tabindex="-1">心音 <a class="header-anchor" href="#心音" aria-label="Permalink to &quot;心音&quot;">​</a></h2><h3 id="基礎心音-normal-heart-sounds" tabindex="-1">基礎心音（Normal Heart Sounds） <a class="header-anchor" href="#基礎心音-normal-heart-sounds" aria-label="Permalink to &quot;基礎心音（Normal Heart Sounds）&quot;">​</a></h3><h4 id="s1-第一心音-「lub」" tabindex="-1">S1（第一心音）「LUB」 <a class="header-anchor" href="#s1-第一心音-「lub」" aria-label="Permalink to &quot;S1（第一心音）「LUB」&quot;">​</a></h4><ul><li>來源： 二尖瓣（Mitral Valve, MV） 與 三尖瓣（Tricuspid Valve, TV） 的關閉</li><li>發生時機： 心室收縮開始（Systole 的開始）</li><li>聽診位置： <ul><li>心尖區（Apex, 第 5 肋間左鎖骨中線）最清楚</li></ul></li><li>強弱變化： <ul><li>增強： 高心搏量狀態（如發燒、貧血、甲亢）、二尖瓣狹窄</li><li>減弱： 二尖瓣閉鎖不全、心室收縮無力、心包積液</li></ul></li></ul><h4 id="s2-第二心音-「dub」" tabindex="-1">S2（第二心音）「DUB」 <a class="header-anchor" href="#s2-第二心音-「dub」" aria-label="Permalink to &quot;S2（第二心音）「DUB」&quot;">​</a></h4><ul><li>來源： 主動脈瓣（Aortic Valve, AV） 與 肺動脈瓣（Pulmonary Valve, PV） 的關閉</li><li>發生時機： 心室舒張開始（Diastole 的開始）</li><li>聽診位置： <ul><li>胸骨左緣第 2 肋間（肺動脈區）最清楚</li></ul></li><li>正常生理性分裂（Physiologic Split）： <ul><li>核心概念：正常情況下，吸氣時因肺血流增加，使肺動脈瓣（P2）關閉延遲，產生生理性分裂。 S2 分裂（A2→P2）</li></ul></li><li>異常變化： <ul><li>固定分裂（Fixed splitting of S2）： <ul><li>核心概念：吸氣與呼氣時的分裂程度相同，沒有隨呼吸改變。原因是左右心房間持續存在的分流，使肺循環血流量固定增加。</li><li>典型病因： <ul><li>Atrial Septal Defect (ASD)（房間隔缺損）</li></ul></li></ul></li><li>寬分裂（Wide splitting of S2）： <ul><li>核心概念：吸氣和呼氣時皆可聽到明顯的 S2 分裂，吸氣時分裂更為明顯。主要是因為右心負荷增加，肺動脈瓣（P2）關閉顯著延遲，使分裂持續存在。</li><li>常見病因： <ul><li>Right Bundle Branch Block (RBBB)（右束支傳導阻滯）</li><li>Pulmonary Stenosis（肺動脈瓣狹窄）</li><li>Pulmonary Hypertension（肺動脈高壓）</li></ul></li></ul></li><li>反轉分裂（Paradoxical Splitting of S2）： <ul><li>核心概念：正常情況下，吸氣時因肺血流增加，使肺動脈瓣（P2）關閉延遲，產生生理性分裂。反常性分裂則是在呼氣時出現分裂，而吸氣時反而消失，這是因為某些病理情況下，主動脈瓣（A2）關閉延遲，使其與肺動脈瓣的關閉時間重疊或錯位。反常性分裂通常與左心系統疾病相關，特別是會延遲主動脈瓣關閉的狀況。</li><li>常見病因： <ul><li>Left Bundle Branch Block (LBBB)（左束支傳導阻滯）</li><li>Aortic Stenosis (AS)（主動脈瓣狹窄）</li><li>Hypertrophic Cardiomyopathy（肥厚型心肌病變）</li><li>人工心律調節器（Ventricular Pacing）導致左心收縮延遲</li></ul></li><li>聽診特徵： <ul><li>呼氣時聽到A2-P2分裂</li><li>吸氣時因肺血回增加，P2延遲與A2重疊，使分裂消失</li><li>最佳聽診點： 胸骨左緣第2肋間（肺動脈區）</li></ul></li></ul></li></ul></li></ul><h3 id="額外心音-extra-heart-sounds" tabindex="-1">額外心音（Extra Heart Sounds） <a class="header-anchor" href="#額外心音-extra-heart-sounds" aria-label="Permalink to &quot;額外心音（Extra Heart Sounds）&quot;">​</a></h3><h4 id="s3-第三心音-「lub-dub-ta」" tabindex="-1">S3（第三心音）「LUB-DUB-ta」 <a class="header-anchor" href="#s3-第三心音-「lub-dub-ta」" aria-label="Permalink to &quot;S3（第三心音）「LUB-DUB-ta」&quot;">​</a></h4><ul><li>時機： 心室舒張早期（快速充盈期，在 S2 之後）</li><li>機轉： 心室快速充盈時，心室壁因擴張或僵硬產生震動</li><li>聽診位置： 心尖區（左側臥位時更清楚），用鈍頭聽診器</li><li>臨床意義： <ul><li>生理性（青少年、運動員、妊娠婦女）</li><li>病理性（心衰竭、擴張型心肌病、二尖瓣閉鎖不全）</li></ul></li><li>記憶法： 「Kentucky」節奏（S1-S2-S3）</li></ul><h4 id="s4-第四心音-—-「ta-lub-dub」" tabindex="-1">S4（第四心音） — 「ta-LUB-DUB」 <a class="header-anchor" href="#s4-第四心音-—-「ta-lub-dub」" aria-label="Permalink to &quot;S4（第四心音） — 「ta-LUB-DUB」&quot;">​</a></h4><ul><li>時機： 心室舒張末期（心房收縮期，在 S1 之前）</li><li>機轉： 心房收縮將血液推入僵硬的心室，產生震動</li><li>聽診位置： 心尖區（左側臥位），用鈍頭聽診器</li><li>臨床意義： <ul><li>病理性（左心室肥厚、主動脈瓣狹窄、高血壓心臟病、心肌缺血）</li><li>不會出現在心房顫動（AF）患者，因為缺乏心房收縮</li></ul></li><li>記憶法： 「Tennessee」節奏（S4-S1-S2）</li></ul><h2 id="heart-murmur-心雜音" tabindex="-1">Heart murmur 心雜音 <a class="header-anchor" href="#heart-murmur-心雜音" aria-label="Permalink to &quot;Heart murmur 心雜音&quot;">​</a></h2><h3 id="heart-murmur-心雜音-是心臟血流經過瓣膜或心腔時產生的紊流-turbulent-flow-所引起的額外聲音-常在心臟聽診時以「吹拂音-blowing-」、「粗糙音-harsh-」或「機械音-machinery-like-」等形式呈現。心雜音可以是生理性-無害-或病理性-與心血管疾病相關-。" tabindex="-1">Heart Murmur（心雜音） 是心臟血流經過瓣膜或心腔時產生的紊流（Turbulent Flow）所引起的額外聲音，常在心臟聽診時以「吹拂音（Blowing）」、「粗糙音（Harsh）」或「機械音（Machinery-like）」等形式呈現。心雜音可以是生理性（無害）或病理性（與心血管疾病相關）。 <a class="header-anchor" href="#heart-murmur-心雜音-是心臟血流經過瓣膜或心腔時產生的紊流-turbulent-flow-所引起的額外聲音-常在心臟聽診時以「吹拂音-blowing-」、「粗糙音-harsh-」或「機械音-machinery-like-」等形式呈現。心雜音可以是生理性-無害-或病理性-與心血管疾病相關-。" aria-label="Permalink to &quot;Heart Murmur（心雜音） 是心臟血流經過瓣膜或心腔時產生的紊流（Turbulent Flow）所引起的額外聲音，常在心臟聽診時以「吹拂音（Blowing）」、「粗糙音（Harsh）」或「機械音（Machinery-like）」等形式呈現。心雜音可以是生理性（無害）或病理性（與心血管疾病相關）。&quot;">​</a></h3><h2 id="雜音強度分級-levine-scale-6-級制" tabindex="-1">雜音強度分級（Levine Scale, 6 級制） <a class="header-anchor" href="#雜音強度分級-levine-scale-6-級制" aria-label="Permalink to &quot;雜音強度分級（Levine Scale, 6 級制）&quot;">​</a></h2><h2 id="heart-murmur介紹" tabindex="-1">Heart murmur介紹 <a class="header-anchor" href="#heart-murmur介紹" aria-label="Permalink to &quot;Heart murmur介紹&quot;">​</a></h2><h2 id="常見心雜音詳解" tabindex="-1">常見心雜音詳解 <a class="header-anchor" href="#常見心雜音詳解" aria-label="Permalink to &quot;常見心雜音詳解&quot;">​</a></h2><h3 id="收縮期雜音-systolic-murmur" tabindex="-1">收縮期雜音（Systolic Murmur） <a class="header-anchor" href="#收縮期雜音-systolic-murmur" aria-label="Permalink to &quot;收縮期雜音（Systolic Murmur）&quot;">​</a></h3><h4 id="s1-與-s2-之間發生-可能是瓣膜狹窄或閉鎖不全。" tabindex="-1">S1 與 S2 之間發生，可能是瓣膜狹窄或閉鎖不全。 <a class="header-anchor" href="#s1-與-s2-之間發生-可能是瓣膜狹窄或閉鎖不全。" aria-label="Permalink to &quot;S1 與 S2 之間發生，可能是瓣膜狹窄或閉鎖不全。&quot;">​</a></h4><h3 id="舒張期雜音-diastolic-murmur" tabindex="-1">舒張期雜音（Diastolic Murmur） <a class="header-anchor" href="#舒張期雜音-diastolic-murmur" aria-label="Permalink to &quot;舒張期雜音（Diastolic Murmur）&quot;">​</a></h3><h4 id="s2與s1之間發生-通常為病理性-提示瓣膜狹窄或閉鎖不全。" tabindex="-1">S2與S1之間發生，通常為病理性，提示瓣膜狹窄或閉鎖不全。 <a class="header-anchor" href="#s2與s1之間發生-通常為病理性-提示瓣膜狹窄或閉鎖不全。" aria-label="Permalink to &quot;S2與S1之間發生，通常為病理性，提示瓣膜狹窄或閉鎖不全。&quot;">​</a></h4><h3 id="連續性雜音-continuous-murmur" tabindex="-1">連續性雜音（Continuous Murmur） <a class="header-anchor" href="#連續性雜音-continuous-murmur" aria-label="Permalink to &quot;連續性雜音（Continuous Murmur）&quot;">​</a></h3><h4 id="聽診時整個心週期-收縮期與舒張期-都能聽見。" tabindex="-1">聽診時整個心週期（收縮期與舒張期）都能聽見。 <a class="header-anchor" href="#聽診時整個心週期-收縮期與舒張期-都能聽見。" aria-label="Permalink to &quot;聽診時整個心週期（收縮期與舒張期）都能聽見。&quot;">​</a></h4><h2 id="心音的變化" tabindex="-1">心音的變化 <a class="header-anchor" href="#心音的變化" aria-label="Permalink to &quot;心音的變化&quot;">​</a></h2><h3 id="呼吸變化" tabindex="-1">呼吸變化： <a class="header-anchor" href="#呼吸變化" aria-label="Permalink to &quot;呼吸變化：&quot;">​</a></h3><h4 id="吸氣時增強-吸氣時-橫膈下降、胸腔內壓降低-→-增加靜脈回流-→-右心血液增加-→-右心相關雜音增加-如-tr、ps" tabindex="-1">吸氣時增強： 吸氣時，橫膈下降、胸腔內壓降低 → 增加靜脈回流 → 右心血液增加 → 右心相關雜音增加（如 TR、PS） <a class="header-anchor" href="#吸氣時增強-吸氣時-橫膈下降、胸腔內壓降低-→-增加靜脈回流-→-右心血液增加-→-右心相關雜音增加-如-tr、ps" aria-label="Permalink to &quot;吸氣時增強： 吸氣時，橫膈下降、胸腔內壓降低 → 增加靜脈回流 → 右心血液增加 → 右心相關雜音增加（如 TR、PS）&quot;">​</a></h4><h4 id="呼氣時增強-呼氣時-橫膈上升、胸腔內壓回升-→-增加肺靜脈回流-更多血液回流到左心房-→-左心室充血量增加-→-左心相關雜音增加-如-mr、as、ar、ms" tabindex="-1">呼氣時增強： 呼氣時，橫膈上升、胸腔內壓回升 → 增加肺靜脈回流，更多血液回流到左心房 → 左心室充血量增加 → 左心相關雜音增加（如 MR、AS、AR、MS） <a class="header-anchor" href="#呼氣時增強-呼氣時-橫膈上升、胸腔內壓回升-→-增加肺靜脈回流-更多血液回流到左心房-→-左心室充血量增加-→-左心相關雜音增加-如-mr、as、ar、ms" aria-label="Permalink to &quot;呼氣時增強： 呼氣時，橫膈上升、胸腔內壓回升 → 增加肺靜脈回流，更多血液回流到左心房 → 左心室充血量增加 → 左心相關雜音增加（如 MR、AS、AR、MS）&quot;">​</a></h4><h3 id="valsalva-maneuver" tabindex="-1">Valsalva Maneuver： <a class="header-anchor" href="#valsalva-maneuver" aria-label="Permalink to &quot;Valsalva Maneuver：&quot;">​</a></h3><h4 id="用力閉氣-→-胸腔內壓上升-→-靜脈回流受阻-→-心室充血量減少。" tabindex="-1">用力閉氣 → 胸腔內壓上升 → 靜脈回流受阻 → 心室充血量減少。 <a class="header-anchor" href="#用力閉氣-→-胸腔內壓上升-→-靜脈回流受阻-→-心室充血量減少。" aria-label="Permalink to &quot;用力閉氣 → 胸腔內壓上升 → 靜脈回流受阻 → 心室充血量減少。&quot;">​</a></h4><h4 id="大部分雜音減弱-因血流減少導致湍流減少。" tabindex="-1">大部分雜音減弱，因血流減少導致湍流減少。 <a class="header-anchor" href="#大部分雜音減弱-因血流減少導致湍流減少。" aria-label="Permalink to &quot;大部分雜音減弱，因血流減少導致湍流減少。&quot;">​</a></h4><h4 id="例外" tabindex="-1">例外： <a class="header-anchor" href="#例外" aria-label="Permalink to &quot;例外：&quot;">​</a></h4><ul><li>Hypertrophic Cardiomyopathy（HCM）： 心室腔室變小 → 動態性流出道阻塞加重 → 雜音變大聲。</li><li>Mitral Valve Prolapse（MVP）： 心室腔變小 → 二尖瓣脫垂更早發生 → 雜音變大聲。</li></ul><h3 id="體位改變" tabindex="-1">體位改變： <a class="header-anchor" href="#體位改變" aria-label="Permalink to &quot;體位改變：&quot;">​</a></h3><h4 id="蹲下-squatting-抬腳-增加靜脈回流-→-大部分雜音變大聲-但hcm充血量增加-→-心室腔室擴大-→-流出道阻塞減少-→-雜音變小聲、mvp左心室腔擴大-→-脫垂延後-→-雜音變小聲" tabindex="-1">蹲下（Squatting）/抬腳： 增加靜脈回流 → 大部分雜音變大聲，但HCM充血量增加 → 心室腔室擴大 → 流出道阻塞減少 → 雜音變小聲、MVP左心室腔擴大 → 脫垂延後 → 雜音變小聲 <a class="header-anchor" href="#蹲下-squatting-抬腳-增加靜脈回流-→-大部分雜音變大聲-但hcm充血量增加-→-心室腔室擴大-→-流出道阻塞減少-→-雜音變小聲、mvp左心室腔擴大-→-脫垂延後-→-雜音變小聲" aria-label="Permalink to &quot;蹲下（Squatting）/抬腳： 增加靜脈回流 → 大部分雜音變大聲，但HCM充血量增加 → 心室腔室擴大 → 流出道阻塞減少 → 雜音變小聲、MVP左心室腔擴大 → 脫垂延後 → 雜音變小聲&quot;">​</a></h4><h4 id="站-靜脈回流減少-→-大部分雜音減弱-但hcm和mvp因心室腔室變小-→-雜音變大聲" tabindex="-1">站： 靜脈回流減少 → 大部分雜音減弱，但HCM和MVP因心室腔室變小 → 雜音變大聲 <a class="header-anchor" href="#站-靜脈回流減少-→-大部分雜音減弱-但hcm和mvp因心室腔室變小-→-雜音變大聲" aria-label="Permalink to &quot;站： 靜脈回流減少 → 大部分雜音減弱，但HCM和MVP因心室腔室變小 → 雜音變大聲&quot;">​</a></h4><h3 id="握拳運動-handgrip-exercise" tabindex="-1">握拳運動（Handgrip Exercise）： <a class="header-anchor" href="#握拳運動-handgrip-exercise" aria-label="Permalink to &quot;握拳運動（Handgrip Exercise）：&quot;">​</a></h3><h4 id="周邊血管阻力-afterload-增加-→-左心壓力增加-→-心室內血流量增加" tabindex="-1">周邊血管阻力（Afterload）增加 → 左心壓力增加 → 心室內血流量增加 <a class="header-anchor" href="#周邊血管阻力-afterload-增加-→-左心壓力增加-→-心室內血流量增加" aria-label="Permalink to &quot;周邊血管阻力（Afterload）增加 → 左心壓力增加 → 心室內血流量增加&quot;">​</a></h4><ul><li>MR、AR、VSD 雜音變大聲（左心或分流相關）</li><li>HCM 雜音變小聲（左心室流出道壓力減少）</li><li>MVP： 影響不明顯，但部分情況下可延遲脫垂發生</li></ul><h3 id="藥物測試" tabindex="-1">藥物測試： <a class="header-anchor" href="#藥物測試" aria-label="Permalink to &quot;藥物測試：&quot;">​</a></h3><h4 id="amyl-nitrite-降低血管阻力-→-mr-減弱、hcm-增強" tabindex="-1">Amyl Nitrite： 降低血管阻力 → MR 減弱、HCM 增強 <a class="header-anchor" href="#amyl-nitrite-降低血管阻力-→-mr-減弱、hcm-增強" aria-label="Permalink to &quot;Amyl Nitrite： 降低血管阻力 → MR 減弱、HCM 增強&quot;">​</a></h4><h2 id="心雜音重點整理" tabindex="-1">心雜音重點整理 <a class="header-anchor" href="#心雜音重點整理" aria-label="Permalink to &quot;心雜音重點整理&quot;">​</a></h2><h2 id="動脈壓脈搏-arterial-pressure-pulse" tabindex="-1">動脈壓脈搏(Arterial pressure pulse) <a class="header-anchor" href="#動脈壓脈搏-arterial-pressure-pulse" aria-label="Permalink to &quot;動脈壓脈搏(Arterial pressure pulse)&quot;">​</a></h2><h3 id="動脈壓脈搏-arterial-pressure-pulse-是指心臟每次收縮所產生的壓力波-經由動脈傳導到全身-形成可觸摸到的脈搏。它反映了心臟泵血功能、血管彈性以及血液動力學的變化。" tabindex="-1">動脈壓脈搏（Arterial Pressure Pulse） 是指心臟每次收縮所產生的壓力波，經由動脈傳導到全身，形成可觸摸到的脈搏。它反映了心臟泵血功能、血管彈性以及血液動力學的變化。 <a class="header-anchor" href="#動脈壓脈搏-arterial-pressure-pulse-是指心臟每次收縮所產生的壓力波-經由動脈傳導到全身-形成可觸摸到的脈搏。它反映了心臟泵血功能、血管彈性以及血液動力學的變化。" aria-label="Permalink to &quot;動脈壓脈搏（Arterial Pressure Pulse） 是指心臟每次收縮所產生的壓力波，經由動脈傳導到全身，形成可觸摸到的脈搏。它反映了心臟泵血功能、血管彈性以及血液動力學的變化。&quot;">​</a></h3><h2 id="頸靜脈波形" tabindex="-1">頸靜脈波形 <a class="header-anchor" href="#頸靜脈波形" aria-label="Permalink to &quot;頸靜脈波形&quot;">​</a></h2><h3 id="頸靜脈壓波形-jugular-venous-pulse-jvp-是反映右心房壓力變化的間接指標。由於頸內靜脈與右心房之間沒有瓣膜阻隔-心房內的壓力變化可以直接傳遞到頸靜脈-形成特有的波形。評估右心功能、心包疾病、以及血液動力學狀態。" tabindex="-1">頸靜脈壓波形（Jugular Venous Pulse, JVP） 是反映右心房壓力變化的間接指標。由於頸內靜脈與右心房之間沒有瓣膜阻隔，心房內的壓力變化可以直接傳遞到頸靜脈，形成特有的波形。評估右心功能、心包疾病、以及血液動力學狀態。 <a class="header-anchor" href="#頸靜脈壓波形-jugular-venous-pulse-jvp-是反映右心房壓力變化的間接指標。由於頸內靜脈與右心房之間沒有瓣膜阻隔-心房內的壓力變化可以直接傳遞到頸靜脈-形成特有的波形。評估右心功能、心包疾病、以及血液動力學狀態。" aria-label="Permalink to &quot;頸靜脈壓波形（Jugular Venous Pulse, JVP） 是反映右心房壓力變化的間接指標。由於頸內靜脈與右心房之間沒有瓣膜阻隔，心房內的壓力變化可以直接傳遞到頸靜脈，形成特有的波形。評估右心功能、心包疾病、以及血液動力學狀態。&quot;">​</a></h3><h2 id="瓣膜性心臟病定義" tabindex="-1">瓣膜性心臟病定義： <a class="header-anchor" href="#瓣膜性心臟病定義" aria-label="Permalink to &quot;瓣膜性心臟病定義：&quot;">​</a></h2><h3 id="valvular-heart-disease-心臟瓣膜疾病-是指心臟四個瓣膜-aortic-valve、mitral-valve、tricuspid-valve、pulmonary-valve-出現結構或功能異常-導致血流受阻-stenosis-狹窄-或血液逆流-regurgitation-insufficiency-閉鎖不全-。這會進一步引起心室負荷增加、心臟擴大、心衰竭等併發症。" tabindex="-1">Valvular Heart Disease（心臟瓣膜疾病） 是指心臟四個瓣膜（Aortic valve、Mitral valve、Tricuspid valve、Pulmonary valve）出現結構或功能異常，導致血流受阻（Stenosis, 狹窄）或血液逆流（Regurgitation/Insufficiency, 閉鎖不全）。這會進一步引起心室負荷增加、心臟擴大、心衰竭等併發症。 <a class="header-anchor" href="#valvular-heart-disease-心臟瓣膜疾病-是指心臟四個瓣膜-aortic-valve、mitral-valve、tricuspid-valve、pulmonary-valve-出現結構或功能異常-導致血流受阻-stenosis-狹窄-或血液逆流-regurgitation-insufficiency-閉鎖不全-。這會進一步引起心室負荷增加、心臟擴大、心衰竭等併發症。" aria-label="Permalink to &quot;Valvular Heart Disease（心臟瓣膜疾病） 是指心臟四個瓣膜（Aortic valve、Mitral valve、Tricuspid valve、Pulmonary valve）出現結構或功能異常，導致血流受阻（Stenosis, 狹窄）或血液逆流（Regurgitation/Insufficiency, 閉鎖不全）。這會進一步引起心室負荷增加、心臟擴大、心衰竭等併發症。&quot;">​</a></h3><h2 id="aortic-valve-disease-主動脈瓣疾病-最常見的瓣膜疾病之一" tabindex="-1">Aortic Valve Disease（主動脈瓣疾病）： 最常見的瓣膜疾病之一 <a class="header-anchor" href="#aortic-valve-disease-主動脈瓣疾病-最常見的瓣膜疾病之一" aria-label="Permalink to &quot;Aortic Valve Disease（主動脈瓣疾病）： 最常見的瓣膜疾病之一&quot;">​</a></h2><h3 id="aortic-stenosis-as-主動脈瓣狹窄" tabindex="-1">Aortic Stenosis (AS, 主動脈瓣狹窄) <a class="header-anchor" href="#aortic-stenosis-as-主動脈瓣狹窄" aria-label="Permalink to &quot;Aortic Stenosis (AS, 主動脈瓣狹窄)&quot;">​</a></h3><h4 id="病因" tabindex="-1">病因： <a class="header-anchor" href="#病因" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>鈣化性退化（老年人最常見）</li><li>先天性二葉瓣（Bicuspid aortic valve）</li><li>風濕性心臟病（Rheumatic fever）</li></ul><h4 id="病理生理" tabindex="-1">病理生理： <a class="header-anchor" href="#病理生理" aria-label="Permalink to &quot;病理生理：&quot;">​</a></h4><ul><li>左心室需產生更大壓力以克服阻力（afterload增加） → 左心室肥厚 → 最終心衰竭</li><li>LV systolic pressure 大於 aortic systolic pressure</li></ul><h4 id="臨床表現" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>經典三徵（Angina, Syncope, Heart Failure）</li><li>心雜音： <ul><li>胸骨右緣第 2 肋間聽到粗糙的收縮期雜音（Systolic Crescendo-Decrescendo），向頸動脈放射。</li><li>S2 單一或矛盾性分裂（paradoxical split）。</li><li>重度 AS：S2 消失或變柔。</li></ul></li><li>Pulsus parvus et tardus（重度AS）</li></ul><h4 id="診斷-心臟超音波-echocardiography-評估瓣膜面積與壓力梯度" tabindex="-1">診斷： 心臟超音波（Echocardiography）評估瓣膜面積與壓力梯度 <a class="header-anchor" href="#診斷-心臟超音波-echocardiography-評估瓣膜面積與壓力梯度" aria-label="Permalink to &quot;診斷： 心臟超音波（Echocardiography）評估瓣膜面積與壓力梯度&quot;">​</a></h4><h4 id="治療" tabindex="-1">治療： <a class="header-anchor" href="#治療" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>無症狀 → 觀察追蹤，藥物緩解症狀（如利尿劑）</li><li>有症狀或重度狹窄 → 外科瓣膜置換（SAVR）或經導管瓣膜置換術（TAVR）</li></ul><h3 id="aortic-regurgitation-ar-主動脈瓣逆流" tabindex="-1">Aortic Regurgitation (AR, 主動脈瓣逆流) <a class="header-anchor" href="#aortic-regurgitation-ar-主動脈瓣逆流" aria-label="Permalink to &quot;Aortic Regurgitation (AR, 主動脈瓣逆流)&quot;">​</a></h3><h4 id="病因-1" tabindex="-1">病因： <a class="header-anchor" href="#病因-1" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>急性：感染性心內膜炎（IE）、主動脈剝離（Dissection）、外傷、心肌梗塞</li><li>慢性：馬凡氏症（Marfan syndrome）、梅毒、風濕性心臟病、瓣膜退化</li></ul><h4 id="病理生理-1" tabindex="-1">病理生理： <a class="header-anchor" href="#病理生理-1" aria-label="Permalink to &quot;病理生理：&quot;">​</a></h4><ul><li>心舒期血液逆流至左心室（LV的preload和stroke volume增加） → 容量負荷增加 → 左心室擴大</li></ul><h4 id="臨床表現-1" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-1" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>急性 AR：肺水腫、心源性休克、嚴重呼吸困難</li><li>慢性 AR：逐漸惡化的呼吸困難、仰臥時心悸感、夜間陣發性呼吸困難</li><li>心雜音： <ul><li>左胸骨緣聽到早期吹氣樣舒張期雜音（Early blowing diastolic murmur）</li><li>Austin Flint murmur：心尖部中舒張期雜音（因逆流壓迫二尖瓣）</li></ul></li><li>脈壓擴大（Wide pulse pressure） <ul><li>de Musset sign（頭部隨脈搏擺動）</li><li>Corrigan sign（Water-hammer pulse）</li><li>Duroziez sign（股動脈雜音）</li></ul></li><li>低舒張壓 <ul><li>血液回流到左心室導致低舒張壓（Low diastolic BP）</li><li>正常120/80 → 120/40</li></ul></li></ul><h4 id="診斷-心臟超音波-echocardiogarphy-確認逆流程度與左心室功能" tabindex="-1">診斷：心臟超音波（Echocardiogarphy）確認逆流程度與左心室功能 <a class="header-anchor" href="#診斷-心臟超音波-echocardiogarphy-確認逆流程度與左心室功能" aria-label="Permalink to &quot;診斷：心臟超音波（Echocardiogarphy）確認逆流程度與左心室功能&quot;">​</a></h4><h4 id="治療-1" tabindex="-1">治療： <a class="header-anchor" href="#治療-1" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>血管擴張劑（如 ACEI、Nifedipine）降低後負荷</li><li>症狀嚴重 → 瓣膜置換術</li><li>急性 AR：需緊急手術</li></ul><h2 id="mitral-valve-disease-僧帽瓣疾病-亞洲地區常見風濕性病變" tabindex="-1">Mitral Valve Disease（僧帽瓣疾病）：亞洲地區常見風濕性病變 <a class="header-anchor" href="#mitral-valve-disease-僧帽瓣疾病-亞洲地區常見風濕性病變" aria-label="Permalink to &quot;Mitral Valve Disease（僧帽瓣疾病）：亞洲地區常見風濕性病變&quot;">​</a></h2><h3 id="mitral-stenosis-ms-僧帽瓣狹窄" tabindex="-1">Mitral Stenosis (MS, 僧帽瓣狹窄) <a class="header-anchor" href="#mitral-stenosis-ms-僧帽瓣狹窄" aria-label="Permalink to &quot;Mitral Stenosis (MS, 僧帽瓣狹窄)&quot;">​</a></h3><h4 id="病因-2" tabindex="-1">病因： <a class="header-anchor" href="#病因-2" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>風濕性心臟病（最主要病因）</li></ul><h4 id="病理生理-2" tabindex="-1">病理生理： <a class="header-anchor" href="#病理生理-2" aria-label="Permalink to &quot;病理生理：&quot;">​</a></h4><ul><li>左心房壓力上升 → 左心房擴大 → 心房顫動（Atrial Fibrillation, AF） &amp; 肺高壓</li><li>LA pressure 大於 LV diastolic pressure（mitral valve stiff，左心房需要更用力把血擠到左心室）</li></ul><h4 id="臨床表現-2" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-2" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>肺鬱血症狀：呼吸困難、仰臥呼吸困難（Orthopnea）、夜間陣發性呼吸困難、咳血（Hemoptysis）</li><li>左心房擴大（LAE）併發症：心房顫動（AF）、聲音沙啞（壓迫喉返神經）、吞嚥困難（壓迫食道）</li><li>心雜音： <ul><li>心尖區聽到舒張期低頻隆隆音（Mid-diastolic Rumbling Murmur），常伴有 Opening Snap</li></ul></li></ul><h4 id="診斷-心臟超音波" tabindex="-1">診斷： 心臟超音波 <a class="header-anchor" href="#診斷-心臟超音波" aria-label="Permalink to &quot;診斷： 心臟超音波&quot;">​</a></h4><h4 id="治療-2" tabindex="-1">治療： <a class="header-anchor" href="#治療-2" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>抗心律不整藥物（β-blockers, digoxin, CCBs）控制心房顫動</li><li>抗凝治療（如 Warfarin）以預防血栓</li><li>嚴重者： 經皮氣球擴張術（PBMV）或瓣膜置換（若嚴重狹窄）</li></ul><h3 id="mitral-regurgitation-mr-僧帽瓣逆流" tabindex="-1">Mitral Regurgitation (MR, 僧帽瓣逆流) <a class="header-anchor" href="#mitral-regurgitation-mr-僧帽瓣逆流" aria-label="Permalink to &quot;Mitral Regurgitation (MR, 僧帽瓣逆流)&quot;">​</a></h3><h4 id="病因-3" tabindex="-1">病因： <a class="header-anchor" href="#病因-3" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>慢性 MR：風濕性心臟病、二尖瓣脫垂（Mitral Valve Prolapse, MVP）、心內膜炎</li><li>急性 MR：乳頭肌斷裂（心肌梗塞後）、腱索斷裂、外傷</li></ul><h4 id="病理生理-3" tabindex="-1">病理生理： <a class="header-anchor" href="#病理生理-3" aria-label="Permalink to &quot;病理生理：&quot;">​</a></h4><ul><li>心收縮期血液逆流至左心房 → 容量負荷增加 → 左心房擴大</li></ul><h4 id="臨床表現-3" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-3" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>慢性：呼吸困難、夜間陣發性呼吸困、疲勞</li><li>急性：肺水腫、休克</li><li>心雜音： <ul><li>S3 心音（左心室容量負荷增加）</li><li>心尖區聽到全收縮期雜音（Holosystolic Murmur），向左腋下放射</li></ul></li></ul><h4 id="診斷-心臟超音波評估逆流程度-心導管評估嚴重程度與冠狀動脈狀況-特別是術前評估-。" tabindex="-1">診斷：心臟超音波評估逆流程度，心導管評估嚴重程度與冠狀動脈狀況（特別是術前評估）。 <a class="header-anchor" href="#診斷-心臟超音波評估逆流程度-心導管評估嚴重程度與冠狀動脈狀況-特別是術前評估-。" aria-label="Permalink to &quot;診斷：心臟超音波評估逆流程度，心導管評估嚴重程度與冠狀動脈狀況（特別是術前評估）。&quot;">​</a></h4><h4 id="治療-3" tabindex="-1">治療： <a class="header-anchor" href="#治療-3" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>藥物治療：ACEIs/ARBs 降低後負荷，利尿劑緩解肺鬱血</li><li>控制心律不整（AF 常見）：β-blockers、抗凝劑</li><li>瓣膜修補或置換術：適用於嚴重 MR</li></ul><h2 id="tricuspid-valve-disease-三尖瓣疾病" tabindex="-1">Tricuspid Valve Disease（三尖瓣疾病） <a class="header-anchor" href="#tricuspid-valve-disease-三尖瓣疾病" aria-label="Permalink to &quot;Tricuspid Valve Disease（三尖瓣疾病）&quot;">​</a></h2><h3 id="tricuspid-regurgitation-tr-三尖瓣逆流" tabindex="-1">Tricuspid Regurgitation (TR, 三尖瓣逆流) <a class="header-anchor" href="#tricuspid-regurgitation-tr-三尖瓣逆流" aria-label="Permalink to &quot;Tricuspid Regurgitation (TR, 三尖瓣逆流)&quot;">​</a></h3><h4 id="病因-4" tabindex="-1">病因： <a class="header-anchor" href="#病因-4" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>少量三尖瓣逆流（Tricuspid Regurgitation, TR）在正常生理情況下是可以接受的，這被稱為生理性三尖瓣逆流（Physiologic TR）</li><li>右心擴大（最常見）、肺高壓、感染性心內膜炎（IV drug users 常見）、Carcinoid、Ebstein anomaly</li></ul><h4 id="臨床表現-4" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-4" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>右心衰竭（Right Heart Failure）症狀：頸靜脈怒張（JVD）、肝腫大（Hepatomegaly）、腹水（Ascites）、下肢水腫（Peripheral edema）</li><li>心雜音： 胸骨左下緣聽到收縮期雜音（Holosystic murmur），吸氣時加重（Carvallo’s sign）</li></ul><h4 id="治療-4" tabindex="-1">治療： <a class="header-anchor" href="#治療-4" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>控制肺高壓、利尿劑減輕症狀</li><li>嚴重者考慮外科修補/置換瓣膜</li></ul><h3 id="tricuspid-stenosis" tabindex="-1">Tricuspid stenosis <a class="header-anchor" href="#tricuspid-stenosis" aria-label="Permalink to &quot;Tricuspid stenosis&quot;">​</a></h3><h4 id="病因-5" tabindex="-1">病因： <a class="header-anchor" href="#病因-5" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>罕見的瓣膜疾病，多與風濕性心臟病（Rheumatic Heart Disease, RHD）併發二尖瓣疾病（Mitral Valve Disease）相關，常與二尖瓣狹窄（Mitral Stenosis, MS）共存</li></ul><h4 id="臨床表現-5" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-5" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>右心衰竭（Right Heart Failure）症狀：頸靜脈怒張（JVD）、肝腫大（Hepatomegaly）、腹水（Ascites）、下肢水腫（Peripheral edema）</li><li>心雜音：左下胸骨緣舒張期雜音（Diastolic Murmur）</li></ul><h2 id="pulmonary-valve-disease-肺動脈瓣疾病" tabindex="-1">Pulmonary Valve Disease（肺動脈瓣疾病） <a class="header-anchor" href="#pulmonary-valve-disease-肺動脈瓣疾病" aria-label="Permalink to &quot;Pulmonary Valve Disease（肺動脈瓣疾病）&quot;">​</a></h2><h3 id="pulmonary-stenosis-ps-肺動脈瓣狹窄" tabindex="-1">Pulmonary Stenosis (PS, 肺動脈瓣狹窄) <a class="header-anchor" href="#pulmonary-stenosis-ps-肺動脈瓣狹窄" aria-label="Permalink to &quot;Pulmonary Stenosis (PS, 肺動脈瓣狹窄)&quot;">​</a></h3><h4 id="病因-6" tabindex="-1">病因： <a class="header-anchor" href="#病因-6" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>先天性心臟病（最常見）</li></ul><h4 id="臨床表現-6" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-6" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>右心衰竭症狀（JVD、肝腫大、下肢水腫）</li><li>胸骨左上緣聽到 收縮期雜音</li></ul><h4 id="治療-5" tabindex="-1">治療： <a class="header-anchor" href="#治療-5" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>嚴重狹窄者進行經皮氣球擴張術</li></ul><h3 id="pulmonary-regurgitation-pr-肺動脈瓣逆流" tabindex="-1">Pulmonary Regurgitation (PR, 肺動脈瓣逆流) <a class="header-anchor" href="#pulmonary-regurgitation-pr-肺動脈瓣逆流" aria-label="Permalink to &quot;Pulmonary Regurgitation (PR, 肺動脈瓣逆流)&quot;">​</a></h3><h4 id="病因-7" tabindex="-1">病因： <a class="header-anchor" href="#病因-7" aria-label="Permalink to &quot;病因：&quot;">​</a></h4><ul><li>肺高壓、感染性心內膜炎、瓣膜退化</li></ul><h4 id="臨床表現-7" tabindex="-1">臨床表現： <a class="header-anchor" href="#臨床表現-7" aria-label="Permalink to &quot;臨床表現：&quot;">​</a></h4><ul><li>胸骨左緣聽到 舒張期遞減型雜音</li><li>右心衰竭症狀</li></ul><h4 id="治療-6" tabindex="-1">治療： <a class="header-anchor" href="#治療-6" aria-label="Permalink to &quot;治療：&quot;">​</a></h4><ul><li>控制肺高壓、必要時瓣膜置換</li></ul><h2 id="" tabindex="-1"><a class="header-anchor" href="#" aria-label="Permalink to &quot;&quot;">​</a></h2><table tabindex="0"><thead><tr><th>疾病</th><th>雜音特徵</th><th>聽診位置</th><th>輻射方向</th></tr></thead><tbody><tr><td>Aortic Stenosis (AS)</td><td>粗糙的噴射性雜音（Crescendo-Decrescendo）</td><td>胸骨右緣第 2 肋間</td><td>頸部、頸動脈</td></tr><tr><td>Pulmonary Stenosis (PS)</td><td>粗糙噴射性雜音</td><td>胸骨左緣第 2 肋間</td><td>左肩</td></tr><tr><td>Mitral Regurgitation (MR)</td><td>持續性吹拂音（Holosystolic/Pansystolic）</td><td>心尖區（Apex）</td><td>左腋下</td></tr><tr><td>Tricuspid Regurgitation (TR)</td><td>持續性吹拂音（呼吸時變化明顯）</td><td>胸骨左緣第 4-5 肋間</td><td>右胸部或劍突</td></tr><tr><td>Mitral Valve Prolapse (MVP)</td><td>中期收縮期點擊音（Mid-systolic Click）</td><td>心尖區</td><td>無特定輻射</td></tr><tr><td>Ventricular Septal Defect (VSD)</td><td>強烈粗糙的全收縮期雜音</td><td>胸骨左緣第 4-5 肋間</td><td>全胸腔範圍</td></tr></tbody></table><p>重點：</p><h3 id="心尖區雜音-→-多半與僧帽瓣有關" tabindex="-1">心尖區雜音 → 多半與僧帽瓣有關 <a class="header-anchor" href="#心尖區雜音-→-多半與僧帽瓣有關" aria-label="Permalink to &quot;心尖區雜音 → 多半與僧帽瓣有關&quot;">​</a></h3><h3 id="胸骨右上緣-→-主動脈瓣問題" tabindex="-1">胸骨右上緣 → 主動脈瓣問題 <a class="header-anchor" href="#胸骨右上緣-→-主動脈瓣問題" aria-label="Permalink to &quot;胸骨右上緣 → 主動脈瓣問題&quot;">​</a></h3><h3 id="胸骨左下緣-→-三尖瓣問題" tabindex="-1">胸骨左下緣 → 三尖瓣問題 <a class="header-anchor" href="#胸骨左下緣-→-三尖瓣問題" aria-label="Permalink to &quot;胸骨左下緣 → 三尖瓣問題&quot;">​</a></h3><h3 id="胸骨左上緣-→-肺動脈瓣問題" tabindex="-1">胸骨左上緣 → 肺動脈瓣問題 <a class="header-anchor" href="#胸骨左上緣-→-肺動脈瓣問題" aria-label="Permalink to &quot;胸骨左上緣 → 肺動脈瓣問題&quot;">​</a></h3><h2 id="人工心臟瓣膜-prosthetic-heart-valves" tabindex="-1">人工心臟瓣膜（Prosthetic Heart Valves） <a class="header-anchor" href="#人工心臟瓣膜-prosthetic-heart-valves" aria-label="Permalink to &quot;人工心臟瓣膜（Prosthetic Heart Valves）&quot;">​</a></h2><h3 id="人工瓣膜主要分為機械瓣膜-mechanical-valves-與生物瓣膜-bioprosthetic-valves-兩者在耐用性、血栓風險、抗凝需求等方面各有優缺點。" tabindex="-1">人工瓣膜主要分為機械瓣膜（Mechanical Valves）與生物瓣膜（Bioprosthetic Valves），兩者在耐用性、血栓風險、抗凝需求等方面各有優缺點。 <a class="header-anchor" href="#人工瓣膜主要分為機械瓣膜-mechanical-valves-與生物瓣膜-bioprosthetic-valves-兩者在耐用性、血栓風險、抗凝需求等方面各有優缺點。" aria-label="Permalink to &quot;人工瓣膜主要分為機械瓣膜（Mechanical Valves）與生物瓣膜（Bioprosthetic Valves），兩者在耐用性、血栓風險、抗凝需求等方面各有優缺點。&quot;">​</a></h3><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h2 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h2><h2 id="boards-beyonds-heart-sounds-heart-murmur-valvular-heart-disease" tabindex="-1">Boards &amp; Beyonds：Heart sounds，heart murmur，valvular heart disease <a class="header-anchor" href="#boards-beyonds-heart-sounds-heart-murmur-valvular-heart-disease" aria-label="Permalink to &quot;Boards &amp; Beyonds：Heart sounds，heart murmur，valvular heart disease&quot;">​</a></h2><h2 id="first-aid-step-2-ck" tabindex="-1">First aid STEP 2 CK <a class="header-anchor" href="#first-aid-step-2-ck" aria-label="Permalink to &quot;First aid STEP 2 CK&quot;">​</a></h2><table tabindex="0"><thead><tr><th>心音/雜音</th><th>病理意義</th><th>聽診區域</th></tr></thead><tbody><tr><td>Opening Snap</td><td>僧帽瓣狹窄（Mitral Stenosis）</td><td>心尖區</td></tr><tr><td>Ejection Click</td><td>主動脈瓣或肺動脈瓣狹窄、二葉瓣畸形</td><td>胸骨上緣</td></tr><tr><td>Mid-systolic Click</td><td>僧帽瓣脫垂（Mitral Valve Prolapse, MVP）</td><td>心尖區</td></tr><tr><td>Pericardial Knock</td><td>限制型心包炎（Constrictive Pericarditis）</td><td>胸骨左緣</td></tr><tr><td>Friction Rub</td><td>心包炎（Pericarditis）</td><td>胸骨左緣</td></tr></tbody></table><table tabindex="0"><thead><tr><th>等級</th><th>特徵</th></tr></thead><tbody><tr><td>Grade I</td><td>非常微弱，需專心聽診才聽得見</td></tr><tr><td>Grade II</td><td>較清楚但仍屬輕度，容易聽到</td></tr><tr><td>Grade III</td><td>中等強度，無震顫</td></tr><tr><td>Grade IV</td><td>強烈，伴隨可觸摸的震顫（Thrill）</td></tr><tr><td>Grade V</td><td>震顫明顯，輕放聽診器即可聽見</td></tr><tr><td>Grade VI</td><td>無需聽診器緊貼皮膚，也可聽見</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類型</th><th>發生時機</th><th>可能原因</th></tr></thead><tbody><tr><td>Systolic Murmur（收縮期雜音）</td><td>S1 → S2</td><td>瓣膜狹窄、閉鎖不全等</td></tr><tr><td>Diastolic Murmur（舒張期雜音）</td><td>S2 → S1</td><td>瓣膜狹窄或閉鎖不全（通常病理性）</td></tr><tr><td>Continuous Murmur（連續性雜音）</td><td>整個心週期持續出現</td><td>PDA 等先天性心臟病</td></tr></tbody></table><table tabindex="0"><thead><tr><th>疾病</th><th>雜音特徵</th><th>聽診位置</th><th>輻射方向</th></tr></thead><tbody><tr><td>Aortic Regurgitation (AR)</td><td>高頻遞減性吹拂音（Decrescendo Blowing）</td><td>胸骨左緣第 3 肋間</td><td>心尖區（Austin Flint murmur）</td></tr><tr><td>Pulmonary Regurgitation (PR)</td><td>遞減性吹拂音</td><td>胸骨左緣第 2 肋間</td><td>無特定輻射</td></tr><tr><td>Mitral Stenosis (MS)</td><td>低頻隆隆音（Opening Snap 後的 Mid-Diastolic Rumbling）</td><td>心尖區</td><td>無特定輻射</td></tr><tr><td>Tricuspid Stenosis (TS)</td><td>低頻隆隆音</td><td>胸骨左緣第 4-5 肋間</td><td>無特定輻射</td></tr></tbody></table><table tabindex="0"><thead><tr><th>疾病</th><th>雜音特徵</th><th>聽診位置</th></tr></thead><tbody><tr><td>Patent Ductus Arteriosus (PDA)</td><td>機械般的連續性雜音（Machinery Murmur）</td><td>胸骨左緣第 2 肋間</td></tr><tr><td>Arteriovenous Fistula</td><td>低頻連續性雜音</td><td>依位置不同</td></tr><tr><td>Coarctation of the Aorta</td><td>背部可聽到連續性雜音</td><td>肩胛間區</td></tr></tbody></table><table tabindex="0"><thead><tr><th>情境</th><th>生理變化</th><th>影響的雜音</th></tr></thead><tbody><tr><td>吸氣（Inspiration）</td><td>↑ 靜脈回流 → ↑ 右心血流量</td><td>右心雜音變大聲（如 TR、PS、ASD）</td></tr><tr><td>吐氣（Expiration）</td><td>↑ 左心血流量</td><td>左心雜音變大聲（如 MR、MS、AS、AR）</td></tr><tr><td>Valsalva maneuver</td><td>↓ 靜脈回流 → ↓ 心室充血量</td><td>大部分雜音變小聲；但 HCM、MVP 雜音變大聲</td></tr><tr><td>站立（Standing）</td><td>↓ 靜脈回流 → ↓ 心室充血量</td><td>大部分雜音變小聲；但 HCM、MVP 雜音變大聲</td></tr><tr><td>蹲下/抬腳（Squatting/Leg raise）</td><td>↑ 靜脈回流 → ↑ 心室充血量</td><td>大部分雜音變大聲；但 HCM、MVP 雜音變小聲</td></tr><tr><td>握拳運動（Handgrip exercise）</td><td>↑ 周邊阻力（↑ Afterload）→ ↑ 左心壓力</td><td>MR、AR、VSD 雜音變大聲；HCM 雜音變小聲</td></tr></tbody></table><table tabindex="0"><thead><tr><th>操作</th><th>大部分雜音</th><th>HCM 雜音</th><th>MVP 雜音</th></tr></thead><tbody><tr><td>吸氣</td><td>右心雜音↑</td><td>-</td><td>-</td></tr><tr><td>吐氣</td><td>左心雜音↑</td><td>-</td><td>-</td></tr><tr><td>Valsalva</td><td>↓</td><td>↑</td><td>↑</td></tr><tr><td>站立</td><td>↓</td><td>↑</td><td>↑</td></tr><tr><td>蹲下/抬腳</td><td>↑</td><td>↓</td><td>↓</td></tr><tr><td>握拳運動（Handgrip）</td><td>↑（左心雜音）</td><td>↓</td><td>-</td></tr></tbody></table><table tabindex="0"><thead><tr><th>脈搏名稱</th><th>可見特徵</th><th>生理/病理意義</th><th>代表疾病</th></tr></thead><tbody><tr><td>Normal Pulse</td><td>上升快速 → 最高點 → 平緩下降</td><td>正常血流動力學</td><td></td></tr><tr><td>Pulsus Tardus</td><td>動脈搏動延遲且緩慢上升</td><td>收縮期阻力增加 → 血流受阻</td><td>Aortic Stenosis（AS）</td></tr><tr><td>Pulsus Parvus</td><td>動脈搏動振幅減少（amplitude↓）</td><td>心輸出量減少</td><td>Aortic Stenosis（AS）</td></tr><tr><td>Pulsus Parvus et Tardus</td><td>振幅減少+延遲上升</td><td>重度 Aortic Stenosis</td><td>Severe AS</td></tr><tr><td>Pulsus Bisferiens</td><td>收縮期出現雙波峰（兩個脈衝）</td><td>快速射血後，再次湧現血流</td><td>Aortic Regurgitation（AR）、Hypertrophic Cardiomyopathy（HCM）</td></tr><tr><td>Bounding Pulse</td><td>動脈搏動快速上升且振幅大（跳動感強）</td><td>Stroke Volume↑ 或周邊血管阻力↓</td><td>Aortic Regurgitation（AR）、Patent Ductus Arteriosus（PDA）</td></tr><tr><td>Water-Hammer Pulse/Corrigan&#39;s pulse</td><td>動脈搏動快速上升後快速下降（急速下墜感）</td><td>大量血液回流 → 快速壓力釋放</td><td>Aortic Regurgitation（AR）</td></tr><tr><td>Pulsus Paradoxus</td><td>吸氣時收縮壓下降超過 10 mmHg</td><td>心包壓迫或右心負荷增加</td><td>心包填塞（Cardiac Tamponade）張力性氣胸（Tension Pneumothorax）嚴重哮喘或慢性阻塞性肺病（COPD）</td></tr><tr><td>Pulsus Alternans</td><td>脈搏節律規則，但強弱交替變化（一強一弱）</td><td>左心室功能嚴重受損 → 射出率不穩定</td><td>Left Heart Failure（心衰竭）</td></tr><tr><td>Dicrotic Pulse</td><td>舒張期出現第二波脈衝（低壓時反彈）</td><td>外周阻力低或心搏出量減少</td><td>Sepsis、Severe Heart Failure</td></tr><tr><td>Anacrotic Pulse</td><td>上升緩慢，伴隨收縮期波頂前的小凹陷（notch）</td><td>血流受阻且左心室收縮力降低</td><td>Severe Aortic Stenosis</td></tr><tr><td>Collapsing Pulse</td><td>快速上升且急速下降（類似 Water-Hammer）</td><td>大量回流 → 壓力迅速釋放</td><td>Aortic Regurgitation（AR）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>Wave/Descent</th><th>成因</th><th>心臟週期階段</th><th>臨床意義</th></tr></thead><tbody><tr><td>a wave</td><td>右心房收縮 → 血液推向右心室</td><td>心房收縮期 (Atrial systole)</td><td>- 增大：肺高壓、三尖瓣狹窄（TS）、右心室肥厚 - 消失：心房顫動（Af）</td></tr><tr><td>c wave</td><td>三尖瓣隆起（因右心室等容收縮）</td><td>等容收縮期 (Isovolumetric contraction)</td><td>- 輕微波動，部分受頸動脈搏動干擾</td></tr><tr><td>x descent</td><td>心室收縮期間右心房放鬆與下移</td><td>心室收縮期 (Ventricular systole)</td><td>- 加深：心包填塞時變淺 - 正常情況下為深谷</td></tr><tr><td>v wave</td><td>三尖瓣關閉時，靜脈血液回流累積於右心房</td><td>心室後期收縮期 (Late systole)</td><td>- 增大：三尖瓣閉鎖不全（TR）</td></tr><tr><td>y descent</td><td>三尖瓣打開 → 血液流向右心室</td><td>心室舒張早期 (Early diastole)</td><td>- 快速下降：縮窄性心包炎（Constrictive Pericarditis） - 緩慢下降：心包填塞（Cardiac Tamponade）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>疾病</th><th>心雜音類型</th><th>病因</th><th>治療方式</th></tr></thead><tbody><tr><td>Aortic Stenosis</td><td>收縮期 Crescendo-Decrescendo</td><td>鈣化退化、二葉瓣、風濕性</td><td>TAVR/SAVR</td></tr><tr><td>Aortic Regurgitation</td><td>舒張期遞減型雜音</td><td>馬凡氏症、內膜炎、升主動脈擴張</td><td>血管擴張劑、瓣膜置換</td></tr><tr><td>Mitral Stenosis</td><td>舒張期隆隆音 + Opening Snap</td><td>風濕性心臟病</td><td>PBMV/瓣膜置換</td></tr><tr><td>Mitral Regurgitation</td><td>全收縮期雜音（向腋下放射）</td><td>MVP、乳頭肌功能障礙、內膜炎</td><td>瓣膜修補/置換</td></tr></tbody></table><table tabindex="0"><thead><tr><th>特性</th><th>機械瓣膜</th><th>生物瓣膜</th></tr></thead><tbody><tr><td>耐用性</td><td>20–30 年</td><td>10–15 年</td></tr><tr><td>血栓風險</td><td>高（需終生抗凝）</td><td>低（大多不需長期抗凝）</td></tr><tr><td>抗凝治療</td><td>終生需使用Warfarin（INR 2.0–3.5）</td><td>3–6 個月 Warfarin，之後 Aspirin</td></tr><tr><td>適用族群</td><td>&lt;50 歲，需長期抗凝者</td><td>&gt;65 歲，無法長期使用抗凝者</td></tr><tr><td>再手術風險</td><td>低</td><td>高</td></tr></tbody></table>',147)]))}const m=a(l,[["render",d]]);export{b as __pageData,m as default};
