import{_ as e,c as t,o as r,ae as i}from"./chunks/framework.BtEkjhVd.js";const f=JSON.parse('{"title":"發燒","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-發燒-23.md","filePath":"InternalMedicineDepartment-發燒-23.md"}'),l={name:"InternalMedicineDepartment-發燒-23.md"};function d(n,a,h,o,s,c){return r(),t("div",null,a[0]||(a[0]=[i('<h1 id="發燒" tabindex="-1">發燒 <a class="header-anchor" href="#發燒" aria-label="Permalink to &quot;發燒&quot;">​</a></h1><h2 id="定義" tabindex="-1">定義 <a class="header-anchor" href="#定義" aria-label="Permalink to &quot;定義&quot;">​</a></h2><h3 id="體溫" tabindex="-1">體溫 <a class="header-anchor" href="#體溫" aria-label="Permalink to &quot;體溫&quot;">​</a></h3><h4 id="早上-37-2度" tabindex="-1">早上:&gt;37.2度 <a class="header-anchor" href="#早上-37-2度" aria-label="Permalink to &quot;早上:&gt;37.2度&quot;">​</a></h4><p>晚上:&gt;37.7度</p><h3 id="惡性高熱hyperpyrexia" tabindex="-1">惡性高熱Hyperpyrexia <a class="header-anchor" href="#惡性高熱hyperpyrexia" aria-label="Permalink to &quot;惡性高熱Hyperpyrexia&quot;">​</a></h3><h4 id="體溫-41-5度" tabindex="-1">體溫&gt;41.5度 <a class="header-anchor" href="#體溫-41-5度" aria-label="Permalink to &quot;體溫&gt;41.5度&quot;">​</a></h4><ul><li>常見原因:中樞神經出血、感染症</li></ul><h3 id="高體溫症候群hyperthermia" tabindex="-1">高體溫症候群Hyperthermia <a class="header-anchor" href="#高體溫症候群hyperthermia" aria-label="Permalink to &quot;高體溫症候群Hyperthermia&quot;">​</a></h3><h4 id="體溫調節受損導致體溫過高" tabindex="-1">體溫調節受損導致體溫過高 <a class="header-anchor" href="#體溫調節受損導致體溫過高" aria-label="Permalink to &quot;體溫調節受損導致體溫過高&quot;">​</a></h4><h3 id="fever-of-unknown-origin-fuo" tabindex="-1">Fever of unknown origin(FUO): <a class="header-anchor" href="#fever-of-unknown-origin-fuo" aria-label="Permalink to &quot;Fever of unknown origin(FUO):&quot;">​</a></h3><h4 id="疾病過程-3週、兩個處所以上-體溫-38-3度。經過實驗、影像、免疫檢查皆無法找出病因。" tabindex="-1">疾病過程&gt;3週、兩個處所以上，體溫&gt;38.3度。經過實驗、影像、免疫檢查皆無法找出病因。 <a class="header-anchor" href="#疾病過程-3週、兩個處所以上-體溫-38-3度。經過實驗、影像、免疫檢查皆無法找出病因。" aria-label="Permalink to &quot;疾病過程&gt;3週、兩個處所以上，體溫&gt;38.3度。經過實驗、影像、免疫檢查皆無法找出病因。&quot;">​</a></h4><h2 id="診斷" tabindex="-1">診斷 <a class="header-anchor" href="#診斷" aria-label="Permalink to &quot;診斷&quot;">​</a></h2><h3 id="流程" tabindex="-1">流程 <a class="header-anchor" href="#流程" aria-label="Permalink to &quot;流程&quot;">​</a></h3><h4 id="approach-病人之前-先研究一下病人病歷是否有以下過去病史或易發燒條件" tabindex="-1">Approach 病人之前，先研究一下病人病歷是否有以下過去病史或易發燒條件: <a class="header-anchor" href="#approach-病人之前-先研究一下病人病歷是否有以下過去病史或易發燒條件" aria-label="Permalink to &quot;Approach 病人之前，先研究一下病人病歷是否有以下過去病史或易發燒條件:&quot;">​</a></h4><ul><li>高齡</li><li>Neutropenia</li><li>使用類固醇/免疫抑制劑/化療</li><li>謝葉</li><li>血液疾病</li><li>糖尿病</li><li>血液透析</li><li>癌症</li><li>人工植入物</li></ul><h4 id="基本vital-sign、藥物使、過敏史-也可以先翻病例" tabindex="-1">基本vital sign、藥物使、過敏史...也可以先翻病例 <a class="header-anchor" href="#基本vital-sign、藥物使、過敏史-也可以先翻病例" aria-label="Permalink to &quot;基本vital sign、藥物使、過敏史...也可以先翻病例&quot;">​</a></h4><h4 id="lqqopera問診" tabindex="-1">LQQOPERA問診 <a class="header-anchor" href="#lqqopera問診" aria-label="Permalink to &quot;LQQOPERA問診&quot;">​</a></h4><ul><li>問診方法可以參考凡鳥OSCE的內容</li><li>這邊是要列出可能需要考慮的鑑別診斷:</li></ul><h4 id="住院已久的病人須注意" tabindex="-1">住院已久的病人須注意: <a class="header-anchor" href="#住院已久的病人須注意" aria-label="Permalink to &quot;住院已久的病人須注意:&quot;">​</a></h4><ul><li>褥瘡</li><li>偽膜性腸炎(抗生素用很久的)</li><li>DVT</li></ul><h4 id="非感染性疾病發燒" tabindex="-1">非感染性疾病發燒 <a class="header-anchor" href="#非感染性疾病發燒" aria-label="Permalink to &quot;非感染性疾病發燒&quot;">​</a></h4><ul><li>甲狀腺風暴</li><li>肺栓塞、ARDS</li><li>AMI</li><li>手術後</li><li>風濕免疫 <ul><li>痛風</li><li>血管炎</li><li>Still&#39;s disease</li></ul></li><li>腫瘤熱</li></ul><h4 id="以上僅列出部分常見-非全部鑑別可能。" tabindex="-1">*以上僅列出部分常見，非全部鑑別可能。 <a class="header-anchor" href="#以上僅列出部分常見-非全部鑑別可能。" aria-label="Permalink to &quot;*以上僅列出部分常見，非全部鑑別可能。&quot;">​</a></h4><h2 id="治療" tabindex="-1">治療 <a class="header-anchor" href="#治療" aria-label="Permalink to &quot;治療&quot;">​</a></h2><h3 id="fever治療" tabindex="-1">Fever治療: <a class="header-anchor" href="#fever治療" aria-label="Permalink to &quot;Fever治療:&quot;">​</a></h3><h4 id="退燒藥" tabindex="-1">退燒藥 <a class="header-anchor" href="#退燒藥" aria-label="Permalink to &quot;退燒藥&quot;">​</a></h4><h2 id="參考資料" tabindex="-1">參考資料: <a class="header-anchor" href="#參考資料" aria-label="Permalink to &quot;參考資料:&quot;">​</a></h2><h2 id="harrison-s" tabindex="-1">Harrison&#39;s <a class="header-anchor" href="#harrison-s" aria-label="Permalink to &quot;Harrison&#39;s&quot;">​</a></h2><h2 id="https-www-researchgate-net-figure-268878020-fig4-figure-4-clinical-fever-patterns-clinical-fever-patterns-and-examples-of-diseases-that" tabindex="-1"><a href="https://www.researchgate.net/figure/268878020_fig4_Figure-4-Clinical-fever-patterns-Clinical-fever-patterns-and-examples-of-diseases-that" target="_blank" rel="noreferrer">https://www.researchgate.net/figure/268878020_fig4_Figure-4-Clinical-fever-patterns-Clinical-fever-patterns-and-examples-of-diseases-that</a> <a class="header-anchor" href="#https-www-researchgate-net-figure-268878020-fig4-figure-4-clinical-fever-patterns-clinical-fever-patterns-and-examples-of-diseases-that" aria-label="Permalink to &quot;https://www.researchgate.net/figure/268878020_fig4_Figure-4-Clinical-fever-patterns-Clinical-fever-patterns-and-examples-of-diseases-that&quot;">​</a></h2><table tabindex="0"><thead><tr><th>可能病因</th><th>發燒pattern</th><th>病程</th><th>問診內容</th><th>身體檢查</th><th>檢查安排/治療</th></tr></thead><tbody><tr><td>細菌、病毒感染</td><td>Continuous</td><td>持續高燒，但波動不超過 1°C。</td><td>最近的症狀（咳嗽、喉嚨痛等），接觸史，疫苗接種情況。</td><td>全身檢查：呼吸音、咽喉檢查等。</td><td>血液檢查（CBC、CRP）、胸部X光。抗生素或抗病毒治療。</td></tr><tr><td>心內膜炎、病毒感染</td><td>Remittent (弛張性發燒)</td><td>高燒，但體溫波動超過 1°C 且不恢復至正常體溫。</td><td>發燒時間長短，出現症狀的頻率，心臟病史。</td><td>心臟檢查、聽診、皮膚檢查。</td><td>血液培養、心臟超音波。抗生素治療。</td></tr><tr><td>TB、JIA</td><td>Intermittent (間歇性發燒)</td><td>體溫週期性升高，間隔恢復至正常體溫。</td><td>發燒週期，是否夜間發作，過去的感染或旅行史。</td><td>淋巴結檢查、肝脾檢查。</td><td>血液檢查、痰液檢查、X光或CT。抗生素或抗結核治療。</td></tr><tr><td>布氏桿菌</td><td>Undulant (波狀性發燒)</td><td>體溫緩慢波動，有高燒與正常體溫之間的變化。</td><td>職業（如農場工作）、接觸動物或未消毒的乳製品。</td><td>關節檢查、肝脾檢查。</td><td>血清檢測（布魯氏菌抗體）。長期抗生素治療。</td></tr><tr><td>登革熱</td><td>Biphasic (雙峰性發燒)</td><td>體溫分為兩個高峰期，期間有恢復或接近正常體溫的間隔。</td><td>蚊蟲叮咬史、旅行史，症狀如關節痛、肌肉痛。</td><td>全身檢查，尤其是皮疹與淋巴結。</td><td>血液檢查、Dengue NS1抗原檢測。支持性療法。</td></tr><tr><td>霍金氏淋巴瘤、蜱媒疾病</td><td>Recurrent (復發性發燒)</td><td>發燒會有間歇性地重複出現，可能間隔數天、數週甚至數年。</td><td>發燒發作的頻率、持續時間、動物或蟲咬接觸史。</td><td>淋巴結檢查、脾臟檢查。</td><td>血液培養、影像檢查（CT、PET）。抗生素或化療。</td></tr></tbody></table>',31)]))}const p=e(l,[["render",d]]);export{f as __pageData,p as default};
