import{_ as a,c as e,o as d,ae as r}from"./chunks/framework.Dgg8-8ov.js";const u=JSON.parse('{"title":"高血壓","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-高血壓-13.md","filePath":"InternalMedicineDepartment-高血壓-13.md"}'),i={name:"InternalMedicineDepartment-高血壓-13.md"};function h(o,t,l,n,s,c){return d(),e("div",null,t[0]||(t[0]=[r('<h1 id="高血壓" tabindex="-1">高血壓 <a class="header-anchor" href="#高血壓" aria-label="Permalink to &quot;高血壓&quot;">​</a></h1><h2 id="流行病學-epidemiology" tabindex="-1">流行病學 (Epidemiology) <a class="header-anchor" href="#流行病學-epidemiology" aria-label="Permalink to &quot;流行病學 (Epidemiology)&quot;">​</a></h2><h3 id="全球影響" tabindex="-1">全球影響： <a class="header-anchor" href="#全球影響" aria-label="Permalink to &quot;全球影響：&quot;">​</a></h3><h4 id="高血壓是全球最常見的慢性疾病之一-影響超過13億人。" tabindex="-1">高血壓是全球最常見的慢性疾病之一，影響超過13億人。 <a class="header-anchor" href="#高血壓是全球最常見的慢性疾病之一-影響超過13億人。" aria-label="Permalink to &quot;高血壓是全球最常見的慢性疾病之一，影響超過13億人。&quot;">​</a></h4><h4 id="是心血管疾病、腦中風、慢性腎病的主要危險因子。" tabindex="-1">是心血管疾病、腦中風、慢性腎病的主要危險因子。 <a class="header-anchor" href="#是心血管疾病、腦中風、慢性腎病的主要危險因子。" aria-label="Permalink to &quot;是心血管疾病、腦中風、慢性腎病的主要危險因子。&quot;">​</a></h4><h4 id="發病率隨年齡上升-60-歲以上族群約-50-以上罹患高血壓。" tabindex="-1">發病率隨年齡上升，60 歲以上族群約 50% 以上罹患高血壓。 <a class="header-anchor" href="#發病率隨年齡上升-60-歲以上族群約-50-以上罹患高血壓。" aria-label="Permalink to &quot;發病率隨年齡上升，60 歲以上族群約 50% 以上罹患高血壓。&quot;">​</a></h4><h3 id="台灣現況" tabindex="-1">台灣現況： <a class="header-anchor" href="#台灣現況" aria-label="Permalink to &quot;台灣現況：&quot;">​</a></h3><h4 id="根據國民健康署統計-台灣20歲以上人口約-25-罹患高血壓。" tabindex="-1">根據國民健康署統計，台灣20歲以上人口約 25% 罹患高血壓。 <a class="header-anchor" href="#根據國民健康署統計-台灣20歲以上人口約-25-罹患高血壓。" aria-label="Permalink to &quot;根據國民健康署統計，台灣20歲以上人口約 25% 罹患高血壓。&quot;">​</a></h4><h4 id="老年族群-65-歲-高血壓盛行率達-70-以上。" tabindex="-1">老年族群 (&gt;65 歲) 高血壓盛行率達 70% 以上。 <a class="header-anchor" href="#老年族群-65-歲-高血壓盛行率達-70-以上。" aria-label="Permalink to &quot;老年族群 (&gt;65 歲) 高血壓盛行率達 70% 以上。&quot;">​</a></h4><h4 id="控制率低-約-1-3-患者血壓未達標準。" tabindex="-1">控制率低，約 1/3 患者血壓未達標準。 <a class="header-anchor" href="#控制率低-約-1-3-患者血壓未達標準。" aria-label="Permalink to &quot;控制率低，約 1/3 患者血壓未達標準。&quot;">​</a></h4><h2 id="依據-2022-台灣高血壓指引-診斷高血壓需多次量測血壓-確認數值" tabindex="-1">依據 2022 台灣高血壓指引，診斷高血壓需多次量測血壓，確認數值： <a class="header-anchor" href="#依據-2022-台灣高血壓指引-診斷高血壓需多次量測血壓-確認數值" aria-label="Permalink to &quot;依據 2022 台灣高血壓指引，診斷高血壓需多次量測血壓，確認數值：&quot;">​</a></h2><h2 id="典型症狀" tabindex="-1">典型症狀 <a class="header-anchor" href="#典型症狀" aria-label="Permalink to &quot;典型症狀&quot;">​</a></h2><h3 id="早期無症狀-因此高血壓常被稱為-「沉默的殺手-silent-killer-」。" tabindex="-1">早期無症狀，因此高血壓常被稱為 「沉默的殺手 (Silent Killer)」。 <a class="header-anchor" href="#早期無症狀-因此高血壓常被稱為-「沉默的殺手-silent-killer-」。" aria-label="Permalink to &quot;早期無症狀，因此高血壓常被稱為 「沉默的殺手 (Silent Killer)」。&quot;">​</a></h3><h3 id="症狀出現時通常已影響器官-可能表現為-頭痛、頭暈、耳鳴、視力模糊-視網膜血管受損-、心悸、胸悶-心血管受影響-、下肢水腫-腎臟受影響-。" tabindex="-1">症狀出現時通常已影響器官，可能表現為：頭痛、頭暈、耳鳴、視力模糊 (視網膜血管受損)、心悸、胸悶 (心血管受影響)、下肢水腫 (腎臟受影響)。 <a class="header-anchor" href="#症狀出現時通常已影響器官-可能表現為-頭痛、頭暈、耳鳴、視力模糊-視網膜血管受損-、心悸、胸悶-心血管受影響-、下肢水腫-腎臟受影響-。" aria-label="Permalink to &quot;症狀出現時通常已影響器官，可能表現為：頭痛、頭暈、耳鳴、視力模糊 (視網膜血管受損)、心悸、胸悶 (心血管受影響)、下肢水腫 (腎臟受影響)。&quot;">​</a></h3><h2 id="病生理-pathophysiology" tabindex="-1">病生理 (Pathophysiology) <a class="header-anchor" href="#病生理-pathophysiology" aria-label="Permalink to &quot;病生理 (Pathophysiology)&quot;">​</a></h2><h3 id="血壓調節機制" tabindex="-1">血壓調節機制 <a class="header-anchor" href="#血壓調節機制" aria-label="Permalink to &quot;血壓調節機制&quot;">​</a></h3><h4 id="血壓受-心輸出量-co-與總周邊阻力-tpr-調控-影響因素包括" tabindex="-1">血壓受 心輸出量 (CO) 與總周邊阻力 (TPR) 調控，影響因素包括： <a class="header-anchor" href="#血壓受-心輸出量-co-與總周邊阻力-tpr-調控-影響因素包括" aria-label="Permalink to &quot;血壓受 心輸出量 (CO) 與總周邊阻力 (TPR) 調控，影響因素包括：&quot;">​</a></h4><ul><li>交感神經系統：活化後心率加快、血管收縮，導致血壓上升。</li><li>腎素-血管收縮素-醛固酮系統 (RAAS)： <ul><li>腎素 (Renin) 促進血管收縮素 II (Angiotensin II) 生成，導致血管收縮。</li><li>醛固酮 (Aldosterone) 促進鈉水滯留，提高血容量。</li></ul></li><li>內皮功能 (Endothelial Dysfunction)： <ul><li>NO (一氧化氮) 減少，降低血管舒張能力。</li><li>內皮素-1 (ET-1) 增加，促進血管收縮。</li></ul></li></ul><h2 id="病因分類" tabindex="-1">病因分類 <a class="header-anchor" href="#病因分類" aria-label="Permalink to &quot;病因分類&quot;">​</a></h2><h3 id="原發性高血壓-essential-htn" tabindex="-1">原發性高血壓 (Essential HTN） <a class="header-anchor" href="#原發性高血壓-essential-htn" aria-label="Permalink to &quot;原發性高血壓 (Essential HTN）&quot;">​</a></h3><h4 id="佔95-常發生於25-55歲-病因不明-與多基因遺傳與環境因素相關。" tabindex="-1">佔95%，常發生於25-55歲，病因不明，與多基因遺傳與環境因素相關。 <a class="header-anchor" href="#佔95-常發生於25-55歲-病因不明-與多基因遺傳與環境因素相關。" aria-label="Permalink to &quot;佔95%，常發生於25-55歲，病因不明，與多基因遺傳與環境因素相關。&quot;">​</a></h4><h4 id="主要危險因子" tabindex="-1">主要危險因子： <a class="header-anchor" href="#主要危險因子" aria-label="Permalink to &quot;主要危險因子：&quot;">​</a></h4><ul><li>家族史 (遺傳因素)</li><li>年齡增加 (血管硬化、彈性降低)</li><li>生活習慣 (高鈉飲食、久坐、肥胖、吸菸、過量飲酒)</li></ul><h3 id="次發性高血壓-secondary-htn" tabindex="-1">次發性高血壓（Secondary HTN） <a class="header-anchor" href="#次發性高血壓-secondary-htn" aria-label="Permalink to &quot;次發性高血壓（Secondary HTN）&quot;">​</a></h3><h4 id="約-5-10-的高血壓屬於次發性-有明確的病因-若治療病因可逆轉高血壓。通常發生於年輕-20-歲-或老年-50-歲-且血壓極高者-應懷疑次發性高血壓。" tabindex="-1">約 5-10% 的高血壓屬於次發性，有明確的病因，若治療病因可逆轉高血壓。通常發生於年輕（&lt; 20 歲）或老年（&gt; 50 歲）且血壓極高者，應懷疑次發性高血壓。 <a class="header-anchor" href="#約-5-10-的高血壓屬於次發性-有明確的病因-若治療病因可逆轉高血壓。通常發生於年輕-20-歲-或老年-50-歲-且血壓極高者-應懷疑次發性高血壓。" aria-label="Permalink to &quot;約 5-10% 的高血壓屬於次發性，有明確的病因，若治療病因可逆轉高血壓。通常發生於年輕（&lt; 20 歲）或老年（&gt; 50 歲）且血壓極高者，應懷疑次發性高血壓。&quot;">​</a></h4><h3 id="次發性高血壓主要病因" tabindex="-1">次發性高血壓主要病因： <a class="header-anchor" href="#次發性高血壓主要病因" aria-label="Permalink to &quot;次發性高血壓主要病因：&quot;">​</a></h3><h2 id="特殊型高血壓" tabindex="-1">特殊型高血壓 <a class="header-anchor" href="#特殊型高血壓" aria-label="Permalink to &quot;特殊型高血壓&quot;">​</a></h2><h3 id="隱藏性高血壓-masked-hypertension" tabindex="-1">隱藏性高血壓（Masked Hypertension） <a class="header-anchor" href="#隱藏性高血壓-masked-hypertension" aria-label="Permalink to &quot;隱藏性高血壓（Masked Hypertension）&quot;">​</a></h3><h4 id="診間血壓正常-但居家血壓-偏高-135-85-mmhg-。" tabindex="-1">診間血壓正常，但居家血壓 偏高（&gt;135/85 mmHg）。 <a class="header-anchor" href="#診間血壓正常-但居家血壓-偏高-135-85-mmhg-。" aria-label="Permalink to &quot;診間血壓正常，但居家血壓 偏高（&gt;135/85 mmHg）。&quot;">​</a></h4><h4 id="常見於年輕族群、壓力大者、糖尿病患者。" tabindex="-1">常見於年輕族群、壓力大者、糖尿病患者。 <a class="header-anchor" href="#常見於年輕族群、壓力大者、糖尿病患者。" aria-label="Permalink to &quot;常見於年輕族群、壓力大者、糖尿病患者。&quot;">​</a></h4><h4 id="需透過居家血壓監測或-24-小時血壓監測診斷。" tabindex="-1">需透過居家血壓監測或 24 小時血壓監測診斷。 <a class="header-anchor" href="#需透過居家血壓監測或-24-小時血壓監測診斷。" aria-label="Permalink to &quot;需透過居家血壓監測或 24 小時血壓監測診斷。&quot;">​</a></h4><h3 id="白袍高血壓-white-coat-hypertension" tabindex="-1">白袍高血壓（White Coat Hypertension） <a class="header-anchor" href="#白袍高血壓-white-coat-hypertension" aria-label="Permalink to &quot;白袍高血壓（White Coat Hypertension）&quot;">​</a></h3><h4 id="診間血壓升高-但居家血壓正常。" tabindex="-1">診間血壓升高，但居家血壓正常。 <a class="header-anchor" href="#診間血壓升高-但居家血壓正常。" aria-label="Permalink to &quot;診間血壓升高，但居家血壓正常。&quot;">​</a></h4><h4 id="可能與焦慮、交感神經亢進有關。" tabindex="-1">可能與焦慮、交感神經亢進有關。 <a class="header-anchor" href="#可能與焦慮、交感神經亢進有關。" aria-label="Permalink to &quot;可能與焦慮、交感神經亢進有關。&quot;">​</a></h4><h4 id="若無其他心血管風險-可觀察追蹤-不一定要用藥。" tabindex="-1">若無其他心血管風險，可觀察追蹤，不一定要用藥。 <a class="header-anchor" href="#若無其他心血管風險-可觀察追蹤-不一定要用藥。" aria-label="Permalink to &quot;若無其他心血管風險，可觀察追蹤，不一定要用藥。&quot;">​</a></h4><h3 id="抵抗性高血壓-resistant-hypertension" tabindex="-1">抵抗性高血壓（Resistant Hypertension） <a class="header-anchor" href="#抵抗性高血壓-resistant-hypertension" aria-label="Permalink to &quot;抵抗性高血壓（Resistant Hypertension）&quot;">​</a></h3><h4 id="使用3種以上降壓藥-包含利尿劑-血壓仍未達標-140-90-mmhg-。" tabindex="-1">使用3種以上降壓藥（包含利尿劑），血壓仍未達標（&gt; 140/90 mmHg）。 <a class="header-anchor" href="#使用3種以上降壓藥-包含利尿劑-血壓仍未達標-140-90-mmhg-。" aria-label="Permalink to &quot;使用3種以上降壓藥（包含利尿劑），血壓仍未達標（&gt; 140/90 mmHg）。&quot;">​</a></h4><h4 id="需排除假性高血壓-如測量錯誤、依從性差-並考慮次發性高血壓。" tabindex="-1">需排除假性高血壓（如測量錯誤、依從性差），並考慮次發性高血壓。 <a class="header-anchor" href="#需排除假性高血壓-如測量錯誤、依從性差-並考慮次發性高血壓。" aria-label="Permalink to &quot;需排除假性高血壓（如測量錯誤、依從性差），並考慮次發性高血壓。&quot;">​</a></h4><h2 id="病生理-pathophysiology-1" tabindex="-1">病生理 (Pathophysiology) <a class="header-anchor" href="#病生理-pathophysiology-1" aria-label="Permalink to &quot;病生理 (Pathophysiology)&quot;">​</a></h2><h3 id="血壓調節機制-1" tabindex="-1">血壓調節機制 <a class="header-anchor" href="#血壓調節機制-1" aria-label="Permalink to &quot;血壓調節機制&quot;">​</a></h3><h4 id="血壓受心輸出量-co-與-總周邊阻力-tpr-調控-影響因素包括" tabindex="-1">血壓受心輸出量 (CO) 與 總周邊阻力 (TPR) 調控，影響因素包括： <a class="header-anchor" href="#血壓受心輸出量-co-與-總周邊阻力-tpr-調控-影響因素包括" aria-label="Permalink to &quot;血壓受心輸出量 (CO) 與 總周邊阻力 (TPR) 調控，影響因素包括：&quot;">​</a></h4><ul><li>交感神經系統：活化後心率加快、血管收縮，導致血壓上升。</li><li>腎素-血管收縮素-醛固酮系統 (RAAS)： <ul><li>腎素 (Renin) 促進血管收縮素 II (Angiotensin II) 生成，導致血管收縮。</li><li>醛固酮 (Aldosterone) 促進鈉水滯留，提高血容量。</li></ul></li><li>內皮功能 (Endothelial Dysfunction)： <ul><li>NO (一氧化氮) 減少，降低血管舒張能力。</li><li>內皮素-1 (ET-1) 增加，促進血管收縮。</li></ul></li></ul><h2 id="高血壓對各器官的併發症與機制" tabindex="-1">高血壓對各器官的併發症與機制 <a class="header-anchor" href="#高血壓對各器官的併發症與機制" aria-label="Permalink to &quot;高血壓對各器官的併發症與機制&quot;">​</a></h2><h2 id="高血壓危象-hypertensive-crisis" tabindex="-1">高血壓危象（Hypertensive Crisis） <a class="header-anchor" href="#高血壓危象-hypertensive-crisis" aria-label="Permalink to &quot;高血壓危象（Hypertensive Crisis）&quot;">​</a></h2><h2 id="高血壓治療目標" tabindex="-1">高血壓治療目標 <a class="header-anchor" href="#高血壓治療目標" aria-label="Permalink to &quot;高血壓治療目標&quot;">​</a></h2><h2 id="老年人降壓需謹慎-避免過度降壓導致低血壓或跌倒。" tabindex="-1">老年人降壓需謹慎，避免過度降壓導致低血壓或跌倒。 <a class="header-anchor" href="#老年人降壓需謹慎-避免過度降壓導致低血壓或跌倒。" aria-label="Permalink to &quot;老年人降壓需謹慎，避免過度降壓導致低血壓或跌倒。&quot;">​</a></h2><h2 id="高血壓治療策略" tabindex="-1">高血壓治療策略 <a class="header-anchor" href="#高血壓治療策略" aria-label="Permalink to &quot;高血壓治療策略&quot;">​</a></h2><h3 id="生活型態調整-適用所有高血壓患者" tabindex="-1">生活型態調整（適用所有高血壓患者） <a class="header-anchor" href="#生活型態調整-適用所有高血壓患者" aria-label="Permalink to &quot;生活型態調整（適用所有高血壓患者）&quot;">​</a></h3><h3 id="藥物治療選擇" tabindex="-1">藥物治療選擇 <a class="header-anchor" href="#藥物治療選擇" aria-label="Permalink to &quot;藥物治療選擇&quot;">​</a></h3><h2 id="第一線降壓藥物-適用大部分患者" tabindex="-1">第一線降壓藥物（適用大部分患者）： <a class="header-anchor" href="#第一線降壓藥物-適用大部分患者" aria-label="Permalink to &quot;第一線降壓藥物（適用大部分患者）：&quot;">​</a></h2><h2 id="第二線降壓藥物-合併特殊疾病" tabindex="-1">第二線降壓藥物（合併特殊疾病）： <a class="header-anchor" href="#第二線降壓藥物-合併特殊疾病" aria-label="Permalink to &quot;第二線降壓藥物（合併特殊疾病）：&quot;">​</a></h2><h2 id="藥物合併原則" tabindex="-1">藥物合併原則 <a class="header-anchor" href="#藥物合併原則" aria-label="Permalink to &quot;藥物合併原則&quot;">​</a></h2><h3 id="stage-1-htn-130-139-80-89-→-生活方式調整-單一藥物-acei-arb-ccb-thiazide-。" tabindex="-1">Stage 1 HTN（130-139/80-89） → 生活方式調整 + 單一藥物（ACEI, ARB, CCB, Thiazide）。 <a class="header-anchor" href="#stage-1-htn-130-139-80-89-→-生活方式調整-單一藥物-acei-arb-ccb-thiazide-。" aria-label="Permalink to &quot;Stage 1 HTN（130-139/80-89） → 生活方式調整 + 單一藥物（ACEI, ARB, CCB, Thiazide）。&quot;">​</a></h3><h3 id="stage-2-htn-≥140-90-→-直接合併2種藥物-如-acei-arb-ccb-或-thiazide-。" tabindex="-1">Stage 2 HTN（≥140/90） → 直接合併2種藥物（如 ACEI/ARB + CCB 或 Thiazide）。 <a class="header-anchor" href="#stage-2-htn-≥140-90-→-直接合併2種藥物-如-acei-arb-ccb-或-thiazide-。" aria-label="Permalink to &quot;Stage 2 HTN（≥140/90） → 直接合併2種藥物（如 ACEI/ARB + CCB 或 Thiazide）。&quot;">​</a></h3><h3 id="若目標未達成-≥-130-80-→-增加藥物劑量或加第三種藥物。" tabindex="-1">若目標未達成（≥ 130/80） → 增加藥物劑量或加第三種藥物。 <a class="header-anchor" href="#若目標未達成-≥-130-80-→-增加藥物劑量或加第三種藥物。" aria-label="Permalink to &quot;若目標未達成（≥ 130/80） → 增加藥物劑量或加第三種藥物。&quot;">​</a></h3><h2 id="常見降壓藥物-iv-用藥" tabindex="-1">常見降壓藥物（IV 用藥） <a class="header-anchor" href="#常見降壓藥物-iv-用藥" aria-label="Permalink to &quot;常見降壓藥物（IV 用藥）&quot;">​</a></h2><h2 id="補充考試重點" tabindex="-1">補充考試重點 <a class="header-anchor" href="#補充考試重點" aria-label="Permalink to &quot;補充考試重點&quot;">​</a></h2><h3 id="惡性高血壓-malignant-hypertension-臨床上常用於指合併視乳頭水腫-papilledema-的高血壓危象" tabindex="-1">惡性高血壓（Malignant Hypertension）：臨床上常用於指合併視乳頭水腫（papilledema）的高血壓危象 <a class="header-anchor" href="#惡性高血壓-malignant-hypertension-臨床上常用於指合併視乳頭水腫-papilledema-的高血壓危象" aria-label="Permalink to &quot;惡性高血壓（Malignant Hypertension）：臨床上常用於指合併視乳頭水腫（papilledema）的高血壓危象&quot;">​</a></h3><h3 id="高血壓危象治療關鍵-降壓速度需逐步降低-不可過快-否則會導致器官低灌流-如腦中風、心肌梗塞" tabindex="-1">高血壓危象治療關鍵：降壓速度需逐步降低，不可過快，否則會導致器官低灌流，如腦中風、心肌梗塞 <a class="header-anchor" href="#高血壓危象治療關鍵-降壓速度需逐步降低-不可過快-否則會導致器官低灌流-如腦中風、心肌梗塞" aria-label="Permalink to &quot;高血壓危象治療關鍵：降壓速度需逐步降低，不可過快，否則會導致器官低灌流，如腦中風、心肌梗塞&quot;">​</a></h3><h3 id="視網膜變化-視網膜出血、視乳頭水腫-嚴重時可致盲" tabindex="-1">視網膜變化：視網膜出血、視乳頭水腫，嚴重時可致盲 <a class="header-anchor" href="#視網膜變化-視網膜出血、視乳頭水腫-嚴重時可致盲" aria-label="Permalink to &quot;視網膜變化：視網膜出血、視乳頭水腫，嚴重時可致盲&quot;">​</a></h3><h3 id="神經學表現-高血壓腦病變-hypertensive-encephalopathy-症狀如意識改變、癲癇" tabindex="-1">神經學表現：高血壓腦病變（hypertensive encephalopathy），症狀如意識改變、癲癇 <a class="header-anchor" href="#神經學表現-高血壓腦病變-hypertensive-encephalopathy-症狀如意識改變、癲癇" aria-label="Permalink to &quot;神經學表現：高血壓腦病變（hypertensive encephalopathy），症狀如意識改變、癲癇&quot;">​</a></h3><h3 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h3><h3 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h3><h3 id="boards-beyonds-hypertension" tabindex="-1">Boards &amp; Beyonds：Hypertension <a class="header-anchor" href="#boards-beyonds-hypertension" aria-label="Permalink to &quot;Boards &amp; Beyonds：Hypertension&quot;">​</a></h3><h3 id="心臟學會-2022-taiwan-hypertension-guideline" tabindex="-1">心臟學會 2022 Taiwan hypertension guideline <a class="header-anchor" href="#心臟學會-2022-taiwan-hypertension-guideline" aria-label="Permalink to &quot;心臟學會 2022 Taiwan hypertension guideline&quot;">​</a></h3><h3 id="https-www-tsoc-org-tw-upload-files-2022-taiwan-hypertension-guidelines-pdf" tabindex="-1"><a href="https://www.tsoc.org.tw/upload/files/2022%20Taiwan%20Hypertension%20Guidelines.pdf" target="_blank" rel="noreferrer">https://www.tsoc.org.tw/upload/files/2022 Taiwan Hypertension Guidelines.pdf</a> <a class="header-anchor" href="#https-www-tsoc-org-tw-upload-files-2022-taiwan-hypertension-guidelines-pdf" aria-label="Permalink to &quot;https://www.tsoc.org.tw/upload/files/2022%20Taiwan%20Hypertension%20Guidelines.pdf&quot;">​</a></h3><table tabindex="0"><thead><tr><th>分類</th><th>診間血壓（mmHg）</th><th>居家血壓（mmHg）</th></tr></thead><tbody><tr><td>正常血壓</td><td>&lt; 120 / 80</td><td>&lt; 120 / 80</td></tr><tr><td>高血壓前期（Elevated BP）</td><td>120-129 / &lt; 80</td><td>120-129 / &lt; 80</td></tr><tr><td>第一期高血壓（Stage 1 HTN）</td><td>130-139 / 80-89</td><td>130-134 / 80-84</td></tr><tr><td>第二期高血壓（Stage 2 HTN）</td><td>≥ 140 / 90</td><td>≥ 135 / 85</td></tr><tr><td>重度高血壓（Severe HTN）</td><td>≥ 180 / 110</td><td>-</td></tr></tbody></table><table tabindex="0"><thead><tr><th>病因分類</th><th>疾病</th><th>機制</th><th>診斷方式</th></tr></thead><tbody><tr><td>腎臟疾病（Renal causes）</td><td>腎實質疾病：慢性腎衰竭CKD患者約 80% 會有高血壓</td><td>腎小球濾過率降低，RAAS 活化</td><td>Active Urine Sediment、蛋白尿 &gt;1000 mg/d</td></tr><tr><td></td><td>腎動脈狹窄（Renal artery stenosis）：1. 動脈粥樣硬化（Atherosclerosis）佔 90%2. 纖維肌發育不良（Fibromuscular Dysplasia）佔 10%，好發於年輕女性，雙側（Bilateral），且多影響腎動脈較遠端的區域（Distal Renal Artery）。</td><td>狹窄導致腎臟的灌流壓下降，啟動RAAS，angiotensin II 導致血管收縮</td><td>腎血流掃描（Renal Scintigraphy）、都卜勒超音波、MRA、腎動脈造影（Renal Angiography, Gold Standard）</td></tr><tr><td>內分泌疾病（Endocrine causes）</td><td>原發性醛固酮增多症（Primary Aldosteronism, Conn&#39;s Syndrome）</td><td>醛固酮過多，鈉滯留、鉀流失</td><td>血漿Aldosterone &amp; Renin ratio</td></tr><tr><td></td><td>庫欣氏症（Cushing’s syndrome）</td><td>皮質醇過多，水鈉滯留</td><td>24hr 尿 Cortisol、Dexamethasone suppression test</td></tr><tr><td></td><td>嗜鉻細胞瘤（Pheochromocytoma）</td><td>兒茶酚胺過多，交感神經興奮</td><td>24hr 尿 Metanephrines</td></tr><tr><td></td><td>甲狀腺疾病（Thyroid dysfunction）</td><td>甲狀腺功能異常影響心血管</td><td>TSH, T3, T4</td></tr><tr><td>血管疾病（Vascular causes）</td><td>主動脈縮窄（Coarctation of the aorta）</td><td>主動脈狹窄，導致上半身高血壓</td><td>CXR, CTA, MRI</td></tr><tr><td>神經性高血壓（Neurologic causes）</td><td>顱內壓升高（如腦瘤、腦出血）</td><td>影響交感神經</td><td>腦部影像（CT, MRI）</td></tr><tr><td>藥物與毒物（Drug-induced）</td><td>NSAIDs、類固醇、口服避孕藥、抗憂鬱藥</td><td>影響鈉水滯留或血管收縮</td><td>病史詢問</td></tr><tr><td></td><td>交感神經興奮藥物（Cocaine, 安非他命）</td><td>交感神經興奮</td><td>毒物篩檢</td></tr></tbody></table><table tabindex="0"><thead><tr><th>併發症</th><th>機制與影響</th></tr></thead><tbody><tr><td>中風（Stroke）</td><td>高血壓是缺血性中風（Ischemic stroke） 和出血性中風（Hemorrhagic stroke）的最重要危險因子。- 缺血性中風：動脈硬化導致腦部血管狹窄、血栓形成。- 出血性中風：長期高血壓導致小動脈破裂，特別是在基底核（Basal ganglia）</td></tr><tr><td>心臟病（Heart Disease）</td><td>高血壓增加心臟負擔，造成心肌肥厚、供血不足，引起心臟病。</td></tr><tr><td>心肌梗塞（Myocardial Infarction, MI）</td><td>高血壓加速動脈硬化，導致冠狀動脈阻塞，造成心肌壞死。</td></tr><tr><td>心臟衰竭（Heart Failure, HF）</td><td>高血壓導致左心室肥厚（LVH），心臟無法有效收縮或舒張，造成收縮性或舒張性心衰竭。</td></tr><tr><td>腎衰竭（Renal Failure）</td><td>高血壓損害腎臟小動脈，造成腎絲球硬化（Glomerulosclerosis），影響腎臟功能，導致慢性腎病（CKD） 或 腎衰竭（ESRD）。</td></tr><tr><td>主動脈瘤（Aortic Aneurysm）</td><td>高血壓造成動脈壁壓力升高，導致動脈擴張，特別是腹主動脈瘤（AAA, Abdominal Aortic Aneurysm）。</td></tr><tr><td>主動脈剝離（Aortic Dissection）</td><td>高血壓會加速動脈硬化與彈性降低，當血壓過高時，可能導致主動脈內膜破裂，形成主動脈夾層（Aortic dissection），是致命的急症。</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類別</th><th>定義</th><th>治療策略</th></tr></thead><tbody><tr><td>Urgency</td><td>BP &gt; 180/120, 無器官損害</td><td>口服降壓藥 (CCB, ACEI, ARB)數小時內緩慢降壓，避免過快降壓導致器官低灌流</td></tr><tr><td>Emergency</td><td>BP &gt; 180/120, 合併器官損害</td><td>需立即降壓（IV Nicardipine, Labetalol）靜脈注射降壓藥，2 小時內逐步降低，平均動脈壓（MAP）不超過 25-30%，目標 &lt;160/100 mmHg</td></tr></tbody></table><table tabindex="0"><thead><tr><th>族群</th><th>目標血壓（mmHg）</th></tr></thead><tbody><tr><td>一般成人（&lt; 65 歲）</td><td>&lt; 130/80</td></tr><tr><td>老年人（≥ 65 歲）</td><td>&lt; 140/90（若可耐受可達 &lt; 130/80）</td></tr><tr><td>慢性腎病（CKD）、糖尿病（DM）、冠心病（CAD）</td><td>&lt; 130/80</td></tr><tr><td>高危險族群（已發生中風、心肌梗塞）</td><td>&lt; 130/80</td></tr></tbody></table><table tabindex="0"><thead><tr><th>方式</th><th>降壓效果</th></tr></thead><tbody><tr><td>體重控制</td><td>BMI&lt;25 kg/m2</td></tr><tr><td>DASH 飲食</td><td>低鈉、高鉀、高纖</td></tr><tr><td>鈉攝取控制</td><td>&lt;6 g NaCl/日</td></tr><tr><td>規律運動</td><td>每週 150 分鐘中等強度運動</td></tr><tr><td>限制酒精攝取</td><td>男性 &lt; 2 杯/日，女性 &lt; 1 杯/日</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物類別</th><th>代表藥物</th><th>機制</th><th>適應症</th><th>主要副作用</th></tr></thead><tbody><tr><td>利尿劑(Diuretics)</td><td>Thiazides: Hydrochlorothiazide, Chlorthalidone；Loop: Furosemide</td><td>減少鈉水滯留，降低血容量</td><td>輕中度高血壓，合併心衰、CKD</td><td>低血鉀（Thiazides）、低血鈉、高尿酸</td></tr><tr><td>ACE 抑制劑(ACEi)</td><td>Enalapril, Lisinopril, Ramipril</td><td>抑制 Angiotensin II 生成，降低血管收縮與醛固酮分泌</td><td>糖尿病腎病、心衰、冠心病（CHD）對腎，心血管有保護作用</td><td>乾咳、高血鉀、腎功能惡化（特別是 RAS 狹窄者）</td></tr><tr><td>ARB(Angiotensin II 受體阻斷劑)</td><td>Losartan, Valsartan, Olmesartan</td><td>阻斷 Angiotensin II 受體</td><td>ACEi 無法耐受者，糖尿病腎病</td><td>高血鉀、腎功能惡化（RAS 狹窄）</td></tr><tr><td>鈣通道阻斷劑（CCBs）</td><td>Dihydropyridine: Amlodipine, Nifedipine；Non-DHP: Diltiazem, Verapamil</td><td>DHP: 血管擴張，Non-DHP: 降低心臟收縮力</td><td>DHP: 高血壓，Non-DHP: 心律不整</td><td>DHP: 下肢水腫，Non-DHP: 心搏過慢</td></tr></tbody></table><table tabindex="0"><thead><tr><th>β 阻斷劑（Beta-Blockers）</th><th>Metoprolol, Atenolol, Carvedilol</th><th>降低心輸出量，減少交感神經活性</th><th>心絞痛、心衰、主動脈剝離</th><th>心搏過慢、疲勞、勃起功能障礙</th></tr></thead><tbody><tr><td>α, β 雙重阻斷劑</td><td>Labetalol, Carvedilol</td><td>同時阻斷 α、β 受體，擴張血管</td><td>妊娠期高血壓、主動脈剝離</td><td>低血壓、疲勞、眩暈</td></tr><tr><td>直接血管擴張劑</td><td>Hydralazine, Minoxidil</td><td>直接作用於小動脈</td><td>主要用於頑固性高血壓</td><td>反射性心搏過速，水腫</td></tr><tr><td>醛固酮拮抗劑</td><td>Spironolactone, Eplerenone</td><td>拮抗醛固酮，保鉀利尿</td><td>主要用於耐藥性高血壓</td><td>高血鉀、男性女乳症（Spironolactone）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物</th><th>機制</th><th>適應症</th><th>主要副作用</th></tr></thead><tbody><tr><td>Hydralazine</td><td>動脈擴張劑</td><td>妊娠相關高血壓（如子癇前症）</td><td>低血壓、反射性心跳過快、頭痛、面部潮紅</td></tr><tr><td>Esmolol</td><td>β1 選擇性阻斷劑</td><td>主動脈剝離</td><td>低血壓、心跳過慢、心律不整、呼吸困難</td></tr><tr><td>Nitroprusside</td><td>一氧化氮供應劑，擴張動靜脈</td><td>急性心衰、主動脈剝離，但 慎防氰化物中毒</td><td>氰化物中毒、低血壓、反射性心跳過快</td></tr><tr><td>Labetalol</td><td>α、β 雙重阻斷劑</td><td>急性高血壓，常用於妊娠期</td><td>低血壓、心跳過慢、疲倦、頭暈、呼吸困難</td></tr><tr><td>Clevidipine / Nicardipine</td><td>鈣離子通道阻斷劑（Dihydropyridine 類）</td><td>最常用於高血壓危象，可控性佳</td><td>低血壓、頭暈、反射性心跳過快、浮腫、頭痛</td></tr></tbody></table>',75)]))}const m=a(i,[["render",h]]);export{u as __pageData,m as default};
