import{_ as d,c as e,o as a,ae as r}from"./chunks/framework.Dgg8-8ov.js";const m=JSON.parse('{"title":"高血脂","description":"","frontmatter":{},"headers":[],"relativePath":"InternalMedicineDepartment-高血脂-12.md","filePath":"InternalMedicineDepartment-高血脂-12.md"}'),i={name:"InternalMedicineDepartment-高血脂-12.md"};function o(n,t,s,l,h,p){return a(),e("div",null,t[0]||(t[0]=[r('<h1 id="高血脂" tabindex="-1">高血脂 <a class="header-anchor" href="#高血脂" aria-label="Permalink to &quot;高血脂&quot;">​</a></h1><h2 id="依風險等級的治療策略" tabindex="-1">依風險等級的治療策略 <a class="header-anchor" href="#依風險等級的治療策略" aria-label="Permalink to &quot;依風險等級的治療策略&quot;">​</a></h2><h2 id="reference" tabindex="-1">REFERENCE： <a class="header-anchor" href="#reference" aria-label="Permalink to &quot;REFERENCE：&quot;">​</a></h2><h3 id="harrison-principles-of-internal-medicine-20th-edition" tabindex="-1">HARRISON Principles of Internal Medicine 20th Edition <a class="header-anchor" href="#harrison-principles-of-internal-medicine-20th-edition" aria-label="Permalink to &quot;HARRISON Principles of Internal Medicine 20th Edition&quot;">​</a></h3><h3 id="boards-beyonds-dyslipidemia" tabindex="-1">Boards &amp; Beyonds：Dyslipidemia <a class="header-anchor" href="#boards-beyonds-dyslipidemia" aria-label="Permalink to &quot;Boards &amp; Beyonds：Dyslipidemia&quot;">​</a></h3><h3 id="https-www-tas-org-tw-upload-files-1-s2-0-s0929664622001036-main-pdf" tabindex="-1"><a href="https://www.tas.org.tw/upload/files/1-s2_0-S0929664622001036-main.pdf" target="_blank" rel="noreferrer">https://www.tas.org.tw/upload/files/1-s2_0-S0929664622001036-main.pdf</a> <a class="header-anchor" href="#https-www-tas-org-tw-upload-files-1-s2-0-s0929664622001036-main-pdf" aria-label="Permalink to &quot;https://www.tas.org.tw/upload/files/1-s2_0-S0929664622001036-main.pdf&quot;">​</a></h3><h3 id="https-www-tas-org-tw-upload-files-1-s2-0-s0929664622002157-main-1-pdf" tabindex="-1"><a href="https://www.tas.org.tw/upload/files/1-s2_0-S0929664622002157-main%20(1).pdf" target="_blank" rel="noreferrer">https://www.tas.org.tw/upload/files/1-s2_0-S0929664622002157-main (1).pdf</a> <a class="header-anchor" href="#https-www-tas-org-tw-upload-files-1-s2-0-s0929664622002157-main-1-pdf" aria-label="Permalink to &quot;https://www.tas.org.tw/upload/files/1-s2_0-S0929664622002157-main%20(1).pdf&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Lipoprotein 類型</th><th>主要成分</th><th>功能</th><th>關鍵 Apoprotein</th></tr></thead><tbody><tr><td>Chylomicrons</td><td>TG（90%）</td><td>攜帶飲食脂肪（TG）從腸道到肝臟</td><td>ApoB-48、ApoC-II、ApoE</td></tr><tr><td>VLDL (Very-Low-Density Lipoprotein)</td><td>TG（55%）</td><td>肝臟合成，運輸內源性TG到周邊組織</td><td>ApoB-100、ApoC-II、ApoE</td></tr><tr><td>IDL (Intermediate-Density Lipoprotein)</td><td>TG（40%）</td><td>VLDL 代謝中間產物，可轉換為 LDL</td><td>ApoB-100、ApoE</td></tr><tr><td>LDL (Low-Density Lipoprotein)</td><td>Cholesterol（50%）</td><td>運輸膽固醇至周邊組織</td><td>ApoB-100</td></tr><tr><td>HDL (High-Density Lipoprotein)</td><td>Protein（50%）</td><td>反向膽固醇運輸，將膽固醇帶回肝臟</td><td>ApoA-I、ApoA-II</td></tr></tbody></table><table tabindex="0"><thead><tr><th>族群</th><th>篩檢建議</th></tr></thead><tbody><tr><td>20-39 歲成人</td><td>有心血管危險因子者（如糖尿病、肥胖、吸菸、高血壓、家族史），應每 4-6 年檢測 1 次。</td></tr><tr><td>40-75 歲成人</td><td>所有人皆應篩檢，至少每4-6年1次，若為高風險族群（如糖尿病或心血管疾病）應每 1-2 年檢測 1 次。</td></tr><tr><td>&gt; 75 歲老年人</td><td>仍應持續監測，但應根據個人健康狀況與餘命決定是否積極治療。</td></tr></tbody></table><table tabindex="0"><thead><tr><th>風險等級</th><th>定義</th><th>LDL-C 目標值</th></tr></thead><tbody><tr><td>極高風險（Very High Risk）</td><td>已確診 ASCVD（如心肌梗塞、腦中風、周邊動脈疾病），或 糖尿病 + 靶器官損害（如微量白蛋白尿）</td><td>&lt; 55 mg/dL（1.4 mmol/L） 且降幅 ≥ 50%</td></tr><tr><td>高風險（High Risk）</td><td>糖尿病（無靶器官損害）、慢性腎病（CKD stage 3-4）、10 年風險 &gt; 20%</td><td>&lt; 70 mg/dL（1.8 mmol/L） 且降幅 ≥ 50%</td></tr><tr><td>中等風險（Moderate Risk）</td><td>2 個以上心血管危險因子（高血壓、吸菸、家族史等），10 年風險 10-20%</td><td>&lt; 100 mg/dL（2.6 mmol/L）</td></tr><tr><td>低風險（Low Risk）</td><td>無主要心血管危險因子或僅有 1 個危險因子，10 年風險 &lt; 10%</td><td>&lt; 130 mg/dL（3.4 mmol/L）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>藥物類別</th><th>代表藥物</th><th>作用機轉</th><th>適應症</th><th>主要副作用</th><th>考點</th></tr></thead><tbody><tr><td>Statins</td><td>Atorvastatin, Rosuvastatin</td><td>抑制HMG-CoA reductase，降低肝臟膽固醇合成，增加LDL受體 表現</td><td>高LDL-C（首選）、ASCVD 預防</td><td>肝毒性、肌肉病變（CK ↑）</td><td>首選藥物，Statin + Fibrate → 肌肉毒性↓LDL</td></tr><tr><td>Bile Acid Sequestrants（膽酸螯合劑）</td><td>Cholestyramine, Colesevelam</td><td>與膽酸結合，促使膽固醇轉化為膽酸</td><td>適合年輕患者或孕婦的高膽固醇血症</td><td>胃腸不適（便秘、腹脹）、可能影響脂溶性維生素吸收</td><td>↓LDL輕微↑HDL</td></tr><tr><td>Fibrates</td><td>Gemfibrozil, Fenofibrate</td><td>活化PPAR-α，增加 LPL活性，降低VLDL 產生</td><td>高三酸甘油酯（TG &gt;1000 mg/dL），預防胰臟炎</td><td>膽結石（cholelithiasis）、肌肉毒性</td><td>TG &gt;1000 mg/dL → Fibrate，Statin + Gemfibrozil → 肌肉毒性 ↑↓TG ↑HDL</td></tr><tr><td>Niacin</td><td>Niacin (Vit B3)</td><td>抑制肝臟 VLDL合成，增加HDL</td><td>提高HDL，輕度高膽固醇血症</td><td>潮紅（flushing，PGE 介導）、高尿酸血症（痛風）</td><td>可用 Aspirin減少潮紅反應↓TG ↑HDL</td></tr><tr><td>Ezetimibe（膽固醇吸收抑制劑）</td><td>Ezetimibe</td><td>抑制腸道NPC1L1 transporter，減少膽固醇吸收</td><td>高LDL-C（可與 statin 合併）</td><td>腹瀉、肝功能異常（罕見）</td><td>常與 statins 併用↓LDL</td></tr><tr><td>PCSK9 inhibitors</td><td>Alirocumab, Evolocumab</td><td>抑制 PCSK9，增加 LDL受體表現</td><td>家族性高膽固醇血症（FH），無法耐受 statin 者</td><td>注射部位反應，罕見肝功能異常</td><td>高風險患者（如家族性高膽固醇血症）適用↓LDL</td></tr><tr><td>Omega-3 脂肪酸</td><td>Fish oil, Icosapent ethyl</td><td>減少肝臟VLDL合成，降低TG</td><td>中等程度高三酸甘油酯血症（TG 200–500 mg/dL）</td><td>胃腸不適、魚腥味回流</td><td>降低 TG，但效果不如fibrates↓TG</td></tr><tr><td>MTP（Microsomal Triglyceride Transfer Protein）抑制劑</td><td>Lomitapide</td><td>抑制 microsomal triglyceride transfer protein（MTP），減少 VLDL 產生</td><td>家族性高膽固醇血症，適合 Statins 及 PCSK9 抑制劑效果不佳的患者</td><td>噁心、腹瀉、肝內脂肪堆積（脂肪肝）</td><td>口服劑型，但 GI 副作用明顯，需低脂飲食以減少腸胃不適。需監測肝酶（ALT/AST），避免嚴重肝毒性。↓LDL</td></tr><tr><td>ApoB-100 抑制劑</td><td>Mipomersen</td><td>抑制 ApoB-100 mRNA，減少 VLDL 和 LDL 生成</td><td>家族性高膽固醇血症，適合 Statins 及 PCSK9 抑制劑效果不佳的患者</td><td>注射部位反應、類流感症狀、肝內脂肪堆積（脂肪肝）</td><td>皮下注射，常見注射部位反應，但主要問題是肝脂肪增加，可能影響肝功能。需監測肝酶（ALT/AST），避免嚴重肝毒性。↓LDL</td></tr></tbody></table><table tabindex="0"><thead><tr><th>風險族群</th><th>治療策略</th></tr></thead><tbody><tr><td>低風險（LDL &lt; 130 mg/dL）</td><td>生活方式調整</td></tr><tr><td>中等風險（LDL 100-129 mg/dL）</td><td>生活方式調整，考慮 Statins</td></tr><tr><td>高風險（LDL ≥ 100 mg/dL，ASCVD 風險 ≥ 7.5%）</td><td>Statins 第一線，若 LDL 不達標可加 Ezetimibe</td></tr><tr><td>極高風險（LDL ≥ 70 mg/dL，已有 ASCVD 或 FH）</td><td>強效 Statins + Ezetimibe ± PCSK9 抑制劑</td></tr></tbody></table>',12)]))}const b=d(i,[["render",o]]);export{m as __pageData,b as default};
