import{_ as e,c as a,o as r,ae as o}from"./chunks/framework.BtEkjhVd.js";const y=JSON.parse('{"title":"不孕症概論","description":"","frontmatter":{},"headers":[],"relativePath":"ObstetricsGynecologyDepartment-不孕症概論-63.md","filePath":"ObstetricsGynecologyDepartment-不孕症概論-63.md"}'),i={name:"ObstetricsGynecologyDepartment-不孕症概論-63.md"};function n(l,t,d,s,h,c){return r(),a("div",null,t[0]||(t[0]=[o('<h1 id="不孕症概論" tabindex="-1">不孕症概論 <a class="header-anchor" href="#不孕症概論" aria-label="Permalink to &quot;不孕症概論&quot;">​</a></h1><h2 id="原發性不孕-primary-infertility-從未懷孕過的情況。" tabindex="-1">原發性不孕（Primary infertility）： 從未懷孕過的情況。 <a class="header-anchor" href="#原發性不孕-primary-infertility-從未懷孕過的情況。" aria-label="Permalink to &quot;原發性不孕（Primary infertility）： 從未懷孕過的情況。&quot;">​</a></h2><h2 id="續發性不孕-secondary-infertility-曾經懷孕過-不論是否成功分娩-但之後無法懷孕。" tabindex="-1">續發性不孕（Secondary infertility）： 曾經懷孕過（不論是否成功分娩），但之後無法懷孕。 <a class="header-anchor" href="#續發性不孕-secondary-infertility-曾經懷孕過-不論是否成功分娩-但之後無法懷孕。" aria-label="Permalink to &quot;續發性不孕（Secondary infertility）： 曾經懷孕過（不論是否成功分娩），但之後無法懷孕。&quot;">​</a></h2><h2 id="排卵功能檢測-基礎體溫-bbt-、黃體期血清-progesterone-3-ng-ml-、促排卵試驗-clomiphene-citrate-challenge-test-ccct" tabindex="-1">排卵功能檢測：基礎體溫（BBT）、黃體期血清 progesterone (&gt;3 ng/mL)、促排卵試驗（Clomiphene Citrate Challenge Test, CCCT） <a class="header-anchor" href="#排卵功能檢測-基礎體溫-bbt-、黃體期血清-progesterone-3-ng-ml-、促排卵試驗-clomiphene-citrate-challenge-test-ccct" aria-label="Permalink to &quot;排卵功能檢測：基礎體溫（BBT）、黃體期血清 progesterone (&gt;3 ng/mL)、促排卵試驗（Clomiphene Citrate Challenge Test, CCCT）&quot;">​</a></h2><h2 id="荷爾蒙檢測-fsh、lh、amh-卵巢儲備功能-、prolactin、tsh" tabindex="-1">荷爾蒙檢測：FSH、LH、AMH（卵巢儲備功能）、Prolactin、TSH <a class="header-anchor" href="#荷爾蒙檢測-fsh、lh、amh-卵巢儲備功能-、prolactin、tsh" aria-label="Permalink to &quot;荷爾蒙檢測：FSH、LH、AMH（卵巢儲備功能）、Prolactin、TSH&quot;">​</a></h2><h2 id="輸卵管檢查-子宮輸卵管攝影-hsg-、子宮鏡-hysteroscopy-、腹腔鏡-laparoscopy" tabindex="-1">輸卵管檢查：子宮輸卵管攝影（HSG）、子宮鏡（Hysteroscopy）、腹腔鏡（Laparoscopy） <a class="header-anchor" href="#輸卵管檢查-子宮輸卵管攝影-hsg-、子宮鏡-hysteroscopy-、腹腔鏡-laparoscopy" aria-label="Permalink to &quot;輸卵管檢查：子宮輸卵管攝影（HSG）、子宮鏡（Hysteroscopy）、腹腔鏡（Laparoscopy）&quot;">​</a></h2><h2 id="子宮評估-超音波-us-、mri、子宮內膜活檢-endometrial-biopsy" tabindex="-1">子宮評估：超音波（US）、MRI、子宮內膜活檢（Endometrial Biopsy） <a class="header-anchor" href="#子宮評估-超音波-us-、mri、子宮內膜活檢-endometrial-biopsy" aria-label="Permalink to &quot;子宮評估：超音波（US）、MRI、子宮內膜活檢（Endometrial Biopsy）&quot;">​</a></h2><h2 id="精液分析-semen-analysis-who-標準-精子濃度-15-million-ml、活動力-40-、正常形態-4" tabindex="-1">精液分析（Semen Analysis）：WHO 標準（精子濃度 &gt;15 million/mL、活動力 &gt;40%、正常形態 &gt;4%） <a class="header-anchor" href="#精液分析-semen-analysis-who-標準-精子濃度-15-million-ml、活動力-40-、正常形態-4" aria-label="Permalink to &quot;精液分析（Semen Analysis）：WHO 標準（精子濃度 &gt;15 million/mL、活動力 &gt;40%、正常形態 &gt;4%）&quot;">​</a></h2><h2 id="荷爾蒙檢查-fsh、lh、testosterone、prolactin" tabindex="-1">荷爾蒙檢查：FSH、LH、Testosterone、Prolactin <a class="header-anchor" href="#荷爾蒙檢查-fsh、lh、testosterone、prolactin" aria-label="Permalink to &quot;荷爾蒙檢查：FSH、LH、Testosterone、Prolactin&quot;">​</a></h2><h2 id="睪丸與生殖道評估-陰囊超音波-檢查精索靜脈曲張-、睪丸活檢-如懷疑無精症" tabindex="-1">睪丸與生殖道評估：陰囊超音波（檢查精索靜脈曲張）、睪丸活檢（如懷疑無精症） <a class="header-anchor" href="#睪丸與生殖道評估-陰囊超音波-檢查精索靜脈曲張-、睪丸活檢-如懷疑無精症" aria-label="Permalink to &quot;睪丸與生殖道評估：陰囊超音波（檢查精索靜脈曲張）、睪丸活檢（如懷疑無精症）&quot;">​</a></h2><table tabindex="0"><thead><tr><th>因素</th><th>可能病因</th></tr></thead><tbody><tr><td>女性因素</td><td>排卵障礙（PCOS、高泌乳素血症、卵巢早衰）、輸卵管阻塞（PID、子宮內膜異位症）、子宮內膜異常（子宮肌瘤、子宮內膜息肉、Asherman syndrome）</td></tr><tr><td>男性因素</td><td>精子數量/活動力異常（精索靜脈曲張、睪丸功能低下）、輸精管阻塞（感染、先天性無輸精管）、性功能障礙</td></tr><tr><td>雙方因素</td><td>免疫因素（抗精子抗體）、染色體異常（Klinefelter syndrome、Turner syndrome）</td></tr><tr><td>不明原因</td><td>約10-20%為不明原因（Unexplained infertility）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類別</th><th>定義</th><th>常見技術</th></tr></thead><tbody><tr><td>廣義 ART</td><td>所有可幫助受孕的醫療技術，包括人工授精（IUI）、荷爾蒙治療、促排卵、胚胎植入前基因篩檢（PGT）等</td><td>促排卵（Ovulation induction）、IUI（Intrauterine Insemination）、精索靜脈曲張手術</td></tr><tr><td>狹義 ART</td><td>直接介入生殖細胞與胚胎操作，主要指體外受精（IVF）及相關技術</td><td>IVF（In Vitro Fertilization）、ICSI（Intracytoplasmic Sperm Injection）、冷凍胚胎（Cryopreservation）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>狀況</th><th>優先考量治療</th></tr></thead><tbody><tr><td>PCOS，排卵障礙</td><td>促排卵（Letrozole、Clomiphene）</td></tr><tr><td>輸卵管阻塞或嚴重子宮內膜異位症</td><td>IVF</td></tr><tr><td>男性精子數量少或活動力低</td><td>ICSI</td></tr><tr><td>不明原因不孕（Unexplained infertility）</td><td>IUI，若失敗則考慮 IVF</td></tr><tr><td>高齡女性（≥35歲）</td><td>直接考慮 IVF，提高成功率</td></tr></tbody></table>',13)]))}const p=e(i,[["render",n]]);export{y as __pageData,p as default};
