import{_ as a,c as e,o as r,ae as d}from"./chunks/framework.BtEkjhVd.js";const u=JSON.parse('{"title":"子宮內膜癌","description":"","frontmatter":{},"headers":[],"relativePath":"ObstetricsGynecologyDepartment-子宮內膜癌-38.md","filePath":"ObstetricsGynecologyDepartment-子宮內膜癌-38.md"}'),o={name:"ObstetricsGynecologyDepartment-子宮內膜癌-38.md"};function i(l,t,n,h,s,c){return r(),e("div",null,t[0]||(t[0]=[d('<h1 id="子宮內膜癌" tabindex="-1">子宮內膜癌 <a class="header-anchor" href="#子宮內膜癌" aria-label="Permalink to &quot;子宮內膜癌&quot;">​</a></h1><h2 id="最常見的女性生殖道癌症-most-common-gynecologic-malignancy-之一" tabindex="-1">最常見的女性生殖道癌症（Most common gynecologic malignancy）之一 <a class="header-anchor" href="#最常見的女性生殖道癌症-most-common-gynecologic-malignancy-之一" aria-label="Permalink to &quot;最常見的女性生殖道癌症（Most common gynecologic malignancy）之一&quot;">​</a></h2><h2 id="國考常考重點-婦科三癌-內膜、子宮頸、卵巢癌" tabindex="-1">國考常考重點：婦科三癌（內膜、子宮頸、卵巢癌） <a class="header-anchor" href="#國考常考重點-婦科三癌-內膜、子宮頸、卵巢癌" aria-label="Permalink to &quot;國考常考重點：婦科三癌（內膜、子宮頸、卵巢癌）&quot;">​</a></h2><h2 id="好發於停經後婦女-postmenopausal-women-平均60-70-歲多" tabindex="-1">好發於停經後婦女（Postmenopausal women, 平均60-70 歲多） <a class="header-anchor" href="#好發於停經後婦女-postmenopausal-women-平均60-70-歲多" aria-label="Permalink to &quot;好發於停經後婦女（Postmenopausal women, 平均60-70 歲多）&quot;">​</a></h2><h2 id="_50-歲與-pcos、hnpcc、fh、brca、tp53-相關" tabindex="-1">&lt;50 歲與 PCOS、HNPCC、FH、BRCA、TP53 相關 <a class="header-anchor" href="#_50-歲與-pcos、hnpcc、fh、brca、tp53-相關" aria-label="Permalink to &quot;&lt;50 歲與 PCOS、HNPCC、FH、BRCA、TP53 相關&quot;">​</a></h2><h2 id="肥胖、高雌激素狀態為主要危險因子" tabindex="-1">肥胖、高雌激素狀態為主要危險因子 <a class="header-anchor" href="#肥胖、高雌激素狀態為主要危險因子" aria-label="Permalink to &quot;肥胖、高雌激素狀態為主要危險因子&quot;">​</a></h2><table tabindex="0"><thead><tr><th>病理類型</th><th>所屬類型</th><th>特徵</th></tr></thead><tbody><tr><td>Endometrioid Adenocarcinoma</td><td>Type I</td><td>最常見（75-80%）</td></tr><tr><td>Serous Carcinoma</td><td>Type II</td><td>高度侵襲性，與 p53 突變 相關</td></tr><tr><td>Clear Cell Carcinoma</td><td>Type II</td><td>預後差，細胞異型明顯</td></tr><tr><td>Mixed Carcinoma</td><td>Type II</td><td>具兩種以上類型，常見於高齡患者</td></tr></tbody></table><h2 id="注意-外生性的常考在乳癌、子宮內膜癌" tabindex="-1">注意：外生性的常考在乳癌、子宮內膜癌 <a class="header-anchor" href="#注意-外生性的常考在乳癌、子宮內膜癌" aria-label="Permalink to &quot;注意：外生性的常考在乳癌、子宮內膜癌&quot;">​</a></h2><h2 id="最常見症狀-停經後異常陰道出血-postmenopausal-bleeding" tabindex="-1">最常見症狀： 停經後異常陰道出血（Postmenopausal bleeding） <a class="header-anchor" href="#最常見症狀-停經後異常陰道出血-postmenopausal-bleeding" aria-label="Permalink to &quot;最常見症狀： 停經後異常陰道出血（Postmenopausal bleeding）&quot;">​</a></h2><h2 id="其他表現" tabindex="-1">其他表現： <a class="header-anchor" href="#其他表現" aria-label="Permalink to &quot;其他表現：&quot;">​</a></h2><h3 id="月經異常-irregular-menstruation" tabindex="-1">月經異常（Irregular menstruation） <a class="header-anchor" href="#月經異常-irregular-menstruation" aria-label="Permalink to &quot;月經異常（Irregular menstruation）&quot;">​</a></h3><h3 id="骨盆疼痛-pelvic-pain" tabindex="-1">骨盆疼痛（Pelvic pain） <a class="header-anchor" href="#骨盆疼痛-pelvic-pain" aria-label="Permalink to &quot;骨盆疼痛（Pelvic pain）&quot;">​</a></h3><h3 id="晚期可能有體重減輕-cachexia-、腹水-ascites" tabindex="-1">晚期可能有體重減輕（Cachexia）、腹水（Ascites） <a class="header-anchor" href="#晚期可能有體重減輕-cachexia-、腹水-ascites" aria-label="Permalink to &quot;晚期可能有體重減輕（Cachexia）、腹水（Ascites）&quot;">​</a></h3><h2 id="子宮內膜取樣-endometrial-biopsy-emb-首選診斷方式" tabindex="-1">子宮內膜取樣（Endometrial Biopsy, EMB）：首選診斷方式 <a class="header-anchor" href="#子宮內膜取樣-endometrial-biopsy-emb-首選診斷方式" aria-label="Permalink to &quot;子宮內膜取樣（Endometrial Biopsy, EMB）：首選診斷方式&quot;">​</a></h2><h2 id="影像學" tabindex="-1">影像學： <a class="header-anchor" href="#影像學" aria-label="Permalink to &quot;影像學：&quot;">​</a></h2><h2 id="超音波-transvaginal-ultrasound-tvus" tabindex="-1">超音波（Transvaginal Ultrasound, TVUS） <a class="header-anchor" href="#超音波-transvaginal-ultrasound-tvus" aria-label="Permalink to &quot;超音波（Transvaginal Ultrasound, TVUS）&quot;">​</a></h2><h3 id="停經後內膜-4mm-→-需進一步檢查" tabindex="-1">停經後內膜 &gt;4mm → 需進一步檢查 <a class="header-anchor" href="#停經後內膜-4mm-→-需進一步檢查" aria-label="Permalink to &quot;停經後內膜 &gt;4mm → 需進一步檢查&quot;">​</a></h3><h3 id="內膜不均勻增厚、異常回聲-→-需活檢確診" tabindex="-1">內膜不均勻增厚、異常回聲 → 需活檢確診 <a class="header-anchor" href="#內膜不均勻增厚、異常回聲-→-需活檢確診" aria-label="Permalink to &quot;內膜不均勻增厚、異常回聲 → 需活檢確診&quot;">​</a></h3><h2 id="子宮鏡-hysteroscopy-可發現病變-但需切片確診" tabindex="-1">子宮鏡（Hysteroscopy）：可發現病變，但需切片確診 <a class="header-anchor" href="#子宮鏡-hysteroscopy-可發現病變-但需切片確診" aria-label="Permalink to &quot;子宮鏡（Hysteroscopy）：可發現病變，但需切片確診&quot;">​</a></h2><h2 id="年輕有生育需求者-低分級-type-i-可考慮高劑量-progestin-治療-定期內膜切片" tabindex="-1">年輕有生育需求者（低分級 Type I）：可考慮高劑量 Progestin 治療 + 定期內膜切片 <a class="header-anchor" href="#年輕有生育需求者-低分級-type-i-可考慮高劑量-progestin-治療-定期內膜切片" aria-label="Permalink to &quot;年輕有生育需求者（低分級 Type I）：可考慮高劑量 Progestin 治療 + 定期內膜切片&quot;">​</a></h2><h2 id="高危險類型-serous-clear-cell-通常需要手術-化療" tabindex="-1">高危險類型（Serous, Clear Cell）：通常需要手術 + 化療 <a class="header-anchor" href="#高危險類型-serous-clear-cell-通常需要手術-化療" aria-label="Permalink to &quot;高危險類型（Serous, Clear Cell）：通常需要手術 + 化療&quot;">​</a></h2><h2 id="_5年存活率" tabindex="-1">5年存活率： <a class="header-anchor" href="#_5年存活率" aria-label="Permalink to &quot;5年存活率：&quot;">​</a></h2><h3 id="stage-i-80-90" tabindex="-1">Stage I：80-90% <a class="header-anchor" href="#stage-i-80-90" aria-label="Permalink to &quot;Stage I：80-90%&quot;">​</a></h3><h3 id="stage-ii-70-80" tabindex="-1">Stage II：70-80% <a class="header-anchor" href="#stage-ii-70-80" aria-label="Permalink to &quot;Stage II：70-80%&quot;">​</a></h3><h3 id="stage-iii-40-60" tabindex="-1">Stage III：40-60% <a class="header-anchor" href="#stage-iii-40-60" aria-label="Permalink to &quot;Stage III：40-60%&quot;">​</a></h3><h3 id="stage-iv-20" tabindex="-1">Stage IV：&lt;20% <a class="header-anchor" href="#stage-iv-20" aria-label="Permalink to &quot;Stage IV：&lt;20%&quot;">​</a></h3><h2 id="預後不好因子" tabindex="-1">預後不好因子： <a class="header-anchor" href="#預後不好因子" aria-label="Permalink to &quot;預後不好因子：&quot;">​</a></h2><h3 id="type-ii-serous-clear-cell" tabindex="-1">Type II（Serous, Clear Cell） <a class="header-anchor" href="#type-ii-serous-clear-cell" aria-label="Permalink to &quot;Type II（Serous, Clear Cell）&quot;">​</a></h3><h3 id="淋巴轉移、淋巴血管侵犯-lvi" tabindex="-1">淋巴轉移、淋巴血管侵犯（LVI） <a class="header-anchor" href="#淋巴轉移、淋巴血管侵犯-lvi" aria-label="Permalink to &quot;淋巴轉移、淋巴血管侵犯（LVI）&quot;">​</a></h3><h3 id="grade-iii-高分化癌" tabindex="-1">Grade III（高分化癌） <a class="header-anchor" href="#grade-iii-高分化癌" aria-label="Permalink to &quot;Grade III（高分化癌）&quot;">​</a></h3><h3 id="肌肉層侵犯-myometrial-invasion-50" tabindex="-1">肌肉層侵犯（Myometrial Invasion &gt;50%） <a class="header-anchor" href="#肌肉層侵犯-myometrial-invasion-50" aria-label="Permalink to &quot;肌肉層侵犯（Myometrial Invasion &gt;50%）&quot;">​</a></h3><h3 id="p53-突變" tabindex="-1">p53 突變 <a class="header-anchor" href="#p53-突變" aria-label="Permalink to &quot;p53 突變&quot;">​</a></h3><table tabindex="0"><thead><tr><th>類別</th><th>具體危險因子</th></tr></thead><tbody><tr><td>內生性（Endogenous E2）</td><td>- 肥胖（Aromatization of androgens in adipose tissue）- 多囊卵巢症候群（PCOS）- 雌激素分泌腫瘤（如顆粒細胞瘤 granulosa cell tumor）</td></tr><tr><td>外生性（Exogenous E2）</td><td>- 未合併 Progesterone 的 HRT（Hormone Replacement Therapy）- Tamoxifen（SERM，對內膜具 Partial Estrogenic Effect）</td></tr><tr><td>基因相關</td><td>- Lynch Syndrome（HNPCC）→ 增加 Type II 風險- BRCA、TP53</td></tr><tr><td>代謝疾病</td><td>- 糖尿病（DM）、高血壓（HTN）、代謝症候群</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類型</th><th>特徵</th><th>癌變機轉</th><th>預後</th></tr></thead><tbody><tr><td>Type I（Estrogen-dependent）</td><td>75-80%</td><td>與長期雌激素暴露相關，常見於肥胖者</td><td>較佳（低度惡性）</td></tr><tr><td>Type II（Non-estrogen-dependent）</td><td>10-20%</td><td>常見於年長女性，與 p53 突變相關</td><td>較差（高度侵襲性）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>比較特性</th><th>Type I</th><th>Type II</th></tr></thead><tbody><tr><td>流行病學（Epidemiology）</td><td>較常見，發生於停經前（年紀較輕）</td><td>發生於停經後（年紀較大）</td></tr><tr><td>雌激素風險（Estrogen Risk）</td><td>非對抗性雌激素（Unopposed estrogen）+ 肥胖</td><td>相關性較低</td></tr><tr><td>前驅病灶（Precursor Lesion）</td><td>子宮內膜增生（Endometrial hyperplasia）→ 非典型增生（Atypical hyperplasia）</td><td>子宮內膜萎縮（Endometrial atrophy）→ 子宮內膜上皮內癌（Endometrial intraepithelial carcinoma）</td></tr><tr><td>病理型態原型（Histologic Type）</td><td>Endometrioid</td><td>Serous</td></tr><tr><td>其他病理型態（Other Variants）</td><td>黏液型（Mucinous）、具鱗狀分化的內膜樣癌（Endometrioid with squamous differentiation）</td><td>部分可見透明細胞癌（Clear Cell）</td></tr><tr><td>腫瘤分級（Tumor Grade）</td><td>低度（Low Grade）</td><td>高度（High Grade）</td></tr><tr><td>基因變異（Genetic Mutations）</td><td>- PTEN、PIK3CA 突變- 微衛星不穩定性（Microsatellite instability）- KRAS 突變</td><td>- P53 突變（約 90% Type II 具此變異）- 部分可見 HER-2/neu、CMYC 過量表現</td></tr></tbody></table><table tabindex="0"><thead><tr><th>類別</th><th>Type I</th><th>Type II</th></tr></thead><tbody><tr><td>預後（Prognosis）</td><td>較好</td><td>較差</td></tr><tr><td>常見合併症（Associated Cancers）</td><td>卵巢癌（Ovarian cancer）</td><td>乳癌（Breast cancer）、大腸直腸癌（Colorectal cancer）、SEOC（Serous Endometrial Ovarian Cancer）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>FIGO Stage</th><th></th><th>侵犯程度</th></tr></thead><tbody><tr><td>I</td><td></td><td></td></tr><tr><td></td><td>IA</td><td>子宮內膜或子宮肌層 &lt;1/2</td></tr><tr><td></td><td>IB</td><td>子宮肌層 &gt;1/2</td></tr><tr><td>II</td><td></td><td>子宮頸基質</td></tr><tr><td>III</td><td></td><td></td></tr><tr><td></td><td>IIIA</td><td>子宮漿膜層（serosa） ± 子宮附件（adnexa）</td></tr><tr><td></td><td>IIIB</td><td>陰道或子宮旁組織</td></tr><tr><td></td><td>IIIC</td><td>IIIC1：骨盆腔淋巴結IIIC2：主動脈旁淋巴結</td></tr><tr><td>IV</td><td></td><td></td></tr><tr><td></td><td>IVA</td><td>膀胱或直腸黏膜層</td></tr><tr><td></td><td>IVB</td><td>遠端轉移（肺、肝、骨頭等）</td></tr></tbody></table><table tabindex="0"><thead><tr><th>分期</th><th>治療策略</th></tr></thead><tbody><tr><td>Stage I</td><td>全子宮 + 雙側卵巢輸卵管切除（TAH + BSO）- IA（低風險）：單獨手術- IB（肌肉層侵犯 &gt;50%）：手術 + 放療</td></tr><tr><td>Stage II</td><td>全子宮 + 卵巢輸卵管切除（TAH + BSO）+ 放射治療</td></tr><tr><td>Stage III</td><td>全子宮切除 + 放療 + 化療（Cisplatin + Paclitaxel）</td></tr><tr><td>Stage IV</td><td>緩和治療（Palliative therapy），可考慮標靶/免疫治療</td></tr></tbody></table>',38)]))}const b=a(o,[["render",i]]);export{u as __pageData,b as default};
