import{_ as e,c as d,o as a,ae as r}from"./chunks/framework.BtEkjhVd.js";const b=JSON.parse('{"title":"男性不孕","description":"","frontmatter":{},"headers":[],"relativePath":"ObstetricsGynecologyDepartment-男性不孕-66.md","filePath":"ObstetricsGynecologyDepartment-男性不孕-66.md"}'),o={name:"ObstetricsGynecologyDepartment-男性不孕-66.md"};function i(n,t,s,l,h,m){return a(),d("div",null,t[0]||(t[0]=[r('<h1 id="男性不孕" tabindex="-1">男性不孕 <a class="header-anchor" href="#男性不孕" aria-label="Permalink to &quot;男性不孕&quot;">​</a></h1><h2 id="精液分析-semen-analysis" tabindex="-1">精液分析（Semen Analysis） <a class="header-anchor" href="#精液分析-semen-analysis" aria-label="Permalink to &quot;精液分析（Semen Analysis）&quot;">​</a></h2><h2 id="若精液分析正常-但仍無法懷孕-→-評估精子功能-精子-dna-fragmentation-test-sperm-vitality-test" tabindex="-1">若精液分析正常，但仍無法懷孕 → 評估精子功能（精子 DNA fragmentation test, Sperm vitality test） <a class="header-anchor" href="#若精液分析正常-但仍無法懷孕-→-評估精子功能-精子-dna-fragmentation-test-sperm-vitality-test" aria-label="Permalink to &quot;若精液分析正常，但仍無法懷孕 → 評估精子功能（精子 DNA fragmentation test, Sperm vitality test）&quot;">​</a></h2><h2 id="若發現精液量少且-ph-偏低-→-考慮射精管阻塞或先天性無輸精管" tabindex="-1">若發現精液量少且 pH 偏低 → 考慮射精管阻塞或先天性無輸精管 <a class="header-anchor" href="#若發現精液量少且-ph-偏低-→-考慮射精管阻塞或先天性無輸精管" aria-label="Permalink to &quot;若發現精液量少且 pH 偏低 → 考慮射精管阻塞或先天性無輸精管&quot;">​</a></h2><h2 id="若無精子-azoospermia" tabindex="-1">若無精子（Azoospermia）： <a class="header-anchor" href="#若無精子-azoospermia" aria-label="Permalink to &quot;若無精子（Azoospermia）：&quot;">​</a></h2><h3 id="fsh-↑-testosterone-↓-klinefelter-syndrome" tabindex="-1">FSH ↑，Testosterone ↓ ：Klinefelter syndrome <a class="header-anchor" href="#fsh-↑-testosterone-↓-klinefelter-syndrome" aria-label="Permalink to &quot;FSH ↑，Testosterone ↓ ：Klinefelter syndrome&quot;">​</a></h3><h3 id="fsh-正常-testosterone-正常-→-可能為輸精管阻塞-obstructive-azoospermia" tabindex="-1">FSH 正常，Testosterone 正常 → 可能為輸精管阻塞（Obstructive azoospermia） <a class="header-anchor" href="#fsh-正常-testosterone-正常-→-可能為輸精管阻塞-obstructive-azoospermia" aria-label="Permalink to &quot;FSH 正常，Testosterone 正常 → 可能為輸精管阻塞（Obstructive azoospermia）&quot;">​</a></h3><h2 id="內分泌檢查-hormonal-evaluation-偶爾會考-近幾年愛考" tabindex="-1">內分泌檢查（Hormonal Evaluation）：偶爾會考，近幾年愛考 <a class="header-anchor" href="#內分泌檢查-hormonal-evaluation-偶爾會考-近幾年愛考" aria-label="Permalink to &quot;內分泌檢查（Hormonal Evaluation）：偶爾會考，近幾年愛考&quot;">​</a></h2><h2 id="影像學與基因檢測" tabindex="-1">影像學與基因檢測 <a class="header-anchor" href="#影像學與基因檢測" aria-label="Permalink to &quot;影像學與基因檢測&quot;">​</a></h2><table tabindex="0"><thead><tr><th>pre-testicular</th><th>腦垂腺和下視丘疾病（如：Kallmann syndrome、hyperprolactinemia、pituitary insufficiency、外源性賀爾蒙）</th></tr></thead><tbody><tr><td>testicular</td><td>不可逆之基因疾病（如：Klinefelter syndrome、Y-chromosomemicrodeletions、Noonan syndrome）、毒素、系統性疾病、睪丸傷害（如：精索靜脈曲張、發炎、外傷）、雄性激素活性異常（androgen receptor deficiency、5a -reductase deficiency）</td></tr><tr><td>post-testicular</td><td>生殖系統阻塞（如：先天性兩側無輸精管）、精子異常、性交異常</td></tr></tbody></table><table tabindex="0"><thead><tr><th>分類</th><th>病因</th><th>機制 / 重要考點</th></tr></thead><tbody><tr><td>內分泌因素（Endocrine causes）</td><td>低促性腺激素性性腺功能低下（Hypogonadotropic hypogonadism）</td><td>- Kallmann syndrome（GnRH 缺乏，嗅覺缺失）- 腦下垂體腫瘤（Prolactinoma 抑制 GnRH）</td></tr><tr><td>高促性腺激素性性腺功能低下（Hypergonadotropic hypogonadism）</td><td>- Klinefelter syndrome（47, XXY）：睪丸萎縮，FSH/LH ↑，Testosterone ↓</td><td></td></tr><tr><td>生殖道阻塞（Obstructive causes）</td><td>先天性無輸精管（Congenital bilateral absence of vas deferens, CBAVD）</td><td>常見於 Cystic Fibrosis（CFTR 基因突變）</td></tr><tr><td>精索靜脈曲張（Varicocele）</td><td>- 陰囊靜脈擴張，多發生於左側（因左精索靜脈直接進入左腎靜脈）- USMLE 高考點：左側精索靜脈曲張合併腎細胞癌，因腫瘤壓迫左腎靜脈</td><td></td></tr><tr><td>感染（Infections）</td><td>生殖道感染（Orchitis, Epididymitis）</td><td>- 流行性腮腺炎（Mumps）併發睪丸炎，可能導致不孕</td></tr><tr><td>射精功能異常（Ejaculatory dysfunction）</td><td>- 逆行性射精（Retrograde ejaculation）</td><td>- 常見於糖尿病自主神經病變或脊髓損傷患者</td></tr><tr><td>精子 DNA 損傷（DNA fragmentation）</td><td>- 精子氧化壓力過高（Oxidative stress）</td><td>- 吸菸、環境毒素（如殺蟲劑）、發炎</td></tr></tbody></table><table tabindex="0"><thead><tr><th>參數</th><th>正常範圍（WHO 標準）</th><th>異常解釋</th></tr></thead><tbody><tr><td>精子濃度（Sperm concentration）</td><td>≥ 15 million/mL</td><td>&lt; 15 million/mL = 少精症（Oligospermia）</td></tr><tr><td>總精子數（Total sperm count）</td><td>≥ 39 million/ejaculate</td><td></td></tr><tr><td>精子活動力（Motility）</td><td>≥ 40% 活動</td><td>活動力低 = 弱精症（Asthenospermia）</td></tr><tr><td>精子正常形態（Morphology）</td><td>≥ 4% 正常形態</td><td>形態異常 = 畸形精子症（Teratospermia）</td></tr><tr><td>白血球數（WBC in semen）</td><td>&lt;1 million/mL</td><td>若增加，可能為感染</td></tr></tbody></table><table tabindex="0"><thead><tr><th>荷爾蒙</th><th>正常範圍</th><th>異常結果的意義</th></tr></thead><tbody><tr><td>FSH（濾泡刺激素）</td><td>1.5-12 IU/L</td><td>↑：睪丸功能異常（原發性性腺功能低下）↓：下視丘/腦垂體功能異常</td></tr><tr><td>LH（黃體生成素）</td><td>1.7-8.6 IU/L</td><td>同 FSH，影響睪固酮分泌</td></tr><tr><td>Testosterone（睪固酮）</td><td>300-1000 ng/dL</td><td>↓：性腺功能低下（Hypogonadism）</td></tr><tr><td>Prolactin（泌乳激素）</td><td>&lt; 15 ng/mL</td><td>↑：可能為腦下垂體腫瘤（Prolactinoma）</td></tr><tr><td>Estradiol（雌二醇）</td><td>10-40 pg/mL</td><td>Klinefelter syndrome（XXY）升高</td></tr></tbody></table><table tabindex="0"><thead><tr><th>檢查</th><th>適應症</th><th>臨床應用</th></tr></thead><tbody><tr><td>陰囊超音波（Scrotal ultrasound）</td><td>精索靜脈曲張懷疑</td><td>可見 精索靜脈擴張 &gt; 3 mm</td></tr><tr><td>腦下垂體 MRI</td><td>高泌乳素血症（Prolactin &gt; 100 ng/mL）</td><td>確認腦下垂體腫瘤</td></tr><tr><td>Y 染色體微缺失（Y chromosome microdeletion）</td><td>無精症（Azoospermia）</td><td>Yq11 缺失 → 無精子症</td></tr><tr><td>CFTR 基因突變檢測</td><td>先天性無輸精管（CBAVD）</td><td>常合併 Cystic Fibrosis</td></tr></tbody></table><table tabindex="0"><thead><tr><th>病因</th><th>治療方式</th></tr></thead><tbody><tr><td>內分泌異常（Hypogonadism）</td><td>補充睪固酮（Testosterone replacement therapy, TRT）</td></tr><tr><td>精索靜脈曲張（Varicocele）</td><td>精索靜脈結紮術（Varicocelectomy），可改善精液參數</td></tr><tr><td>輸精管阻塞（Obstructive azoospermia）</td><td>顯微外科手術（Vasovasostomy 或 Epididymovasostomy）</td></tr><tr><td>免疫性不孕（抗精子抗體）</td><td>可能需要輔助生殖技術（ART, Assisted Reproductive Technology）</td></tr><tr><td>精子活動力低下（Asthenospermia）</td><td>體外受精 + 單精子顯微注射（ICSI）</td></tr></tbody></table>',15)]))}const y=e(o,[["render",i]]);export{b as __pageData,y as default};
